/********************************************************************************
* 기      능   : 조회 1
********************************************************************************/
var fn_query_chk = false; //중복조회 방지용 flag

function fn_query() {

	var arr = ['Deliv. Date', '12'];
	if(gfn_getDayCnt(ds_days, cal_edatufr.Value, cal_edatuto.Value) > 365) {
		gfn_showAlert("CW10087", arr);
		cal_edatufr.SetFocus();
		return false;
	}

	if(!fn_query_chk) {
		fn_query_chk = true;
	} else {
		gfn_showAlert("CW10025", "조회중입니다..");
		return;
	}

	ds_cond.ClearData();
	ds_cond.InsertRow(0);
	ds_cond.SetColumn(0, "EDATUFR"   , cal_edatufr.Value      		);
	ds_cond.SetColumn(0, "EDATUTO"   , cal_edatuto.Value      		);
	ds_cond.SetColumn(0, "VBELN"     , ed_pjt.Text  				);
	ds_cond.SetColumn(0, "ZKUNNR"    , div_sman_fr.ed_sman_cd.Text  );
	ds_cond.SetColumn(0, "REGION"    , ed_region.Text         		);
	ds_cond.SetColumn(0, "SEDAT"   	 , "99999999"					);		// 메일전송완료 FLAG

	nsfRequest("header", "ses0243/header", "ds_cond=ds_cond ds_header=ds_header", "ds_header=ds_header ds_error=ds_error", "", "fn_afterQuery");
    fn_createWait("",this);     // 처리중입니다. 메시지 처리
}

/********************************************************************************
* 기      능   : 조회 2
********************************************************************************/
function fn_detail() {
	ds_cond.ClearData();
	ds_cond.InsertRow(0);
	ds_cond.SetColumn(0, "MANDT", ds_Header.GetColumn(ds_Header.rowpos, "MANDT"));
	ds_cond.SetColumn(0, "VBELN", ds_Header.GetColumn(ds_Header.rowpos, "VBELN"));
	ds_cond.SetColumn(0, "ZSEQ", ds_Header.GetColumn(ds_Header.rowpos, "ZSEQ"));

	nsfRequest("detail", "ses0243/detail", "ds_cond=ds_cond ds_detail=ds_detail", "ds_detail=ds_detail ds_error=ds_error", "", "fn_afterQuery");
}

