/********************************************************************************
* 기      능   : 접수
********************************************************************************/
function fn_receipt() {
	ds_header2.ClearData();
	ds_header2.InsertRow(0);
	ds_header2.CopyRow(0, ds_header, ds_header.row);

	var strMsg = [domain.btn.receipt];
	if(!gfn_showConfirm("CW10207",strMsg)){		//접수 하시겠습니까?
	   return false;
	}

	nsfRequest("receipt"																	// srvId
				, "ses0243/receipt"														// url
				, "ds_header2=ds_header2"														// inDsList
				, "ds_error=ds_error"												    	// outDsList
				, ""																		// strParam
				, "fn_afterreceipt"															// CallBackFunc
				);
}
/********************************************************************************
* 기      능   : 출력
********************************************************************************/
function fn_print() {

	var nHeaderCheckedRow = ds_header.row;

	ds_header2.ClearData();
	var nRow = ds_header2.AddRow();
	ds_header2.CopyRow(nRow, "ds_header", nHeaderCheckedRow);

	if ( ds_header2.GetRowCount() > 1 ) {
		gfn_showAlert("CW00222");		//하나의 자료만 선택하여 주십시오.
		return;
	} else if ( ds_header2.GetRowCount() == 0 ) {
		gfn_showAlert("CW10021");		//선택된 데이터가 없습니다. \n확인하시기 바랍니다.
		return;
	}

	// CHECK한 행이 현재 선택한 행일 경우
	if ( nHeaderCheckedRow == ds_header.row ) {
		var sumKwmeng = 0;
		var sumNetwr = 0;
		var vrkme;

		sumKwmeng = ParseInt(ds_detail.TotalSum("KWMENG"));	//수량합계
		sumNetwr = ParseInt(ds_detail.TotalSum("PONETWR"));	//상해금액합계
		vrkme = ds_detail.GetColumn(0, "VRKME");	//판매단위

		var arg = "sumKwmeng=" + sumKwmeng
				+ " sumNetwr=" + sumNetwr
				+ " vrkme=" + vrkme;
		Dialog("SES::Ses0246.xml", arg, -1, -1, "Resize=true");	// UbiReport Call
	} 
	// CHECK한 행이 현재 선택한 행이 아닐 경우
	else {
		F_PRINT_FLAG = 1;
		ds_header.row = nHeaderCheckedRow;
		fn_detail(ds_header.row);
	}
}

