/********************************************************************************
* 기      능   : 조회 1
********************************************************************************/
function fn_query() {
	ds_cond.ClearData();	
	ds_hogi.ClearData();

    if (ed_pjt.Text == null || ed_pjt.Text =="") {
		var strmsg = domain.com.pjtno + domain.com.no;
		gfn_showAlert("CW00208",strmsg);
		return false;
    }

	fn_createWait("",this);     // 처리중입니다. 메시지 처리
	ds_cond.InsertRow(0);
	ds_cond.setColumn(0, "MANDT"  	, G_MANDT);    
	ds_cond.SetColumn(0, "EDATUFR"  , cal_edatufr.Value     	  );
	ds_cond.SetColumn(0, "EDATUTO"  , cal_edatuto.Value     	  );
	ds_cond.SetColumn(0, "VBELN"    , ed_pjt.Text 				  );
	ds_cond.SetColumn(0, "VKBUR"    , div_vbvg.cb_frvb.Value	  );
	ds_cond.SetColumn(0, "VKGRP"    , div_vbvg.cb_frvg.Value 	  );
	ds_cond.SetColumn(0, "ZKUNNR"   , div_sman_fr.ed_sman_cd.Text );
	ds_cond.SetColumn(0, "REGION"   , ed_region.Text         	  );

	nsfRequest("query", "ses0243/hogi", "ds_cond=ds_cond ds_hogi=ds_hogi", "ds_hogi=ds_hogi ds_error=ds_error", "", "fn_afterQuery");
}


/********************************************************************************
* 기      능   : 저장 1
********************************************************************************/
function fn_save() {

	if(ds_hogi.findrow("FLAG", "1") == -1) {
		gfn_showAlert("CW10021");	//CW10021=선택된 데이터가 없습니다. \n확인하시기 바랍니다.
		return;
	}

	if(!gfn_showConfirm("CW10212")){		//MN생성하시겠습니까?
	   return;
	}	

	ds_cond.setColumn(0, "MANDT"  	, G_MANDT);    
	ds_cond.setColumn(0, "VBELN"	, ed_pjt.Text);
	ds_cond.setColumn(0, "ERNAM"	, G_SAP_USER_ID);
	ds_cond.setColumn(0, "ZSEQ"		, "");
	trace(ds_hogi.SaveXML());
	nsfRequest("query", "ses0243/checkVbak", "ds_cond=ds_cond ds_list=ds_list ds_hogi=ds_hogi:U ds_sapinsert = ds_sapinsert", "ds_hogi=ds_hogi  ds_error=ds_error", "", "fn_afterSave");	

	fn_createWait("",this);     // 처리중입니다. 메시지 처리
}

