/********************************************************************************
* 기      능   : 폼 초기화
********************************************************************************/
function fn_init_form() {
	pGubun = pGubun;

	// 최종 변경된 셀정보 초기화
	F_CHG_COLNUM = "";	             // 변경된 컬럼번호
	F_CHG_ROWNUM = "";	             // 변경된 행번호
	F_MAX_SERNO  = 0;	             // 최종순번

	ed_pjt.Text =  pVbeln;
	cal_edatuto.Value = gfn_getCurrDate();
} 

