/********************************************************************************
* 기      능   : 정렬,전체선택  처리 
********************************************************************************/
function grd_detail_OnHeadClick(obj,nCell,nX,nY,nPivotIndex) {
	if ( nCell == 1 ) {
		// 그리드 표시할 컬럼 선택 
		gfn_selectAllGrid(obj,nCell,nCell,true);
	} else {
		// 그리드 정렬 처리 
		gfn_sortGrid(obj,nCell);
	}
}
/********************************************************************************
* 기      능   : 국가 팝업
********************************************************************************/
function unf_opneContry_OnClick(obj)
{
	var param = ed_region.Text;
	var arg  = "popFlag="+"nation"; // pop구분값 ( nation:국가 / area:운송지역 )
		arg += " pCdNm="+quote(param); // 넘길 값

	var arr = gfn_openPopupUrl("COM::Com0060.xml", true, arg);

	if (arr != null && arr != 0)
	{
		ed_region.Text = arr[0];
		ed_regionnm.Text = arr[1];
		ed_region.SetFocus();
	}
}
/********************************************************************************
* 기      능   : 프로젝트 팝업
********************************************************************************/
function ufn_openPjt(obj)
{
	var arg = "pjt="+quote(ed_pjt.Text);
	var arr = gfn_openPopup("CS9601002A",true,arg);

	if (arr != null && arr != 0) {
		ed_pjt.Text = arr[0];
		ed_pjtnm.Text = arr[2];
	}
}
/******************************************************
// Code Value 변경시 명칭 clear처리
******************************************************/
function Common_Edit_OnCharChanged(obj,strPreText,strPostText)
{
	switch(obj.id) {
		case "ed_pjt" :
			ed_pjtnm.Text = "";
			break;
		case "ed_region" :
			ed_regionnm.Text = "";
			break;
	}
}


