//insert 한데이터의 호기번호로 RFC연결후 특성 가져오기(LOOP)
function fn_selectHogi(errCode, errMsg){

	//ds_sap.ClearData();		//RFC조회용 데이터셋 초기화

	if(ds_list.rowcount < 1)
	{	
		fn_destroyWait();
		alert("해당 오더번호가 없습니다.");
		return;
	}

	if(ds_hogi.rowcount < 1)
	{	
		fn_destroyWait();
		alert("해당 품목의 호기번호가 없습니다.");
		return;
	}

	for(var i = 0; i<ds_hogi.rowcount; i++)
	{
		ds_cond.ClearData();
		ds_cond.AddRow();	
		ds_cond.SetColumn(0, "HOGI", ds_hogi.GetColumn(i, "POSID"));

		alert(ds_cond.SaveXML());

		nsfRequest("query", "ses0320/receive", "ds_cond=ds_cond", "ds_sap=ds_list ds_sap2=ds_list2 ds_error=ds_error", "", "");

		fn_aftersap();
		//nsfRequest("query", "ses0320/receive", "ds_cond=ds_cond", "ds_sap=ds_list ds_sap2=ds_list2 ds_error=ds_error", "", "fn_aftersap");
		//fn_createWait("",this);     // 처리중입니다. 메시지 처리
	}

	fn_afterSave(errCode, errMsg);
}

function fn_aftersap(errCode, errMsg){
	//fn_destroyWait();

	if(ds_sap.rowcount < 1)
	{
		//fn_destroyWait();
		alert("해당 호기번호의 수주데이터가 없습니다.");
		return;
	}

	//trace(ds_sap.SaveXML());

	//alert("sap가져온정보 " + ds_sapinsert.SaveXML());

	for( var i=0; i < ds_sap.rowcount; i++) {

		var nRow = ds_sapinsert.addRow();

		ds_sapinsert.setColumn(nRow, "MANDT"			, G_MANDT);
		ds_sapinsert.setColumn(nRow, "VBELN" 			, ds_hogi.getColumn(0, "VBELN"));
		ds_sapinsert.setColumn(nRow, "ZSEQ"				, ds_hogi.getColumn(0, "ZSEQ"));
		ds_sapinsert.setColumn(nRow, "POSNR" 			, ds_hogi.getColumn(0, "POSNR"));
		ds_sapinsert.setColumn(nRow, "CHARACTERISTIC" 	, ds_sap.getColumn(i, "NAM_CHAR"));
		ds_sapinsert.setColumn(nRow, "POSID" 			, ds_hogi.getColumn(0, "POSID"));
		ds_sapinsert.setColumn(nRow, "VALUE" 			, ds_sap.getColumn(i, "VALUE1"));				

	}	
		//alert(ds_sapinsert.SaveXML());
		nsfRequest("saveSap", "ses0243/saveSap", "ds_sapinsert=ds_sapinsert", "ds_error=ds_error", "", "");


		//fn_createWait("",this);     // 처리중입니다. 메시지 처리

		//if (pGubun == "U") fn_close();// 신규 저장		
}
