var F_FRM_TITLE = "duty";	// 공통코드 화면명
//=========================================================================================
// [ PART 2 ]
// Form Loading / Unloading 시 작업 정의
//=========================================================================================
function fn_OnLoadCompleted(obj) {

	gfn_initForm(true);	// Form 로드 이벤트 맨 첫줄에 기술 
	div_btn.fn_loadUseBtn("1,30", F_FRM_TITLE, "", "");
	fn_init_form(); 	// 폼 초기화
}

/********************************************************************************
* 기      능   : form UnloadCompleted 
********************************************************************************/
function fn_OnUnloadCompleted(obj) {
	return gfn_isFormUnload(obj);
}

//=========================================================================================
// [ PART 3 ]
// Main Process Event 정의(조회,입력,삭제,저장,인쇄,form닫기)
//=========================================================================================
/********************************************************************************
* 기      능   : 조회 1
********************************************************************************/
function fn_query() {

	ds_cond.DeleteAll();
	ds_cond.AddRow();

	ds_cond.setColumn(0, "GTYPE"     , txt_gtype.Text);

	nsfRequest("header", "ses0290/header", "ds_cond=ds_cond ds_header=ds_header", "ds_header=ds_header ds_error=ds_error", "", "fn_afterQuery");
    fn_createWait("",this);     // 처리중입니다. 메시지 처리
}

/********************************************************************************
* 기      능   : 조회 2
********************************************************************************/
function fn_detail() {

	ds_cond.setColumn(0, "MANDT", G_MANDT                                                 );
	ds_cond.SetColumn(0, "BLOCKNO", grd_header.GetCellText("Body", grd_header.CurrentRow, 3));


	nsfRequest("detail", "ses0290/detail", "ds_cond=ds_cond ds_detail=ds_detail", "ds_detail=ds_detail ds_error=ds_error", "", "fn_afterQuery");
}

/********************************************************************************
* 기      능   : 폼 초기화
********************************************************************************/
