/********************************************************************************
* 기      능   : 정렬 처리 
********************************************************************************/
function grd_header_OnHeadClick(obj,nCell,nX,nY,nPivotIndex) {

	if( nCell == 0 ) gfn_selectAllGrid(obj,nCell,nCell,true);
	else             gfn_sortGrid(obj, nCell);
}

/********************************************************************************
* 기      능   : 조회
********************************************************************************/
function fn_OnClick_btn(obj) {
	var fncName = "fn_" + substr(obj.id, 4); 
	eval( fncName + "()");
}

function grd_header_OnCellClick(obj,nRow,nCell,nX,nY,nPivotIndex) {
	fn_detail();
}


function btn_quotation_OnClick(obj) {

	var arg = "branch="+quote("head");
    gfn_openPopUpUrlHdel("Ses::Ses0032.xml", true, arg);
}

function btn_plus_OnClick(obj)
{
	var newRow = gfn_addGridRow(grd_header, -1, "FLAG");// 추가된 행번호 수신(FLAG:I) 

	ds_header.SetColumn(newRow, "MANDT", G_MANDT);
}

function fn_Save(){
	ds_header.Filter("FLAG != null");
	ds_header2.copyF(ds_header);
	ds_header.UnFilter();

	nsfRequest("saveh", "ses0290/saveh", "ds_header2=ds_header2", "ds_header2=ds_header2 ds_error=ds_error", "", "fn_afterQuery");

	ds_detail.Filter("FLAG != null");
	ds_detail2.copyF(ds_detail);
	ds_detail.UnFilter();

	nsfRequest("saved", "ses0290/saved", "ds_detail2=ds_detail2", "ds_detail2=ds_detail2 ds_error=ds_error", "", "fn_afterQuery");
}

function ds_header_OnColumnChanged(obj,nRow,strColumnID,varOldValue,varNewValue,nPivotIndex)
{
	gfn_changeFlag( obj, strColumnID, nRow, "FLAG" );
}

function btn_plus1_OnClick(obj)
{
	var newRow = gfn_addGridRow(grd_detail, -1, "FLAG");// 추가된 행번호 수신(FLAG:I) 

	ds_detail.SetColumn(newRow, "MANDT", G_MANDT);
	ds_detail.SetColumn(newRow, "BLOCKNO", grd_header.GetCellText("Body", grd_header.CurrentRow, 3));
}

function ds_detail_OnColumnChanged(obj,nRow,strColumnID,varOldValue,varNewValue,nPivotIndex)
{
	gfn_changeFlag( obj, strColumnID, nRow, "FLAG" );
}
