/********************************************************************************
* 기      능   : 삭제
********************************************************************************/
function fn_delete() {

	if(Confirm("삭제하시겠습니까?") == true) {

		ds_header.ClearData();
		ds_header.AddRow();
		ds_header.CopyRow(0, ds_list, ds_list.row);
		ds_header.SetColumn(0, "FLAG", "D");

		//nsfRequest("deleteHeader", "ses0020/delete", "ds_save=ds_save ds_head=ds_save", "ds_save=ds_save ds_error=ds_error", "", "fn_afterSave");
		nsfRequest("deleteHeader", "ses0330/save", "ds_save=ds_save ds_head=ds_save", "ds_save=ds_save ds_error=ds_error", "", "fn_afterSave");

	} else {
		return;
	}
}


function ds_detail_OnColumnChanged(obj,nRow,strColumnID,varOldValue,varNewValue,nPivotIndex)
{
	ds_detail.fireevent = false;
	if(strColumnID!="FLAG"){
		ds_detail.SetColumn(nRow, "FLAG", 1);
	}
	// 상세 그리드의 상해금액을 변경하면, 헤더의 상해금액 합계를 바로 변경해준다.
	if ( strColumnID == "SHNETWR" ) {
		ds_header.fireEvent = false;
		ds_header.SetColumn(ds_header.row, "SHNETWR", ds_detail.TotalSum("SHNETWR"));
		ds_header.fireEvent = true;
	}
	ds_detail.fireevent = true;
}

/********************************************************************************
* 기      능   : 저장
********************************************************************************/
function fn_save()
{
	// 그리드 저장 대상 로우
	if(ds_header.FindRow("FLAG", 1)==-1){
		fn_saveDetail();
		gfn_showMsg('CW00046');
		return;
	}

	////if(!gfn_showConfirm("저장하시겠습니까?")){	//CI00008=저장 하시겠습니까?
	if(!gfn_showConfirm("CI00008")){		//저장 하시겠습니까?
	   return;
	}		

	ds_header.Filter("FLAG==1");
	ds_header2.copyF(ds_header);
	ds_header.unFilter();

	tit_clearActionInfo();

	// 서비스 호출 
	ds_error.ClearData(); 

	fn_createWait("",this);     // 처리중입니다. 메시지 처리
	nsfRequest("updateHeader"																	// srvId
				, "ses0330/updateHeader"															// url
				, "ds_list=ds_header2"														// inDsList
				, "ds_error=ds_error"												    	// outDsList
				, ""																		// strParam
				, "fn_aftersave"															// CallBackFunc
				);
}

/********************************************************************************
* 기      능   : 저장 Callback  
********************************************************************************/
function fn_aftersave(svcId, errCode, errMsg) {
	fn_destroyWait();

	if ( errCode != 0 ) {
		gfn_showMsg("CE00001");
	} else {
		gfn_showMsg("CI00003");		
	}

	fn_query();
}

/********************************************************************************
* 기      능   : 저장
********************************************************************************/
function fn_saveDetail()
{
	// 그리드 저장 대상 로우
	if(ds_detail.FindRow("FLAG", 1)==-1){
		gfn_showMsg('CW00046');
		return;
	}

	if(!gfn_showConfirm("CI00008")){		//저장 하시겠습니까?
	   return;
	}		

	ds_detail.Filter("FLAG==1");
	ds_detail2.copyF(ds_detail);
	ds_detail.unFilter();

	tit_clearActionInfo();

	// 서비스 호출 
	ds_error.ClearData(); 

	fn_createWait("",this);     // 처리중입니다. 메시지 처리
	nsfRequest("receipt"																	// srvId
				, "ses0330/save"															// url
				, "ds_list=ds_detail2"														// inDsList
				, "ds_error=ds_error"												    	// outDsList
				, ""																		// strParam
				, "fn_aftersaveDetail"															// CallBackFunc
	);
}

/********************************************************************************
* 기      능   : 저장 Callback  
********************************************************************************/
function fn_aftersaveDetail(svcId, errCode, errMsg) {
	fn_destroyWait();

	if ( errCode != 0 ) {
		gfn_showMsg("CE00001");
	} else {
		gfn_showMsg("CI00003");		
	}

	if(svcId = "sendmail")
	{
		fn_query();
	}
	else
	{
		fn_query();
	}
}


/********************************************************************************
* 기      능   : 출력
********************************************************************************/
function fn_print()
{
	var nHeaderCheckedRow = -1;

	ds_header2.DeleteAll();
	for ( var i = 0 ; i < ds_header.GetRowCount() ; i++ ) {
		if ( ds_header.GetColumn(i, "CHK") == 1 ) {
			var nRow = ds_header2.AddRow();
			ds_header2.CopyRow(nRow, "ds_header", i);
			nHeaderCheckedRow = i;
		}
	}
	if ( ds_header2.GetRowCount() > 1 ) {
		gfn_showAlert("CW00222");		//하나의 자료만 선택하여 주십시오.
		return;
	} else if ( ds_header2.GetRowCount() == 0 ) {
		gfn_showAlert("CW10021");		//선택된 데이터가 없습니다. \n확인하시기 바랍니다.
		return;
	}

	// CHECK한 행이 현재 선택한 행일 경우
	if ( nHeaderCheckedRow == ds_header.row ) {
		var sumKwmeng = 0;
		var sumNetwr = 0;
		var vrkme;
		for(var i =0; i<ds_detail.rowcount; i++)
		{
			sumKwmeng = sumKwmeng + ParseInt(ds_detail.GetColumn(i, "KWMENG"));	//수량합계
			sumNetwr = sumNetwr + ParseInt(ds_detail.GetColumn(i, "SHNETWR"));	//판매금액합계
		}
		vrkme = ds_detail.GetColumn(0, "VRKME");								//판매단위

		var arg = "sumKwmeng=" + sumKwmeng
				+ " sumNetwr=" + sumNetwr
				+ " vrkme=" + vrkme;
		Dialog("SES::Ses0339.xml", arg, -1, -1, "Resize=true");	// UbiReport Call
	} 
	// CHECK한 행이 현재 선택한 행이 아닐 경우
	else {
		F_PRINT_FLAG = 1;
		ds_header.row = nHeaderCheckedRow;
		fn_detail(ds_header.row);
	}
}

/********************************************************************************
* 기      능   : 전송
********************************************************************************/
function fn_trans() {

	ds_header2.DeleteAll();
	for ( var i = 0 ; i < ds_header.GetRowCount() ; i++ ) {
		if ( ds_header.GetColumn(i, "CHK") == 1 ) {
			if ( ds_header.GetColumn(i, "SEDAT") != "00000000" ) {
				gfn_showAlert("CW00027");	//선택한 자료는 이미 전송을 완료한 자료입니다.
				return;
			}

			var nRow = ds_header2.AddRow();
			ds_header2.CopyRow(nRow, "ds_header", i);
		}
	}

	// 그리드 전송 대상 로우
	if ( ds_header2.RowCount() < 1 ) {
		gfn_showAlert("CW10021");		//선택된 데이터가 없습니다. \n확인하시기 바랍니다.
		return;
	}

	if ( !gfn_showConfirm("CI00010") ) {	//전송하시겠습니까?
	   return;
	}

	ds_email.ClearData();
	for ( var i = 0 ; i < ds_header2.GetRowCount() ; i++ ) {
		var sMailTo = ds_header2.GetColumn(i, "MAILADDR"); //"MJ.SHIN@hdel.co.kr"
		//if ( sMailTo == "" ) {
		//	alert('발송자 미지정');
		//}
		var subject = "PO_" + ds_header2.GetColumn(i, "VBELN") + ". " + ds_header2.GetColumn(i, "BSTKD");
		var content = "Dear Sirs \n Please find Purchase Order of " + subject + " as attached \n\n Best Regards,\n Hyundai Global marketing & Sales in Seoul";

		var eRow = ds_email.AppendRow();
		ds_email.SetColumn(eRow, "FROM"		, G_EMAIL);
		ds_email.SetColumn(eRow, "TO"		, sMailTo);
		ds_email.SetColumn(eRow, "TITLE"	, subject);
		ds_email.SetColumn(eRow, "CONTENT"	, content);
	}

	tit_clearActionInfo();

	// 서비스 호출 
	ds_error.ClearData(); 

	fn_createWait("",this);     // 처리중입니다. 메시지 처리
	nsfRequest("sendmail"																	// srvId
				, "ses0330/sendmail"														// url
				, "ds_list=ds_header2 ds_email=ds_email"									// inDsList
				, "ds_error=ds_error"												    	// outDsList
				, ""																		// strParam
				, "fn_aftersend"															// CallBackFunc
	);
}

/********************************************************************************
* 기      능   : sendmail Callback  
********************************************************************************/
function fn_aftersend(svcId, errCode, errMsg) {
	fn_destroyWait();

	if ( errCode != 0 ) {
		gfn_showMsg("CE00001");
	} else {
		gfn_showMsg("CI00003");		
	}

	fn_query();
}

function grd_detail_OnCellPosChanged(obj,nRow,nCell,nOldRow,nOldCell,nPivotIndex,nOldPivotIndex)
{
	//메일전송일자가 지정되어 있으면 납기일/판매금액 수정불가
	if(grd_header.GetCellValue(grd_header.CurrentRow, grd_header.GetBindCellIndex("Body", "SEDAT")) != "00000000")
	{		
		grd_detail.Editable = false;
	}
	else
	{
		grd_detail.Editable = true;
	}
}

function grd_Header_OnCellPosChanged(obj,nRow,nCell,nOldRow,nOldCell,nPivotIndex,nOldPivotIndex)
{
	// 메일전송일자가 지정되어 있으면 비고 수정불가
	if( grd_header.GetCellValue(nRow, grd_header.GetBindCellIndex("Body", "SEDAT")) != "00000000")
	{		
		grd_header_remark.Editable = false;
	}
	else
	{
		grd_header_remark.Editable = true;
	}
	ds_header_remark.fireEvent = false;
	ds_header_remark.ClearData();
	ds_header_remark.AddRow();
	ds_header_remark.CopyRow(0, ds_header, nRow);
	ds_header_remark.fireEvent = true;

	// 상세 그리드에서, 값을 변경하고 저장하지 않은 상태에서 다른 행으로 옮길 때, 원래 값 복원
	if ( nRow != nOldRow ) {
		ds_header.fireEvent = false;
		ds_header.SetColumn(nOldRow, "SHNETWR", ds_header.GetOrgColumn(nOldRow, "SHNETWR"));
		ds_header.fireEvent = true;
	}
}

