// 달력 처리 부분 
/********************************************************************************
* 기      능   : 그리드에서 날짜 입력시 (헤더 그리드)
********************************************************************************/
function grd_header_OnExpandEdit(obj,nRow,nCell,strVal,nPivotIndex)
{
	F_SELECTED_GRID  = obj;
	F_SELECTED_COLID = obj.GetCellProp("body", nCell, "ColId");

	var rect = obj.GetCellRect(nRow, ncell);
	cal_temp.top = obj.Top + rect[1] + 20;
	cal_temp.Left = rect[0] - 50; 
	cal_temp.Visible = true;
	cal_temp.value = ds_header.GetColumn(nRow, F_SELECTED_COLID);
	cal_temp.SetFocus();
}

/********************************************************************************
* 기      능   : 그리드에서 날짜 입력시 (디테일 그리드)
********************************************************************************/
function grd_detail_OnExpandEdit(obj,nRow,nCell,strVal,nPivotIndex)
{
	F_SELECTED_GRID  = obj;
	F_SELECTED_COLID = obj.GetCellProp("body", nCell, "ColId");

	var rect = obj.GetCellRect(nRow, ncell);
	cal_temp.top = obj.Top + rect[1] + 20;
	cal_temp.Left = rect[0] - 50; 
	cal_temp.Visible = true;
	cal_temp.value = ds_detail.GetColumn(nRow, F_SELECTED_COLID);
	cal_temp.SetFocus();
}

/********************************************************************************
* 기      능   : 달력에서 일자 선택했을 경우 (헤더, 디테일 공통)
********************************************************************************/
function cal_temp_OnDayClick(obj,strText)
{
	if ( F_SELECTED_GRID == grd_header ) {
		ds_header.SetColumn(ds_header.row, F_SELECTED_COLID, strText);
	} else if ( F_SELECTED_GRID == grd_detail ) {
		ds_detail.SetColumn(ds_detail.row, F_SELECTED_COLID, strText);
	}
	obj.Visible = false;
}

/********************************************************************************
* 기      능   : 달력에서 포커스 잃었을 경우 (헤더, 디테일 공통)
********************************************************************************/
function cal_temp_OnKillFocus(obj)
{
	obj.Visible = false;
}

/********************************************************************************
* 기      능   : 달력을 보여주는 컬럼의 ExpandShow 설정 (헤더 그리드-납기일자)
********************************************************************************/
function ufn_setHeaderExpandshow(nRow, nCurrentCell)
{
	var sColId = grd_header.GetCellProp("body", nCurrentCell, "ColId");
	if ( nRow == ds_header.row && sColId == "EDATU" && ds_header.GetColumn(ds_header.row, "SEDAT") == "00000000" ) {
		return true;
	} else {
		return false;
	}
}
/********************************************************************************
* 기      능   : 달력을 보여주는 컬럼의 Edit 설정 (헤더 그리드-납기일자)
********************************************************************************/
function ufn_setHeaderEdit(nRow, nCurrentCell)
{
	var sColId = grd_header.GetCellProp("body", nCurrentCell, "ColId");
	if ( nRow == ds_header.row && sColId == "EDATU" && ds_header.GetColumn(ds_header.row, "SEDAT") == "00000000" ) {
		return "date";
	} else {
		return "none";
	}
}
/********************************************************************************
* 기      능   : 달력을 보여주는 컬럼의 Mask 설정 (헤더 그리드-납기일자)
********************************************************************************/
function ufn_setHeaderMask(nRow, nCurrentCell)
{
	var sColId = grd_header.GetCellProp("body", nCurrentCell, "ColId");
	if ( nRow == ds_header.row && sColId == "EDATU" && ds_header.GetColumn(ds_header.row, "SEDAT") == "00000000" ) {
		return "";
	} else {
		return "####.##.##";
	}
}

