/********************************************************************************
* 기      능   : 국가코드 조회
********************************************************************************/
function btn_Land1_popup(obj)
{
	var param = ed_land1.Text;
	var arg  = "popFlag="+"nation"; // pop구분값 ( nation:국가 / area:운송지역 )
		arg += " pCdNm="+quote(param); // 넘길 값

	var arr = gfn_openPopupUrl("COM::Com0060.xml", true, arg);

	if (arr != null && arr != 0)
	{
		ed_land1.Text = arr[0];
		ed_landx.Text = arr[1];
		ed_land1.SetFocus();
	}
}

function ds_header_remark_OnColumnChanged(obj,nRow,strColumnID,varOldValue,varNewValue,nPivotIndex)
{
	ds_header.fireevent = false;
	if(strColumnID!="FLAG"){
		ds_header.CopyRow(ds_header.row, obj, 0);
		ds_header.SetColumn(ds_header.row, "FLAG", 1);
	}
	ds_header.fireevent = true;
}

function ufn_openPjt(obj)
{
	var arg = "pjt="+quote(ed_pjt.Text);
	var arr = gfn_openPopup("CS9601002A",true,arg);

	if (arr != null && arr != 0) {
		ed_pjt.Text = arr[0];
		ed_pjtnm.Text = arr[2];
	}
}

/******************************************************
// 프로젝트번호  변경시 명칭 clear처리
******************************************************/
function ed_pjt_OnCharChanged(obj,strPreText,strPostText)
{
	if (strPreText != strPostText)
		ed_pjtnm.Text = "";
}

/******************************************************
// 국가코드 변경시 명칭 clear처리
******************************************************/
function ed_land1_OnCharChanged(obj,strPreText,strPostText)
{
	if (strPreText != strPostText)
		ed_landx.Text = "";
}
