var fn_query_chk = false; //중복조회 방지용 flag

function fn_query()
{

	var arr = ['Deliv. Date', '12'];
	if(gfn_getDayCnt(ds_days, cal_edatufr.Value, cal_edatuto.Value) > 365) {
		gfn_showAlert("CW10087", arr);
		cal_edatufr.SetFocus();
		return false;
	}

	if(!fn_query_chk) {
		fn_query_chk = true;
	} else {
		gfn_showAlert("CW10025", "조회중입니다..");
		return;
	}

	ds_cond.DeleteAll();
	ds_cond.AddRow();
	ds_cond.SetColumn(0, "EDATUFR"	, cal_edatufr.Value	);
	ds_cond.SetColumn(0, "EDATUTO"	, cal_edatuto.Value	);
	ds_cond.SetColumn(0, "SEDAT"	, "99999999"		); // 메일전송완료 FLAG
	ds_cond.SetColumn(0, "SPRAS"	, G_LANG					);
	ds_cond.SetColumn(0, "ZZPJT_ID" , ed_pjt.Text				); // PROJ. NO.
	//ds_cond.SetColumn(0, "BSTKD"   	, ed_pjtnm.Text         	); // PROJ. NAME
	ds_cond.SetColumn(0, "LAND1" 	, ed_land1.Text				); // 국가코드
	//ds_cond.SetColumn(0, "VKBUR" 	, div_vbvg.cb_frvb.Value	); // 부서
	//ds_cond.SetColumn(0, "VKGRP" 	, div_vbvg.cb_frvg.Value	); // 팀
	ds_cond.SetColumn(0, "ZKUNNR" 	, div_sman.ed_sman_cd.Text	); // 담당자

	nsfRequest("header", "ses0330/header", "ds_cond=ds_cond ds_header=ds_header", "ds_header=ds_header ds_error=ds_error", "", "fn_afterQuery");
	fn_createWait("",this);     // 처리중입니다. 메시지 처리
}

/********************************************************************************
* 기      능   : 조회 2
********************************************************************************/
function fn_detail(nRow)
{
	ds_cond.SetColumn(0, "MANDT", ds_header.GetColumn(nRow, "MANDT"));
	ds_cond.SetColumn(0, "VBELN", ds_header.GetColumn(nRow, "VBELN"));	
	ds_cond.SetColumn(0, "ZSEQ",  ds_header.GetColumn(nRow, "ZSEQ"));
	nsfRequest("detail", "ses0330/detail", "ds_cond=ds_cond ds_detail=ds_detail", "ds_detail=ds_detail ds_error=ds_error", "", "fn_afterQuery");
}

/********************************************************************************
* 기      능   : 폼 초기화
********************************************************************************/
function fn_init_form()
{
	// 납기일자
	cal_edatufr.Value = "";
	//cal_edatufr.Value = gfn_getCurrDate();
	cal_edatuto.Value = gfn_getCurrDate();

	//코드콤보 설정 
	var codeArr = ["INCO1"]; 	//코드콤보 설정
	var dsObjArr = [ds_inco1];
	var addTypeArr = [G_CODE_ALL];
	gfn_searchCommCode(codeArr, dsObjArr, addTypeArr);
}

