//=========================================================================================
// [ PART 4]
// Callback 함수 정의 : callback 함수명은 메인 함수의 fn_xxxx() 에서 xxxx 부분은 fn_afterXXXX()
// 형태가 되도록 작성 
//=========================================================================================
/********************************************************************************
* 기      능   : 조회 Callback  
********************************************************************************/
function fn_afterQuery(svcId, errCode, errMsg)
{

	fn_query_chk = false; //중복조회 방지용 flag

	if (svcId == "header") {
		gfn_clearGridSortMark(grd_header);
		fn_detail(0);
		return;
	} else {
		gfn_clearGridSortMark(grd_detail);
		fn_destroyWait();
		if ( F_PRINT_FLAG == 1 ) {
			F_PRINT_FLAG = 0;
			fn_print();
		}
	}

	if(ds_error.rowcount() > 0) {
		gfn_showMsg("CE00001");
		gfn_openWSErrorWin(grd_header, ds_header, ds_error, "FLAG");	// error 정보 표시 
	} else {
		if(errCode != 0) gfn_showMsg("CE00001");
		else             gfn_showMsg("CI00002", ds_header.RowCount() + "");
	}
}

