/********************************************************************************
* 기      능   : 출력
********************************************************************************/
function fn_print() {

	var nHeaderCheckedRow = ds_header.row;

	ds_header2.DeleteAll();
	//for ( var i = 0 ; i < ds_header.GetRowCount() ; i++ ) {
		//if ( ds_header.GetColumn(i, "CHK") == 1 ) {
			var nRow = ds_header2.AddRow();
			ds_header2.CopyRow(nRow, "ds_header", nHeaderCheckedRow);
			//nHeaderCheckedRow = i;
		//}
	//}
	if ( ds_header2.GetRowCount() > 1 ) {
		gfn_showAlert("CW00222");		//하나의 자료만 선택하여 주십시오.
		return;
	} else if ( ds_header2.GetRowCount() == 0 ) {
		gfn_showAlert("CW10021");		//선택된 데이터가 없습니다. \n확인하시기 바랍니다.
		return;
	}

	// CHECK한 행이 현재 선택한 행일 경우
	if ( nHeaderCheckedRow == ds_header.row ) {
		var sumKwmeng = 0;
		var sumNetwr = 0;
		var vrkme;
		for(var i =0; i<ds_detail.rowcount; i++)
		{
			sumKwmeng = sumKwmeng + ParseInt(ds_detail.GetColumn(i, "KWMENG"));	//수량합계
			sumNetwr = sumNetwr + ParseInt(ds_detail.GetColumn(i, "SHNETWR"));	//판매금액합계
		}
		vrkme = ds_detail.GetColumn(0, "VRKME");								//판매단위

		var arg = "sumKwmeng=" + sumKwmeng
				+ " sumNetwr=" + sumNetwr
				+ " vrkme=" + vrkme;
		Dialog("SES::Ses0339.xml", arg, -1, -1, "Resize=true");	// UbiReport Call
	} 
	// CHECK한 행이 현재 선택한 행이 아닐 경우
	else {
		F_PRINT_FLAG = 1;
		ds_header.row = nHeaderCheckedRow;
		fn_detail(ds_header.row);
	}
}

/********************************************************************************
* 기      능   : TXT 저장
********************************************************************************/
function btn_filesave_OnClick(obj)
{
	if ( ds_header.GetRowCount() == 0 ) {
		gfn_showAlert("CW10021");		//선택된 데이터가 없습니다. \n확인하시기 바랍니다.
		return;
	}

	ds_header2.ClearData();
	ds_header2.AddRow();
	ds_header2.CopyRow(0, ds_header, ds_header.row);

	ds_cond.ClearData();
	ds_cond.AddRow();
	ds_cond.SetColumn(0, "MANDT", ds_header.GetColumn(ds_header.row, "MANDT"));
	ds_cond.SetColumn(0, "VBELN", ds_header.GetColumn(ds_header.row, "VBELN"));	
	ds_cond.SetColumn(0, "ZSEQ"	, ds_header.GetColumn(ds_header.row, "ZSEQ"));
	ds_cond.SetColumn(0, "SPRAS", G_LANG									);

	nsfRequest("saveTxt", "ses0330/saveTxt", "ds_cond=ds_cond ds_header=ds_header2 ds_detail=ds_detail ds_down=ds_down", "ds_down=ds_down ds_error=ds_error", "", "fn_afterDown");
}

/********************************************************************************
* 기      능   : TXT 파일 생성 서버측 스크립트가 완료된 후 Callback 함수
********************************************************************************/
function fn_afterDown(errCode, errMsg)
{
	var buffer = ds_down.GetColumn(0, "txt");
	var bufferSize = length(buffer);

	saveDialog.Type = "save";
	saveDialog.Open();

	var sFileName = saveDialog.FilePath + "\\" + saveDialog.FileName;
	if ( toLower(right(sFileName, 4)) != ".txt" ) {
		sFileName += ".txt";
	}

	f_tmp.FileName = sFileName;
	f_tmp.Open("wb");

	var len = f_tmp.WriteBinary( buffer, bufferSize ); // 파일에 기록할 내용이 담긴 buffer, 한번에 적을 Buffer의 Size.

	f_tmp.Close();

	// 파일 저장 후 재조회 (상해접수일자 변경을 반영하기 위함)
	fn_query();
}

function btn_Land1_popup(obj)
{
	var param = ed_land1.Text;
	var arg  = "popFlag="+"nation"; // pop구분값 ( nation:국가 / area:운송지역 )
		arg += " pCdNm="+quote(param); // 넘길 값

	var arr = gfn_openPopupUrl("COM::Com0060.xml", true, arg);

	if (arr != null && arr != 0)
	{
		ed_land1.Text = arr[0];
		ed_landx.Text = arr[1];
		ed_land1.SetFocus();
	}
}

function ufn_openPjt(obj)
{
	var arg = "pjt="+quote(ed_pjt.Text);
	var arr = gfn_openPopup("CS9601002A",true,arg);

	if (arr != null && arr != 0) {
		ed_pjt.Text = arr[0];
		ed_pjtnm.Text = arr[2];
	}
}

/******************************************************
// 프로젝트번호  변경시 명칭 clear처리
******************************************************/
function ed_pjt_OnCharChanged(obj,strPreText,strPostText)
{
	if (strPreText != strPostText)
		ed_pjtnm.Text = "";
}

/******************************************************
// 국가코드 변경시 명칭 clear처리
******************************************************/
function ed_land1_OnCharChanged(obj,strPreText,strPostText)
{
	if (strPreText != strPostText)
		ed_landx.Text = "";
}
