//저장할 영업데이터가 존재하는지 확인후 있을시 master 및 detail insert
function fn_save()
{
	gfn_clearGridSortMark(grd_detail);

	// 저장 대상 데이터 복사
	grd_detail.Redraw = false;
	ds_hdrdtl.Filter("FLAG=1");
	ds_hdrdtl_chk.CopyF("ds_hdrdtl");
	ds_hdrdtl.UnFilter();
	grd_detail.Redraw = true;

	if ( ds_hdrdtl_chk.GetRowCount() == 0 ) {
		gfn_showAlert("CW10021");	//선택된 데이터가 없습니다. \n확인하시기 바랍니다.
		return;
	}

	// 오더번호 승인 체크
	if ( ds_hdrdtl_chk.FindRow("RECAD_DA", "00000000") > -1 ) {
		gfn_showAlert("CW10211");	//승인된 오더번호가 아닙니다.
		return;
	}

	// 다른 오더번호를 선택한 경우
	vbeln = ds_hdrdtl_chk.GetColumn(0, "VBELN");
	for ( var i = 0 ; i < ds_hdrdtl_chk.GetRowCount() ; i++ ) {
		if ( vbeln != ds_hdrdtl_chk.GetColumn(i, "VBELN")) {
			gfn_showAlert("CW00116", "한 개의 오더번호만");
			return;
		}
	}

	if ( !gfn_showConfirm("CW10209") ) { //PO생성하시겠습니까?
		return;
	}

	ds_cond.setColumn(0, "MANDT"  	, G_MANDT);
	ds_cond.setColumn(0, "VBELN"	, ds_hdrdtl.GetColumn(0, "VBELN"));
	ds_cond.setColumn(0, "ERNAM"	, G_SAP_USER_ID);
	ds_cond.setColumn(0, "ZSEQ"		, "");

	nsfRequest("query", "ses0330/checkVbak", "ds_cond=ds_cond ds_list=ds_list ds_hogi=ds_hogi ds_sapinsert=ds_sapinsert ds_hdrdtl=ds_hdrdtl_chk", "ds_hdrdtl=ds_hdrdtl ds_error=ds_error", "", "fn_afterSave");
	fn_createWait("",this);     // 처리중입니다. 메시지 처리
}


/********************************************************************************
* 기      능   : 저장 Callback
********************************************************************************/
function fn_afterSave(svcId, errCode, errMsg) {
	fn_destroyWait();

	if ( ds_error.rowcount > 0 ) {
		gfn_showAlert(ds_error.GetColumn(0,"ERRMSG"));
	}
	parent.fn_query();
	fn_close();
}

//insert 한데이터의 호기번호로 RFC연결후 특성 가져오기(LOOP)
function fn_selectHogi(errCode, errMsg){

	//ds_sap.ClearData();		//RFC조회용 데이터셋 초기화

	if(ds_list.rowcount < 1)
	{	
		fn_destroyWait();
		alert("해당 오더번호가 없습니다.");
		return;
	}

	if(ds_hogi.rowcount < 1)
	{	
		fn_destroyWait();
		alert("해당 품목의 호기번호가 없습니다.");
		return;
	}

	for(var i = 0; i<ds_hogi.rowcount; i++)
	{
		ds_cond.ClearData();
		ds_cond.AddRow();	
		ds_cond.SetColumn(0, "HOGI", ds_hogi.GetColumn(i, "POSID"));

		alert(ds_cond.SaveXML());

		nsfRequest("query", "ses0320/receive", "ds_cond=ds_cond", "ds_sap=ds_list ds_sap2=ds_list2 ds_error=ds_error", "", "");

		fn_aftersap();
		//nsfRequest("query", "ses0320/receive", "ds_cond=ds_cond", "ds_sap=ds_list ds_sap2=ds_list2 ds_error=ds_error", "", "fn_aftersap");
		//fn_createWait("",this);     // 처리중입니다. 메시지 처리
	}

	fn_afterSave(errCode, errMsg);
}

function fn_aftersap(errCode, errMsg){
	//fn_destroyWait();

	if(ds_sap.rowcount < 1)
	{
		//fn_destroyWait();
		alert("해당 호기번호의 수주데이터가 없습니다.");
		return;
	}

	//trace(ds_sap.SaveXML());

	//alert("sap가져온정보 " + ds_sapinsert.SaveXML());

	for( var i=0; i < ds_sap.rowcount; i++) {

		var nRow = ds_sapinsert.addRow();

		ds_sapinsert.setColumn(nRow, "MANDT"			, G_MANDT);
		ds_sapinsert.setColumn(nRow, "VBELN" 			, ds_hogi.getColumn(0, "VBELN"));
		ds_sapinsert.setColumn(nRow, "ZSEQ"				, ds_hogi.getColumn(0, "ZSEQ"));
		ds_sapinsert.setColumn(nRow, "POSNR" 			, ds_hogi.getColumn(0, "POSNR"));
		ds_sapinsert.setColumn(nRow, "CHARACTERISTIC" 	, ds_sap.getColumn(i, "NAM_CHAR"));
		ds_sapinsert.setColumn(nRow, "POSID" 			, ds_hogi.getColumn(0, "POSID"));
		ds_sapinsert.setColumn(nRow, "VALUE" 			, ds_sap.getColumn(i, "VALUE1"));				

	}	
		//alert(ds_sapinsert.SaveXML());
		nsfRequest("saveSap", "ses0330/saveSap", "ds_sapinsert=ds_sapinsert", "ds_error=ds_error", "", "");


		//fn_createWait("",this);     // 처리중입니다. 메시지 처리

		//if (pGubun == "U") fn_close();// 신규 저장	


}

/******************************************************
// 국가코드 변경시 명칭 clear처리
******************************************************/
function ed_land1_OnCharChanged(obj,strPreText,strPostText)
{
	if (strPreText != strPostText)
		ed_landx.Text = "";
}

function btn_Land1_popup(obj)
{
	var param = ed_land1.Text;
	var arg  = "popFlag="+"nation"; // pop구분값 ( nation:국가 / area:운송지역 )
		arg += " pCdNm="+quote(param); // 넘길 값

	var arr = gfn_openPopupUrl("COM::Com0060.xml", true, arg);

	if (arr != null && arr != 0)
	{
		ed_land1.Text = arr[0];
		ed_landx.Text = arr[1];
		ed_land1.SetFocus();
	}
}

function ufn_openPjt(obj)
{
	var arg = "pjt="+quote(ed_pjt.Text);
	var arr = gfn_openPopup("CS9601002A",true,arg);

	if (arr != null && arr != 0) {
		ed_pjt.Text = arr[0];
		ed_pjtnm.Text = arr[2];
	}
}

/******************************************************
// 프로젝트번호  변경시 명칭 clear처리
******************************************************/
function ed_pjt_OnCharChanged(obj,strPreText,strPostText)
{
	if (strPreText != strPostText)
		ed_pjtnm.Text = "";
}
