/********************************************************************************
* 기      능   : 조회 1
********************************************************************************/
var fn_query_chk = false; //중복 조회방지용 flag

function fn_query() {

	if ( div_vbvg.cb_frvb.Value = "C600" || div_vbvg.cb_frvb.Value = "C700" || div_vbvg.cb_frvb.Value = "C800" ) {
	   gfn_showAlert("CW10025", "SS/주차 부서는 통합영업관리(SS/주차) 메뉴를 이용하세요");
	   return;
	}

	if (!fn_query_validation())	{
		return;
	}

	if(!fn_query_chk) {
		fn_query_chk = true;
	} else {
		gfn_showAlert("CW10025", "조회중입니다..");
		return;
	}

	fn_createWait("",this);     // 처리중입니다. 메시지 처리

	ds_detail.ClearData();

	ds_error.ClearData();
	ds_cond.DeleteAll();
	ds_cond.AddRow();

	ds_cond.SetColumn(0, "FR_QTDAT",  cal_qtdat_fm.Value);
	ds_cond.SetColumn(0, "TO_QTDAT",  cal_qtdat_to.Value);
	ds_cond.SetColumn(0, "FR_ZRQDAT", cal_zrqdat_fm.Value);
	ds_cond.SetColumn(0, "TO_ZRQDAT", cal_zrqdat_to.Value);
	ds_cond.SetColumn(0, "VKBUR",     div_vbvg.cb_frvb.Value);
	ds_cond.SetColumn(0, "VKGRP",     div_vbvg.cb_frvg.Value);
	ds_cond.SetColumn(0, "ZKUNNR",    div_sman.ed_sman_cd.Text);
	ds_cond.SetColumn(0, "ZRQSTAT",   cb_zrqstat.Value);
	ds_cond.SetColumn(0, "GUBUN",     cb_gubun.Value);
	ds_cond.SetColumn(0, "QTSO_NO",   ed_qtso_no.Text);

	nsfRequest(  "header"
			   , "ses0400/findListHeader"
			   , "ds_cond=ds_cond ds_header=ds_header"
			   , "ds_header=ds_header ds_error=ds_error"
			   , ""
			   , "fn_afterQuery"
			  );
}

/********************************************************************************
* 기      능   : 조회조건 필수 체크
********************************************************************************/
function fn_query_validation()
{
	var vMSG = "생성기간";

	// 필수조회조건 체크
	if(length(trim(cal_qtdat_fm.Value)) != 8)	{
		gfn_showAlert("CW00001", vMSG);
		cal_qtdat_fm.SetFocus();
		return false;
	}

	if(length(trim(cal_qtdat_to.Value)) != 8)	{
		gfn_showAlert("CW00001", vMSG);
		cal_qtdat_to.SetFocus();
		return false;
	}

	vMSG = "요청기간";
	var vMSGs = ['요청기간 From','요청기간 To'];
	if(length(trim(cal_zrqdat_fm.Value)) > 0 && length(trim(cal_zrqdat_fm.Value)) < 8)	{
		gfn_showAlert("CW00008", vMSG);
		cal_zrqdat_fm.SetFocus();
		return false;
	}else if (length(trim(cal_zrqdat_fm.Value)) == 8)	{
		if (length(trim(cal_zrqdat_to.Value)) != 8)	{
			gfn_showAlert("CW00004", vMSGs);
			cal_zrqdat_to.SetFocus();
			return false;
		}
	}

	vMSGs = ['요청기간 To','요청기간 From'];
	if(length(trim(cal_zrqdat_to.Value)) > 0 && length(trim(cal_zrqdat_to.Value)) < 8)	{
		gfn_showAlert("CW00008", vMSG);
		cal_zrqdat_to.SetFocus();
		return false;
	}else if (length(trim(cal_zrqdat_to.Value)) == 8)	{
		if (length(trim(cal_zrqdat_fm.Value)) != 8)	{
			gfn_showAlert("CW00004", vMSGs);
			cal_zrqdat_fm.SetFocus();
			return false;
		}
	}

	var arr = ['생성기간', '12'];
	if(gfn_getDayCnt(ds_days, cal_qtdat_fm.Value, cal_qtdat_to.Value) > 365) {
		gfn_showAlert("CW10087", arr);
		cal_qtdat_fm.SetFocus();
		return false;
	}

	return true;
}

/********************************************************************************
* 기      능   : 조회 2
********************************************************************************/
function fn_detail() {

	fn_createWait("",this);     // 처리중입니다. 메시지 처리

	ds_error.ClearData();
	ds_cond.ClearData();
	ds_cond.AddRow();

	ds_cond.SetColumn(0, "QTSO_NO", grd_header.GetCellText("Body", grd_header.CurrentRow, 2));
	ds_cond.SetColumn(0, "QTSER",   grd_header.GetCellText("Body", grd_header.CurrentRow, 3));
	ds_cond.SetColumn(0, "QTSEQ",   grd_header.GetCellText("Body", grd_header.CurrentRow, 4));
	ds_cond.SetColumn(0, "HOGI",    grd_header.GetCellText("Body", grd_header.CurrentRow, 5));

	nsfRequest( "detail"
			  , "ses0400/findListDetail"
			  , "ds_cond=ds_cond ds_detail=ds_detail"
			  , "ds_detail=ds_detail ds_error=ds_error"
			  , ""
			  , "fn_afterQuery"
			  );
}


/********************************************************************************
* 기      능   : 닫기  
********************************************************************************/
function fn_close() {
	close();
}

//=========================================================================================
// [ PART 4]
// Callback 함수 정의 : callback 함수명은 메인 함수의 fn_xxxx() 에서 xxxx 부분은 fn_afterXXXX()
// 형태가 되도록 작성 
//=========================================================================================
/********************************************************************************
* 기      능   : 조회 Callback  
********************************************************************************/
function fn_afterQuery(svcId, errCode, errMsg) {

	fn_query_chk = false; //중복조회 방지용 flag

	fn_destroyWait();
	if(ds_error.rowcount() > 0) 	{
		gfn_showAlert("CE00001");
		return;
	}

	if (svcId == "header")	{
		fn_header_grid_change(cb_gubun.Value);
		grd_header_OnCellClick();
	}

	gfn_showMsg("CI00002", ds_header.RowCount() + "");
}

/********************************************************************************
* 기      능   : 공통코드가 조회된 후의 Callback 함수 
********************************************************************************/
function fn_completeInit(sCode, sDsObj, sCodeType) {
	cb_gubun.Index = 0;
	fn_header_grid_change(cb_gubun.Value);
}

