/********************************************************************************
* 기      능   : 업무구분 변경 시 Header 그리드 Title변경
********************************************************************************/
function fn_header_grid_change(strCode)
{
	if (strCode == "P")		{	// 프로젝트
		Static1.Text = "프로젝트";
		grd_header.SetCellProp("head", 2, "Text", "프로젝트");		// 
		grd_header.SetColProp(3, "Width", 0);						// 
		grd_header.SetColProp(4, "Width", 0);						// 
		grd_header.SetColProp(5, "Width", 90);						// 
	}else	{					// 견적
		Static1.Text = "견적번호";
		grd_header.SetCellProp("head", 2, "Text", "견적번호");		// 
		grd_header.SetColProp(3, "Width", 50);						// 
		grd_header.SetColProp(4, "Width", 85);						// 
		grd_header.SetColProp(5, "Width", 0);						// 
	}
}

/********************************************************************************
* 기      능   :  SPEC 정보 추출
********************************************************************************/
function fn_specInfo(strGubun, nRow, strMatnr, strRtype, strType1, strType2, strType3, strZacapa, strZuse, strZopn, strZlen)
{
	if (strGubun == "Q")	{	// 견적의 경우만 처리
		var strVkbur = ds_header.GetColumn(nRow, "VKBUR");
		var strSpec  = "";
		strSpec = gfn_specInfo(strVkbur, strMatnr, strRtype, strType1, strType2, strType3, strZacapa, strZuse, strZopn, strZlen);
		ds_header.SetColumn(nRow, "SPEC", strSpec);
		return strSpec;
	}
}

