/********************************************************************************
* 기      능   :  기술검토요청
********************************************************************************/
function btn_techInspect_OnClick(obj) {

	if(ds_header.GetRowCount() == 0) {
		gfn_showAlert('CW00013');
		return;
	}

	// 비표준승인배포 요청시 비표준도면요청 선요청 필요
	if (obj.id == 'btn_nstd_pub') {
		var hasDesignRequest = false;
		for (var i=0; i<ds_detail.GetRowCount(); i++) {
			if (ds_detail.GetColumn(i, "ZRQFLG") == 'D') {
				hasDesignRequest = true;
			}
		}
		if(!hasDesignRequest) {
			alert("'비표준 도면요청' 의뢰 후 '비표준 승인배포' 의뢰 바랍니다.");
			return;
		}
	}

	arg =  ' fa_zrqseq = ' + quote('');
	arg += ' fa_qtso_no = ' + quote(ds_header.GetColumn(ds_header.currow, 'QTSO_NO'));
	arg += ' fa_qtser = ' + quote(ds_header.GetColumn(ds_header.currow, 'QTSER'));
	arg += ' fa_qtseq = ' + quote(ds_header.GetColumn(ds_header.currow, 'QTSEQ'));
	arg += ' fa_autolp  = ' + quote(ds_header.GetColumn(ds_header.currow, 'AUTOLP'));

	switch(obj) {
		case btn_design_request:	//비표준도면요청
			arg += ' fa_zrqflg = ' + quote('D');
			break;

		case btn_accept_approval:	//표준승인배포
			arg += ' fa_zrqflg = ' + quote('A');
			arg += ' fa_hogi  = ' + quote(ds_header.GetColumn(ds_header.currow, 'HOGI'));
			break;

		case btn_nstd_pub:	//비표준승인배포
			arg += ' fa_zrqflg = ' + quote('B');
			break;

		case btn_tech_req:	//기술검토요청
			arg += ' fa_zrqflg = ' + quote('T');
			break;

		default:
			break;
	}
	fn_callPopup(arg);
}
