/********************************************************************************
* 기      능   :  더블클릭으로 상세화면 호출
********************************************************************************/
function grd_detail_OnCellDblClick(obj,nRow,nCell,nX,nY,nPivotIndex)
{
    var gubun = substr(ds_detail.GetColumn(nRow, "ZRQSEQ"),0,1);

	var arg =  " fa_zrqseq = " + quote(ds_detail.GetColumn(nRow, "ZRQSEQ"));
	    arg += " fa_zrqflg = " + quote("");
	    arg += " fa_qtso_no = " + quote("");
	    arg += " fa_qtser = " + quote("");
	    arg += " fa_qtseq = " + quote("");
	    arg += " fa_hogi  = " + quote("");

    if (gubun == "X") {
    	fn_callPopup2(arg);
    }else{
		fn_callPopup(arg);
	}
}

/********************************************************************************
* 기      능   :  출력 버튼 클릭
********************************************************************************/
function grd_detail_OnExpandEdit(obj,nRow,nCell,strVal,nPivotIndex)
{
	if (nCell == 10)	{

		var arg  = "P_ZRQSEQ=" +quote(ds_detail.GetColumn(nRow, "ZRQSEQ"));

		// 출력용 조회 필요
		if (length(trim(ds_detail.GetColumn(nRow, "HOGI")))	> 0)	{	// 프로젝트 정보
			arg += " P_GUBUN=P";
		}else	{		// 견적정보로 처리
			arg += " P_GUBUN=Q";
		}

		fn_createWait("",this);     // 처리중입니다. 메시지 처리

		ds_error.ClearData();
		nsfRequest(  "print"
				   , "ses0401/findPrint"
				   , "ds_print=ds_print ds_template=ds_template"
				   , "ds_print=ds_print ds_template=ds_template ds_error=ds_error"
				   , arg
				   , "fn_afterPrint"
				  );
	}
}

