// 견적번호 또는 프로젝트 번호 더블 클릭 시  상세 사양 조회 popup처리
function grd_header_OnCellDblClick(obj,nRow,nCell,nX,nY,nPivotIndex)
{
	if (ds_header.RowCount() == 0)
		return;

	var matnr = trim( ds_header.getColumn(nRow, "MATNR") );
	if (matnr == null || matnr == "")
	{
		gfn_showAlert("CW10046");
		return false;
	}else if (matnr == "F-1000")	{	// 물류는 사양이 존재하지 않음
		return false;
	}

	// 견적번호 또는 프로젝트 번호 
	if (nCell == 2)	{
		ds_arg.ClearData(); 
		var addRow = ds_arg.addRow();

		if (ds_header.GetColumn(nRow, "GUBUN") == "P")		//  프로젝트 경우
		{
			ds_arg.setColumn(addRow, "GUBUN", "SO");		// 수주
			ds_arg.setColumn(addRow, "MANDT", G_MANDT);
			ds_arg.setColumn(addRow, "VBELN", ds_header.getColumn(nRow, "QTSO_NO"));	// 프로젝트
			ds_arg.setColumn(addRow, "MATNR", matnr);  // 자재번호

			// 판매문서품목 체크
			posnr = trim(ds_header.getColumn(nRow, "POSNR"));
			if (posnr == null || posnr == "")
			{
				gfn_showAlert("CW10068");
				return false;
			}

			ds_arg.setColumn(addRow, "POSNR",   posnr);   // 판매문서품목
			ds_arg.setColumn(addRow, "HOGI",    trim(ds_header.getColumn(nRow, "HOGI")));      // 호기
		}
		else	// 견적정보 경우
		{
			ds_arg.setColumn(addRow, "GUBUN", "QT");		// 견적
			ds_arg.setColumn(addRow, "MANDT", G_MANDT); 
			ds_arg.setColumn(addRow, "QTNUM", ds_header.getColumn(nRow, "QTSO_NO")); 	// 견적번호
			ds_arg.setColumn(addRow, "QTSER", ds_header.getColumn(nRow, "QTSER"));	// 견적차수
			ds_arg.setColumn(addRow, "QTSEQ", ds_header.getColumn(nRow, "QTSEQ"));	// 견적일련번호
			ds_arg.setColumn(addRow, "MATNR", matnr);  // 자재번호
		}

		var vArg = "fa_gubun = " + quote("T");
		var arr = gfn_openPopUpUrlHdel("SSO::Sso0062.xml", true, vArg);

		if (arr != null && arr != 0) {
			return;
		}	
	}
}

// 이동시 처리
function grd_header_OnCellPosChanged(obj,nRow,nCell,nOldRow,nOldCell,nPivotIndex,nOldPivotIndex)
{
	if (nOldRow != nRow)	{
		grd_header_OnCellClick(obj, nRow, nCell, "", "", nPivotIndex);
	if(trim(ds_header.GetColumn(nRow, 'VBELN')) == '')
		btn_request_site.Enable = true;
	else
		btn_request_site.Enable = false;

	}
}

// 비표준도면요청 존재 여부 check
function fn_hasDesignRequest()
{
	trace(this);
	var hasDesignRequest = false;
	for (var i=0; i<ds_detail.GetRowCount(); i++) {
		if (ds_detail.GetColumn(i, "ZRQFLG") == 'B') {
			hasDesignRequest = true;
			return hasDesignRequest;
		}
	}
	return hasDesignRequest;
}
