	//btn_approval.Enable = false;
	btn_detail.Enable   = false;

	// 부서코드 정보에 따라 업무구분 결정
	/*if (G_SAP_USER_VGCD == "B81")	{
		cb_zrqflg.Value = "D";		// 도면 (설계부팀)
	}else */
	if (G_SAP_USER_VGCD == "B82")	{
		cb_zrqflg.Value = "R";		// 디자인(디자인팀)
	}
	/*else	{
		cb_zrqflg.Value = "D";
		//gfn_showAlert("CW10025", "권한이 존재하지 않습니다. 정보기술팀에 문의 하십시요.");
		//this.Close();
	}*/

	cb_zrqflg.Enable = false;

	var codeArr    = ["VKBUR",   "VKGRP", "BS403"]; 	//코드콤보 설정
	var dsObjArr   = [ds_vkbur, ds_vkgrp, ds_zbdtyp];
	var addTypeArr = [G_CODE_ALL];
	gfn_searchCommCode(codeArr, dsObjArr, addTypeArr);

	gfn_queryDD07T('ZRQSTAT', ds_zrqstat_cd, 'N');		//진행상태
	gfn_queryDD07T('ZSDD_CAD_REQTYPE', ds_cadreqtyp_cd, 'N');
