function fn_afterExtVendor(svcId, errCode, errMsg) {
	var foundRow = ds_extvd_master.FindRow('lifnr', G_ORG_VEND_CODE);

	if(foundRow >= 0) {
		cb_outgbn.Value = ds_extvd_master.GetColumn(foundRow, 'cvabr');
		cb_outgbn.Enable = false;
	} else {
		ds_extvd_master.InsertRow(0);
		ds_extvd_master.SetColumn(0, 'outgbn', '');
		ds_extvd_master.SetColumn(0, 'cvabr', '');
		ds_extvd_master.SetColumn(0, 'cvabrt', gfn_getCodeNameHdel(G_CODE_SELECT));
		cb_outgbn.Value = '';
	}
}
// 기종 조회 CALL BACK
function fn_afterQueryDD07T_ZRQSTAT(errCode, errMsg) {
	gfn_InsertRowCombo(ds_zrqstat_cd, 0, "Y", "Y"); // 선택 행 추가
	cb_zrqstat.index = 0;
}

