/******************************************************
// 그리드 클릭 시 접수/반납 & 전송 버튼 상태 처리
******************************************************/
function grd_list_OnCellClick(obj,nRow,nCell,nX,nY,nPivotIndex) {
	var varUserGbn = right(G_USER_GROUP_B, 2 );		// 30 : 담당, 40 : 팀장, 50 : 관리자
	var varStat = ds_list.GetColumn(nRow, "ZRQSTAT");

	btn_detail.Enable   = true;

	btn_accept.Enable 	= false;	//접수/반려
	btn_approval.Enable = false;	//완료
	switch(ds_list.GetColumn(nRow, "CISTAT")) {
		case 'SG':
		case 'SR':
			btn_accept.Enable = true;	//접수/반려
			break;
		case 'TA':
			btn_accept.Enable = true;	//접수/반려
			//btn_approval.Enable = true;	//완료
			break;
		case 'TS':
			btn_approval.Enable = true;	//완료
			break;
		case 'TC':
			btn_approval.Enable = true;	//완료
			break;
		case 'TO':
			btn_approval.Enable = true;	//완료
			break;
		case 'EB':
			btn_approval.Enable = true;	//완료
			break;
		case 'EF':
			//btn_approval.Enable = true;	//완료
			break;
		default:
	}

	if (varStat == "R" || varStat = "Q")		{	// 요청이 경우 팀장, 관리자만 접수/반려 가능
		if (varUserGbn == "30" || varUserGbn == "40" || varUserGbn == "50")	{
			//btn_accept.Enable = true;
		} else {
			btn_accept.Enable = false;
		}
		//btn_approval.Enable  = false;
	} else if (varStat != "A") {	// 접수상태에서만 전송 가능
		btn_approval.Enable  = false;
		//btn_accept.Enable = false;
	} else {		// 상세 조회만 가능
		//btn_accept.Enable   = false;
		//btn_approval.Enable = false;
	}	
}

/******************************************************
// 그리드 row 변경 시
******************************************************/
function grd_list_OnCellPosChanged(obj,nRow,nCell,nOldRow,nOldCell,nPivotIndex,nOldPivotIndex) {
	if (nOldRow != nRow)	{
		grd_list_OnCellClick(obj, nRow, nCell, "", "", nPivotIndex);
	}
}

/******************************************************
// 그리드 출력버튼 클릭
******************************************************/
function grd_list_OnExpandEdit(obj,nRow,nCell,strVal,nPivotIndex) {
	//if (nCell == 44)	{
	if (nCell == 58)	{

		var arg  = "P_ZRQSEQ=" +quote(ds_list.GetColumn(nRow, "ZRQSEQ"));

		// 출력용 조회 필요
		if (length(trim(ds_list.GetColumn(nRow, "HOGI")))	> 0)	{	// 프로젝트 정보
			arg += " P_GUBUN=P";
		}else	{		// 견적정보로 처리
			arg += " P_GUBUN=Q";
		}

		fn_createWait("",this);     // 처리중입니다. 메시지 처리

		ds_error.ClearData();
		nsfRequest(  "print"
				   , "ses0401/findPrint"
				   , "ds_print=ds_print ds_template=ds_template"
				   , "ds_print=ds_print ds_template=ds_template ds_error=ds_error"
				   , arg
				   , "fn_afterPrint"
				  );
	}
}

/********************************************************************************
* 기      능   : 출력 Callback  
********************************************************************************/
function fn_afterPrint(svcId, errCode, errMsg) {

	if(ds_error.rowcount() > 0) 	{
		fn_destroyWait();

		gfn_showAlert("CE00001");
		return;
	}

	var vRow = 0;
	var vSearchRow = 0;

	if (svcId == "print") {
		// 사양정보 Copy처리 (제공 Template에 전체가 존재함)

		// print
		// 도면승인여부
