// 상세조회 버튼 클릭
function btn_detail_OnClick(obj) {
	var url = "Ses::Ses0411.xml";
	var arg =  " fa_zrqseq = "  + quote(ds_list.GetColumn(ds_list.currow, "ZRQSEQ"));
	    arg += " fa_flag = "    + quote("Q");	// 상세 조회
	    arg += " fa_gsnam = "	+ quote(ds_list.GetColumn(ds_list.currow, "GSNAM"));
	    arg += " fa_cistat = "	+ quote(ds_list.GetColumn(ds_list.currow, "CISTAT"));
	    arg += " fa_stdcad = "	+ quote(ds_list.GetColumn(ds_list.currow, "STDCAD"));
		arg += " fa_qtso_no = "	+ quote(ds_list.GetColumn(ds_list.currow, "QTSO_NO"));
	gfn_openPopUpUrlHdel(url, true, arg);
}

