function fn_excel() {
	gfn_exportExcel(grd_list, "도면의뢰현황");
}

function ufn_popupQutation(obj) {	
//	if ( left(ed_qtso_no.Text, 1) == "A" || left(ed_qtso_no.Text, 1) == "N" ) {	
		if (length(ed_qtso_no.Text) < 2) 
			ed_qtso_no.Text = '';

		var arg = "f_qtnum = "  + quote(ed_qtso_no.Text) 		// 견적번호
				+ " f_gsnam = " + quote(ed_qtso_nm.Text); 	//현장명
		var arr = gfn_openPopUpUrlHdel("Com::Com0250.xml", true, arg);			

		if (arr != null && arr != 0) {
			ed_qtso_no.Text = arr[0];
			ed_qtso_nm.Text = arr[1];
		}		
}

function ufn_popupProject(obj) {
	var arg = "fa_zzpjt_cd   = "+quote(ed_pjt.Text)		//프로젝트번호
			+ " fa_zzpjt_nm  = "+quote(ed_pjt_nm.Text); 	//프로젝트명
	var arr = gfn_openPopUpUrlHdel("Com::Com0180.xml", true, arg);			
	if (arr != null && arr != 0) {
		ed_pjt.Text = arr[0];
		ed_pjt_nm.Text = arr[1];
	}						
}

function ed_qtso_no_OnCharChanged(obj,strPreText,strPostText) {
	if (strPreText != strPostText)
		ed_qtso_nm.Text = "";	
}

/********************************************************************************
* 기      능   :  견적/프로젝트 정보 처리
********************************************************************************/ 
function ed_qtso_no_OnChanged(obj,strText) {
	// 입력된 조건이 없으면
	if (length(trim(ed_qtso_no.text)) == 0 && length(trim(ed_qtso_nm.text)) == 0) {
	} else {
		fn_query_qt();  				// 견적조회 서비스 호출
	}
}

/********************************************************************************
* 기      능   :  견적/프로젝트 정보 처리
********************************************************************************/ 
function ed_pjt_OnChanged(obj,strText) {
	// 입력된 조건이 없으면
	if (length(trim(ed_pjt.text)) == 0 && length(trim(ed_pjt_nm.text)) == 0) {
	} else {
		fn_query_so();  				// 프로젝트조회 서비스 호출
	}
}

/********************************************************************************
* 기      능   :  견적조회   
********************************************************************************/
function fn_query_qt() { 

	gfn_clearMsgBar();   

	// 출력데이타셋 초기화
	ds_list_qt.ClearData();
	ds_cond_qt.ClearData();
	ds_cond_qt.AddRow();

	ds_cond_qt.SetColumn(0, "QTNUM", trim(ed_qtso_no.text));
	ds_cond_qt.SetColumn(0, "QTSER", "1");
	ds_cond_qt.SetColumn(0, "GSNAM", trim(ed_qtso_nm.text));

	// 서비스호출
	nsfRequest("query"									// srvId
				, "com0250/find"						// url
				, "ds_cond=ds_cond_qt ds_list=ds_list_qt"		// inDsList
				, "ds_list_qt=ds_list ds_error=ds_error_qtso"	// outDsList
				, ""									// strParam
				, "fn_afterQuery_qt");				// CallBackFunc
}

/********************************************************************************
* 기      능   :  견적 조회 Callback  
********************************************************************************/ 
function fn_afterQuery_qt(errCode, errMsg) {

	if(ds_error_qtso.rowcount() > 0) { 	// 호출결과처리
	} else {
		if (ds_list_qt.RowCount() = 1) { // 한 건 조회된 경우
			// 조회해 온 값으로 견적/프로젝트 검색정보 SET
			ed_qtso_cd.Text = ds_list_qt.GetColumn(ds_list_qt.row, "QTNUM");
			ed_qtso_nm.Text = ds_list_qt.GetColumn(ds_list_qt.row, "GSNAM");
		} 
		else if (ds_list_qt.RowCount() > 1) { // 다 건 조회된 경우	
			fn_OnClick_popup(btn_popqtso); // 견적/프로젝트조회 팝업화면  호출
		}
	}
}

/********************************************************************************
* 기      능   :  프로젝트 조회 
********************************************************************************/
function fn_query_so() { 

	gfn_clearMsgBar();   

	// 출력데이타셋 초기화
	ds_list_so.ClearData();
	ds_cond_so.ClearData();
	ds_cond_so.AddRow();

	ds_cond_so.SetColumn(0, "ZZPJT_ID", trim(ed_pjt.text));
	ds_cond_so.SetColumn(0, "BSTNK", trim(ed_pjt_nm.text));

	// 서비스호출
	nsfRequest("query"									// srvId
				, "com0180/find"						// url
				, "ds_cond_zzpjt=ds_cond_so ds_list_zzpjt=ds_list_so"	// inDsList
				, "ds_list_so=ds_list_zzpjt ds_error=ds_error_qtso"		// outDsList
				, ""									// strParam
				, "fn_afterQuery_so");					// CallBackFunc
}


/********************************************************************************
* 기      능   :  프로젝트조회 Callback  
********************************************************************************/ 
function fn_afterQuery_so(errCode, errMsg) {

	if(ds_error_qtso.rowcount() > 0) { 	// 호출결과처리
	} else {
		if (ds_list_so.RowCount() = 1) { // 한 건 조회된 경우
			// 조회해 온 값으로 견적/프로젝트 검색정보 SET
			ed_qtso_cd.Text = ds_list_so.GetColumn(ds_list_so.row, "ZZPJT_ID");
			ed_qtso_nm.Text = ds_list_so.GetColumn(ds_list_so.row, "BSTNK");
		} 
		else if (ds_list_so.RowCount() > 1) { // 다 건 조회된 경우	
			fn_OnClick_popup(btn_popqtso); // 견적/프로젝트조회 팝업화면  호출
		}
	}
}
