var F_FRM_TITLE  = "기술검토요청 접수/반려";	// 공통코드 화면명
var imgUploadFlg = ""; 					// 권한별 이미지 업로드 여부 "":업로드, "VIEW":보기
var F_USER_GBN   = "";
var F_BUTTON_DIS = "";
var F_SERVER_DIR = "SD";
var F_SMS_TITLE  = "기술검토 요청";
var qtso_no = "";   //2020.08.27 jss
var cbOutgbnV = ""; //2020.08.27 jss
var cbOutgbnE = ""; //2020.08.27 jss
var cbOutgbnA = ""; //2020.08.27 jss

//=========================================================================================
// [ PART 2 ]
// Form Loading / Unloading 시 작업 정의
//=========================================================================================
function fn_OnLoadCompleted(obj)
{
	gfn_initForm(false);	// Form 로드 이벤트 맨 첫줄에 기술

	F_USER_GBN = right(G_USER_GROUP_B, 2 );		// 30 : 담당, 40 : 팀장, 50 : 관리자

	global.destroy("gds_zsdt1079");
	global.create("dataset","gds_zsdt1079");
	gds_zsdt1079.AddColumn("ZRQSEQ");
	gds_zsdt1079.AddColumn("CHGCHRPT");

	fn_init_form();			// 폼 초기화

	div_btn.btn_sendback.DomainId = "";
	div_btn.btn_sendback.Text     = "반려";
	div_btn.btn_approval.DomainId = "";
	div_btn.btn_approval.Text     = "완료";
}

/********************************************************************************
* 기      능   : form UnloadCompleted 
********************************************************************************/
function fn_OnUnloadCompleted(obj)
{
	return gfn_isFormUnload(obj);
}

/********************************************************************************
* 기      능   : 폼 초기화
********************************************************************************/
function fn_init_form()
{
	F_BUTTON_DIS = "";	// 닫기

	var zrqseq = "";
	if ( IsExistVar("fa_zrqseq" ) )
		zrqseq = trim(fa_zrqseq );

	var flag = "";
	if ( IsExistVar("fa_flag") )
		flag = trim(fa_flag);

	ed_sman_cd.Enable  = true;
	btn_popSman.Enable = true;
	ed_zrsrlt.Enable   = false;

	// 파일첨부
	btn_add.Visible   = false;
	btn_del.Visible   = false;
	btn_add_2.Visible = false;
	btn_del_2.Visible = false;

	Static1.Text = "견적차수";

	if (flag == "R")	{
		F_FRM_TITLE  = "기술검토요청 접수/반려";
		if (F_USER_GBN == "30" || F_USER_GBN == "40" || F_USER_GBN == "50")	{	// 팀장 및 관리자의 경우 접수/ 반려 가능
			F_BUTTON_DIS = "5,65";	// 접수, 반려
			ed_sman_cd.Enable  = true;
			btn_popSman.Enable = true;
			ed_zrsrlt.Enable   = true;
			btn_savez3.Visible = true; //기영담당자 일괄지정
		}
	}else if (flag == "A")	{
		F_FRM_TITLE  = "기술검토요청 완료";
		F_BUTTON_DIS = "10";	// 완료
		ed_zrsrlt.Enable  = true;
		cal_conf.Enable  = true;
		cal_confdc.Enable = true;
		btn_add.Visible   = true;
		btn_del.Visible   = true;
	}else	{
		F_FRM_TITLE  = "기술검토요청 상세조회";
		cal_conf.Enable  = true;
		cal_confdc.Enable = true;
		if (F_USER_GBN == "30" || F_USER_GBN == "40" || F_USER_GBN == "50")	{	// 팀장 및 관리자의 경우 변경 가능 
			F_BUTTON_DIS = "30";	// 완료
		}
	}

	div_btn.ds_currBtnList.ClearData();
	div_btn.fn_loadUseBtn(F_BUTTON_DIS, F_FRM_TITLE, "", "");

	// 요청일련번호 가 있으면 조회/수정
	if ( length(zrqseq) > 0 ) {
		ds_cond.ClearData();
		ds_cond.AddRow();
		ds_cond.SetColumn(0, "MANDT" , G_MANDT  );
		ds_cond.SetColumn(0, "ZRQSEQ", zrqseq);

		fn_query();
	}

	// 요청일련번호가 없으면 등록
	else {
		gfn_showAlert("파라미터 오류 입니다.");
		this.close();
	}

	tx_description.Text	= "A : 기종/인승/용도 오류\n"
						+ "B : 승강로 및 기계실 오류\n"
						+ "C : 승강로 전장오류\n"
						+ "D : 출입구 오류\n"
						+ "E : 기타오류";

	gfn_queryDD07T('ZSDD_CAD_OUTGBN', ds_outgbn_cd, 'N');
	gfn_queryDD07T('ZSDD_CAD_REQTYPE', ds_cadreqtype, 'N');


}

