/********************************************************************************
* 기      능   : 조회
********************************************************************************/
function fn_query()
{
	fn_createWait("",this);     // 처리중입니다. 메시지 처리

	//var qtso_no = "";  //2020.08.27 jss 전연변수로 변경
	if ( IsExistVar("fa_qtso_no" ) )
		qtso_no = trim(fa_qtso_no );

	var inDs =  "ds_cond=ds_cond"
			 + " ds_header=ds_header"
			 + " ds_file=ds_file"
			 + " ds_file_2=ds_file_2"
			 + " ds_zsdt1079=gds_zsdt1079"
			 + " ds_1084=ds_1084"
	;
	var outDs = "ds_header=ds_header"
			 + " ds_file=ds_file"
			 + " ds_file_2=ds_file_2"
			 + " gds_zsdt1079=ds_zsdt1079"
			 + " ds_1084=ds_1084"
			 + " ds_error=ds_error"
	;

	nsfRequest("search", "ses0401/find", inDs, outDs, "", "fn_afterQuery");
}

/********************************************************************************
* 기      능   : 저장 (기술영업 담당자 변경)
********************************************************************************/
function fn_save()
{

	if (length(trim(ed_sman_cd.Text)) == 0)		{
		gfn_showAlert("CW00001", "담당자");
		ed_sman_cd.SetFocus();
		return;
	}

	if (length(trim(ed_sman_cd.Text)) > 0 &&  length(trim(ed_sman_nm.Text)) == 0)		{
		gfn_showAlert("CW00038", "담당자");
		ed_sman_nm.SetFocus();
		return;
	}

	if ( !gfn_showConfirm("CI00024", "[저장]") ) { // [저장]을 하시겠습니까?
		return;
	}

	fn_createWait("",this);     // 처리중입니다. 메시지 처리

	/* 변경사항 -> 오류항목
	gds_zsdt1079.ClearData();
	if(ds_zsdt1079.GetColumn(0,"CHGCHRPTA") == 1) {
		gds_zsdt1079.AppendRow();
		gds_zsdt1079.SetColumn(gds_zsdt1079.currow,"CHGCHRPT", "A");
	}
	if(ds_zsdt1079.GetColumn(0,"CHGCHRPTB") == 1) {
		gds_zsdt1079.AppendRow();
		gds_zsdt1079.SetColumn(gds_zsdt1079.currow,"CHGCHRPT", "B");
	}
	if(ds_zsdt1079.GetColumn(0,"CHGCHRPTC") == 1) {
		gds_zsdt1079.AppendRow();
		gds_zsdt1079.SetColumn(gds_zsdt1079.currow,"CHGCHRPT", "C");
	}
	if(ds_zsdt1079.GetColumn(0,"CHGCHRPTD") == 1) {
		gds_zsdt1079.AppendRow();
		gds_zsdt1079.SetColumn(gds_zsdt1079.currow,"CHGCHRPT", "D");
	}
	if(ds_zsdt1079.GetColumn(0,"CHGCHRPTE") == 1) {
		gds_zsdt1079.AppendRow();
		gds_zsdt1079.SetColumn(gds_zsdt1079.currow,"CHGCHRPT", "E");
	}
	*/
	var inDs	= "ds_header=ds_header"
				+ " ds_zsdt1079=gds_zsdt1079";

	var outDs = "ds_header=ds_header"
			 + " ds_error=ds_error";

	var arg = "";
	/** 2021.02 jss 주석처리 :이현철 책임요청
	if (LEFT(ed_sman_cd.text, 1) == 'A') {
		arg = "cistat = " + quote("EA");
	}
	*/

	nsfRequest("save", "ses0401/saveZrqstat", inDs, outDs, arg, "fn_afterSave");
}

/********************************************************************************
* 기      능   : 접수 처리
********************************************************************************/
function fn_receipt()
{
	if (length(trim(ed_zrqseq.Text)) == 0)		{
		gfn_showAlert("CW00001", "요청일련번호");
		ed_zrqseq.SetFocus();
		return;
	}

	ed_zrsrlt.Text = "";

	if (length(trim(ed_sman_cd.Text)) == 0)		{
		gfn_showAlert("CW00001", "담당자");
		ed_sman_cd.SetFocus();
		return;
	}

	if (length(trim(ed_sman_cd.Text)) > 0 &&  length(trim(ed_sman_nm.Text)) == 0)		{
		gfn_showAlert("CW00038", "담당자");
		ed_sman_nm.SetFocus();
		return;
	}
	if(cb_outgbn.Enable == true && cb_outgbn.Index == -1) {
		alert('도면처리 방법을 선택하세요.');
		cb_outgbn.SetFocus();
		return;
	}

	if ( !gfn_showConfirm("CI00024", "[접수]") ) { // [저장]을 하시겠습니까?
		return;
	}

	fn_createWait("",this);     // 처리중입니다. 메시지 처리

	ds_header.SetColumn(0, "ZRQSTAT", "A");	// 접수

	var inDs =  "ds_header=ds_header";

	var outDs = "ds_header=ds_header"
			 + " ds_error=ds_error";
	var arg = "cistat = " + quote("TA");

	nsfRequest("saveReceipt", "ses0401/saveZrqstat", inDs, outDs, arg, "fn_afterSave");

	/** 2021.02 jss 주석처리 :이현철 책임요청
	if (LEFT(ed_sman_cd.text, 1) == 'A') {
		btn_drawing_OnClick(btn_outaccept);
	}
	*/
}

/********************************************************************************
* 기      능   :  반송처리
********************************************************************************/
function fn_sendback()
{
	if (length(trim(ed_zrqseq.Text)) == 0)		{
		gfn_showAlert("CW00001", "요청일련번호");
		ed_zrqseq.SetFocus();
		return;
	}

	if (length(trim(ed_zrsrlt.Text)) == 0)		{
		gfn_showAlert("CW00001", "처리결과");
		ed_zrsrlt.SetFocus();
		return;
	}

	if (length(trim(ed_sman_cd.Text)) == 0)		{
		gfn_showAlert("CW00001", "담당자");
		ed_sman_cd.SetFocus();
		return;
	}

	if (length(trim(ed_sman_cd.Text)) > 0 &&  length(trim(ed_sman_nm.Text)) == 0)		{
		gfn_showAlert("CW00038", "담당자");
		ed_sman_nm.SetFocus();
		return;
	}

	if ( !gfn_showConfirm("CI00024", "[반려]") ) { // [저장]을 하시겠습니까?
		return;
	}

	fn_createWait("",this);     // 처리중입니다. 메시지 처리

	ds_header.SetColumn(0, "ZRQSTAT", "D");	// 반려

	var inDs =  "ds_header=ds_header";

	var outDs = "ds_header=ds_header"
			 + " ds_error=ds_error";
	var arg = "cistat = " + quote("TB");
	nsfRequest("saveSendback", "ses0401/saveZrqstat", inDs, outDs, arg, "fn_afterSave");
}

/********************************************************************************
* 기      능   :  완료처리
********************************************************************************/
function fn_approval()
{
	var nCnt = 0;

	if (length(trim(ed_zrqseq.Text)) == 0)		{
		gfn_showAlert("CW00001", "요청일련번호");
		ed_zrqseq.SetFocus();
		return;
	}

	if (length(trim(ed_zrsrlt.Text)) == 0)		{
		gfn_showAlert("CW00001", "처리결과");
		ed_zrsrlt.SetFocus();
		return;
	}

	// 첨부파일 정리
	ds_del_file.ClearData();
	for ( var i = 0 ; i < ds_file.GetRowCount() ; i++ ) {
		// 일련번호를 재부여한다.
		ds_file.SetColumn(i, "ZRQSEQ", ds_header.GetColumn(0, "ZRQSEQ"));

		// DB처리 완료후, 실제 삭제할 파일을 별도 데이터셋에 저장해둔다.
		if ( ds_file.GetColumn(i, "FLAG") == "D" ) {
			ds_del_file.AddRow();
			ds_del_file.CopyRow(ds_del_file.rowcount-1, ds_file, i);
		}

		// 추가된 첨부 중 승인 파일 존재여부 확인
		if (ds_file.GetColumn(i, "FLAG") == "I")	{
			if (ds_file.GetColumn(i, "ZATGBN") == "F")	{	// 전송 파일 확인
				nCnt++;
			}
		}
	}

	if ( !gfn_showConfirm("CI00024", "[완료]") ) { // [저장] 작업을 진행 하시겠습니까?
		return;
	}

    ds_header.SetColumn(0,"CONFDOCU",gfn_getCurrDate());

	fn_createWait("",this);     // 처리중입니다. 메시지 처리

	ds_header.SetColumn(0, "ZRQSTAT", "S");	// 완료

	var inDs =  "ds_header=ds_header"
			 + " ds_file=ds_file";

	var outDs = "ds_header=ds_header"
			 + " ds_file=ds_file"
			 + " ds_error=ds_error";

	nsfRequest("saveApproval", "ses0401/saveApproval", inDs, outDs, "", "fn_afterSave");
}

/********************************************************************************
* 기      능   : 닫기  
********************************************************************************/
function fn_close()
{
	this.close();
}

//=========================================================================================
// [ PART 4]
// Callback 함수 정의 : callback 함수명은 메인 함수의 fn_xxxx() 에서 xxxx 부분은 fn_afterXXXX()
// 형태가 되도록 작성 
//=========================================================================================
function fn_afterExtVendor(svcId, errCode, errMsg) {

	var outgbn;
	var foundRow;

	if(ds_extvd_assigned.GetRowCount() == 0) return;

	switch(ds_header.GetColumn(0,'CISTAT')) {
		case 'SR':
		case 'SG':
			cb_outgbn.Enable = true;
			break;

		case 'TA':
			cb_outgbn.Enable = true;
			btn_cadjob.Enable = true;

			//도면외주 파일첨부(도면처리, 도면반려 시)
			if(ds_header.GetColumn(0,'OUTGBN') == "I" || ds_header.GetColumn(0,"OUTGBN") == "K"){
				btn_add_2.Visible   = true;
				btn_del_2.Visible   = true;
				grd_file_2.SetCellProp("body", 5, "Expandshow", "true");	
			}

			break;

		case 'TC':
			cb_outgbn.Enable = true;
			btn_cadjob.Enable = true;

			//도면외주 파일첨부(도면처리, 도면반려 시)
			if(ds_header.GetColumn(0,'OUTGBN') == "I" || ds_header.GetColumn(0,"OUTGBN") == "K"){
				btn_add_2.Visible   = true;
				btn_del_2.Visible   = true;
				grd_file_2.SetCellProp("body", 5, "Expandshow", "true");	
			}

			break;

		case 'EB':
			cb_outgbn.Enable = true;
			btn_cadjob.Enable = true;

			//도면외주 파일첨부(도면처리, 도면반려 시)
			if(ds_header.GetColumn(0,'OUTGBN') == "I" || ds_header.GetColumn(0,"OUTGBN") == "K"){
				btn_add_2.Visible   = true;
				btn_del_2.Visible   = true;
				grd_file_2.SetCellProp("body", 5, "Expandshow", "true");	
			}

			break;

		case 'TR':
			btn_outcanc.Enable = true;
			btn_outreturn.Enable = true;	//외주반려
			btn_outaccept.Enable = true;	//외주접수

			ed_grd.Enable	= true;			//도면등급
			ed_loq.Enable	= true;			//매수
			ed_outman.Enable = true;		//외주접수담당

			btn_add.Visible   = true;
			btn_del.Visible   = true;
			break;

		case 'TS':
			btn_outcanc.Enable = true;

			ed_grd.Enable	= true;			//도면등급
			ed_loq.Enable	= true;			//매수
			ed_outman.Enable = true;		//외주접수담당
			break;

		case 'EA':
			btn_outcanc.Enable = true;
			btn_outfnl.Enable = true;		//외주완료

			ed_grd.Enable	= true;			//도면등급
			ed_loq.Enable	= true;			//매수
			ed_outman.Enable = true;		//외주접수담당
			break;

		case 'EF':
			btn_confirmDrawing.Enable = true;
			btn_rejectDrawing.Enable = true;

			btn_add.Visible   = true;
			btn_del.Visible   = true;

			cb_chgchrpt_a.Enable = true;	//오류항목
			cb_chgchrpt_b.Enable = true;
			cb_chgchrpt_c.Enable = true;
			cb_chgchrpt_d.Enable = true;
			cb_chgchrpt_e.Enable = true;
			ed_error_text.Enable = true;	//오류내용

			//도면외주 파일첨부(도면처리, 도면반려 시)
			if(ds_header.GetColumn(0,'OUTGBN') == "I" || ds_header.GetColumn(0,"OUTGBN") == "K"){
				btn_add_2.Visible   = true;
				btn_del_2.Visible   = true;
				grd_file_2.SetCellProp("body", 5, "Expandshow", "true");	
			}			

			break;

		case 'TK':
			btn_outfnl.Enable = true;
			break;

		default:
			btn_cadjob.Enable = false;
	}

	if(ds_header.GetColumn(0,'OUTGBN') == '') {
		deleteAllExternalVendor(ds_outgbn_cd, 'CODE');

		for(var i=0; i<ds_extvd_assigned.Rowcount(); i++) {
			ds_outgbn_cd.AddRow();
			ds_outgbn_cd.SetColumn(ds_outgbn_cd.GetRowCount()-1,'CODE', ds_extvd_assigned.GetColumn(i,'cvabr'));
			ds_outgbn_cd.SetColumn(ds_outgbn_cd.GetRowCount()-1,'CODE_NAME', ds_extvd_assigned.GetColumn(i,'cvabrt'));
		}
	}

	//도면처리 하기전에는 업체를 저장하더라도 저장된 data는 무시하고 지정된 업체로 조회
	//협력업체 직원으로 이미 지정된 경우에는 해당 로직 제외 - 2020.12.23
	if (trim(ed_sman_cd.text).substr(0, 1) == 'A') {
		cb_outgbn.Enable = false;
		cb_cadreqtype.Enable = false;
	} else {
		outgbn = ds_header.GetColumn(0,'OUTGBN');
		foundRow = ds_extvd_assigned.FindRow('cvabr', outgbn);
		switch(ds_header.GetColumn(0,'CISTAT')) {
			case 'SR':
			case 'SB':
			case 'SG':
			case 'TA':
				deleteAllExternalVendor(ds_outgbn_cd, 'CODE');

				if(foundRow >= 0) {
					foundRow = ds_extvd_master.FindRow('cvabr', outgbn);
					ds_outgbn_cd.AddRow();
					ds_outgbn_cd.SetColumn(ds_outgbn_cd.GetRowCount()-1,'CODE', outgbn);
					ds_outgbn_cd.SetColumn(ds_outgbn_cd.GetRowCount()-1,'CODE_NAME', ds_extvd_master.GetColumn(foundRow, 'cvabrt'));
					ds_header.SetColumn(0,'OUTGBN',outgbn);
				} else {
					for(var i=0; i<ds_extvd_assigned.Rowcount(); i++) {
						ds_outgbn_cd.AddRow();
						ds_outgbn_cd.SetColumn(ds_outgbn_cd.GetRowCount()-1,'CODE', ds_extvd_assigned.GetColumn(i,'cvabr'));
						ds_outgbn_cd.SetColumn(ds_outgbn_cd.GetRowCount()-1,'CODE_NAME', ds_extvd_assigned.GetColumn(i,'cvabrt'));
					}
					ds_header.SetColumn(0,'OUTGBN','');
				}
				break;

			default:
		}
	}

	//자동도면인 경우 업체지정 불필요
	if (IsExistVar("fa_stdcad")) {
		if(trim(fa_stdcad) == 'Y' && cb_outgbn.Enable == true) {
			cb_outgbn.Value = 'A';
			deleteAllExternalVendor(ds_outgbn_cd, 'CODE');
		}
	}

	if(foundRow >= 0 || cb_outgbn.Value == 'S') {
		cb_cadreqtype.Enable = true;
	}

	//2020.08.27 jss
    if(substr(qtso_no, 0, 1) == "N"){	
		//cb_cadreqtype.Enable = true;
	    //cbOutgbnV =  cb_outgbn.Value; 11.09 주석처리 jss
   	    //cbOutgbnE  = cb_outgbn.Enable;
   	    if(ds_header.GetColumn(0, "CADREQTYPE") == "D" || ds_header.GetColumn(0, "CADREQTYPE") == "E"){
	     // cb_outgbn.Enable = false;
		  btn_cadjob.Text = "외주처리";
		  btn_outcanc.Text = "외주처리취소";
		  btn_rejectDrawing.Text = "외주반려";
		  btn_confirmDrawing.Text = "외주완료";
   	    }
    }

}
function deleteAllExternalVendor(dsTarget, colnm) {
	for (var i=0; i < ds_extvd_master.Rowcount(); i++) {
		dsTarget.DeleteRow(dsTarget.FindRow(colnm, ds_extvd_master.getColumn(i, 'cvabr')));
	}
}
/********************************************************************************
* 기      능   : 저장 Callback
********************************************************************************/
function fn_afterQuery(svcId, errCode, errMsg) {
	fn_destroyWait();

	if ( ds_error.GetRowCount() > 0 ) {
		gfn_showAlert("CE00001");
		return;
	}

	if (length(trim(ds_header.GetColumn(0, "HOGI"))) == 0)	{	// 견적의 경우
		Static1.Text = "견적차수";
	}else	{
		Static1.Text = "호기";
	}

	ds_zsdt1079.ClearData();
	ds_zsdt1079.AppendRow();
	for (var i=0; i < gds_zsdt1079.Rowcount(); i++) {
		switch(gds_zsdt1079.GetColumn(i,"CHGCHRPT")) {
		case "A":
			ds_zsdt1079.SetColumn(0,"CHGCHRPTA", 1);
			break;
		case "B":
			ds_zsdt1079.SetColumn(0,"CHGCHRPTB", 1);
			break;
		case "C":
			ds_zsdt1079.SetColumn(0,"CHGCHRPTC", 1);
			break;
		case "D":
			ds_zsdt1079.SetColumn(0,"CHGCHRPTD", 1);
			break;
		case "E":
			ds_zsdt1079.SetColumn(0,"CHGCHRPTE", 1);
			break;
		}
	}

	var flag = "";
	if ( IsExistVar("fa_flag") )
		flag = trim(fa_flag);

	if (flag == "Q")	{	// 조회
		grd_file.SetCellProp("body", 5, "Expandshow", "false");
	}

	nsfRequest(	"search",
				"ses0403/findExternalVendor",
				"ds_header=ds_header ds_extvd_master=ds_extvd_master ds_extvd_assigned=ds_extvd_assigned",
				"ds_extvd_master=ds_extvd_master ds_extvd_assigned=ds_extvd_assigned ds_error=ds_error",
				"",
				"fn_afterExtVendor");
}

/********************************************************************************
* 기      능   : 저장 Callback
********************************************************************************/
function fn_afterSave(svcId, errCode, errMsg)
{
    var gsnam = ds_header.GetColumn(0,"GSNAM");
	if ( ds_error.GetRowCount() > 0 ) {
		fn_destroyWait();
		gfn_showAlert("CE00001");
		return;
	} 

	parent.fn_query();

	ds_sms.ClearData();
	var addRow = ds_sms.AddRow();

	var vMsg;
	vMsg = "기술검토요청이 ";

	//var vSendTel = parent.ds_list.GetColumn(parent.ds_list.currow, "ZKUNNR_TEL");		// 영업담당자
	var vSendTel = ds_header.GetColumn(0, "ZKUNNR_TEL");		// 영업담당자
	var vBackTel = "";	// 처리자 전화번호
	var vBackTel2 = "";
	var vMan = parent.ds_list.GetColumn(parent.ds_list.currow, "KUNNR_Z3_NM");

	vBackTel2 = parent.ds_list.GetColumn(parent.ds_list.currow, "KUNNR_Z3_TEL");		// 기술영업담당
	if ( svcId == "saveReceipt" ) {
		vBackTel = ds_header.GetColumn(0, "UUSER_TEL");
		vMsg += "접수 ";
	}else if ( svcId == "saveSendback" ) {
		vBackTel = ds_header.GetColumn(0, "UUSER_TEL");
		vMsg += "반려 ";
	}else if ( svcId == "saveApproval" ) {
		//vBackTel2 = parent.ds_list.GetColumn(parent.ds_list.currow, "KUNNR_Z3_TEL");		// 기술영업담당
		vMsg += "완료 ";
	}else if ( svcid == "save"){
		this.close();
	}
	vMsg += "되었습니다.";
	vMsg += "요청일련번호 : " + ed_zrqseq.Text;
	vMsg += "/" + gsnam;      //현장명 추가 
    if ( vMan != '' || vMan != null ) {
      vMsg += "/기술영업담당자(" + parent.ds_list.GetColumn(parent.ds_list.currow, "KUNNR_Z3_NM") + " : " + vBackTel2 + ")";
    }
	//trace(vMsg);
	// 전화번호가 부 정확한 경우 SMS 처리 불가
	var telTemp = vSendTel;
	if (length(trim(replace(telTemp,"-",""))) < 11)	{
		gfn_showAlert("CW10025", "SMS 발송 실패. 영업담당자 전화번호 확인 바랍니다.");
		fn_afterSMS(svcId, errCode, errMsg);
		return;
	}

	if (length(trim(vBackTel)) < 12)	{
		vBackTel = "0236700726";         //  20130521  임시로 제외
	}

	// 전송 정보 Setting
	ds_sms.SetColumn(addRow, "TITLE",   F_SMS_TITLE);	// Title
	ds_sms.SetColumn(addRow, "TEL",     vSendTel);		// 영업사업 전화번호
	ds_sms.SetColumn(addRow, "BACKTEL", vBackTel);		// 회송전화번호
	ds_sms.SetColumn(addRow, "MSG",     vMsg);

	tit_clearActionInfo();
	tit_addSingleActionInfo("cs12:CS1221001A_I2");	//CS1221001A_I1
	tit_callService(
        "PDA_ACT"
        , ""
        , "ds_sms=ds_sms"
        , ""
        , ""
        , "fn_afterSMS");
    //=====================메일====================영업담당자에게 이메일 전송 기능 추가 2015.12.10
	var body;
	var title;
	var zkun_email;
	zkun_email = ds_header.GetColumn(0,"ZKUN_EMAIL");
	title = vMSG;
	body = vMSG ;
	body = body + " 발송일 : " + gfn_getCurrDate();    

		tit_clearActionInfo();	
		tit_callService(
			"COMM_ACT"
			, "sendmail"
			, ""
			, ""
			//, "USER_JUMIN=" + quote(med_jumin1.value+med_jumin2.value)
			//	+ 
			," SEND_USER=ADMIN@HDEL.CO.KR"
				//+ " RECE_USER=SK.KANG@HDEL.CO.KR"
				+ " RECE_USER=" + quote( zkun_email )  
				+ " MESGTITL=" + quote( title ) 
				+ " MESGBODY=" + quote( body ) 
			, ""  //"fn_afterQuery"
			, false
			, true);				
	    //=====================메일끝====================	     
}

function fn_afterSMS(svcId, errCode, errMsg) {
	fn_destroyWait();

	if ( svcId == "saveReceipt" ) {
		gfn_showAlert("CI00006", "접수"); // ${} 처리를 완료했습니다.
		div_btn.btn_receipt.Visible  = false;
		div_btn.btn_sendback.Visible = false;
	}else if ( svcId == "saveSendback" ) {
		gfn_showAlert("CI00006", "반려"); // ${} 처리를 완료했습니다.
		div_btn.btn_receipt.Visible  = false;
		div_btn.btn_sendback.Visible = false;
	}else if ( svcId == "saveApproval" ) {
		// 실제 파일 삭제
		for ( var i = 0 ; i < ds_del_file.GetRowCount() ; i++ ) {
			tit_fileDelete(F_SERVER_DIR, ds_del_file.getColumn(i, "ZATTFN"), "");
		}
		ds_del_file.ClearData();

		gfn_showAlert("CI00006", "완료"); // ${} 처리를 완료했습니다.
		div_btn.btn_approval.Visible  = false;
	}

	this.close();
	//fn_query();
}

