function fn_afterOutsrc(svcId, errCode, errMsg) {
	fn_destroyWait();

	if ( ds_error.GetRowCount() > 0 ) {
		gfn_showAlert("CE00001");
		return;
	}
	gfn_showAlert("CI00006", "외주발송"); // ${} 처리를 완료했습니다.
	//btn_outsrc.Enable  = false;
	cb_outgbn.Enable = false;
	btn_cadjob.Enable = false;
	this.close();
	//fn_query();
}

function ufn_send_email(email_to, content, gsnam) {
	var title = "요청번호 " + ed_zrqseq.text + "에 대한 " + content + "건 ";
	var body = "견적/수주 " + ed_qtso_no.text + " " + gsnam + " 에 대하여 " + content
			+ " 확인 바랍니다. "
			+ " 발송일 : " + gfn_getCurrDate();

	tit_clearActionInfo();	
	tit_callService(
		"COMM_ACT"
		, "sendmail"
		, ""
		, ""
		," SEND_USER=ADMIN@HDEL.CO.KR"
			+ " RECE_USER=" + quote(email_to)
			+ " MESGTITL=" + quote( title ) 
			+ " MESGBODY=" + quote( body ) 
		, ""  //"fn_afterQuery"
		, false
		, true);			
}

function btn_savez3_OnClick(obj) {   //기영 담당자 빈값 확인 팝업 
	if (ed_sman_cd.Text == "") {
		gfn_showAlert("CW10025", "기술영업 담당자 지정 바랍니다.");
		return;
	}
	if ( !gfn_showConfirm("CI00024", "[체크된 요청건에 대하여 담당자 일괄지정]") ) { // [외주발송]을 하시겠습니까?
		return;
	}

	destroy("ds_check");
	create("dataset","ds_check");
	parent.ds_list.Filter("check == '1'");
	ds_check.copyF(parent.ds_list);

	if( ds_check.Rowcount() > 0 ) {		
		//trace("rowcnt"+ds_check.Rowcount());		
		for ( var i = 0; i < ds_check.Rowcount(); i++) {
		      ds_list.AddRow();
			  ds_list.SetColumn(i,"ZRQSEQ", ds_check.GetColumn(i,"ZRQSEQ"));
			  ds_list.SetColumn(i,"ZKUNNR_Z3", ed_sman_cd.Text);
		}		
	}
	parent.ds_list.Unfilter();

	var inDs =  "ds_list=ds_list";

	var outDs = "ds_list=ds_list"
			 + " ds_error=ds_error";

	nsfRequest("", "ses0401/savez3all", inDs, outDs, "", "fn_afterSavez3");
}

function fn_afterSavez3(svcId, errCode, errMsg) {
    if ( ds_error.GetRowCount() > 0 ) {
		fn_destroyWait();
		gfn_showAlert("CE00001");
		return;
	} else {
		gfn_showAlert("CI00006", "기술영업담당자 일괄지정"); 
	}

	parent.fn_query();
}

function checkRequiredAboutOutsourcing( ) {
	if(trim(ed_grd.Text) == "" ||  trim(ed_loq.Text) == "" || ToNumber(trim(ed_loq.Text)) == 0 || trim(ed_outman.Text) == "") {
		gfn_showAlert("CW00001", "도면등급, 매수, 외주접수담당"); 
		return false;
	}
	return true;
}

function btn_drawing_OnClick(obj) {
	var popupMesg = '';
	var serviceID = '';
	var requestID = '';
	var strParam = '';
	var callBackFunc = '';
	var email_addr = '';
	var email_content = '';

	switch(obj) {
	case btn_cadjob:
		if(cb_cadreqtype.Enable == true && cb_cadreqtype.Index == -1) {
			alert('요청도면종류를 선택하세요.');
			return;
		}

		popupMesg = '[도면작성의뢰]';
		serviceID = 'saveOutdate';
		requestID = 'outsourcing';
		callBackFunc = 'fn_afterOutsrc';

		if (length(trim(ed_zrqseq.Text)) == 0) {
			gfn_showAlert('CW00001', '요청일련번호');
			ed_zrqseq.SetFocus();
			return;
		}

		email_addr = get_vendor_email_address(cb_outgbn.Value);
		email_content = '도면작성의뢰';

		switch (cb_outgbn.Value) {
			case 'A':	//자동도면
			case 'N':	//도면불필요
			case 'S':	//자체작도
				strParam = 'outgbn=' + quote(cb_outgbn.Value) + ' cistat = ' + quote('TS');
				break;

			default:	//외주
				var foundRow = ds_extvd_master.FindRow('cvabr', ds_header.GetColumn(0,'OUTGBN'));
				var lifnr = ds_extvd_master.GetColumn(foundRow, 'lifnr');
				strParam = 'outgbn=' + quote(cb_outgbn.Value) + ' cistat = ' + quote('TR') + ' lifnr = ' + quote(lifnr);
				break;
		}

		break;

	case btn_outaccept:
		if( checkRequiredAboutOutsourcing() == false)
			return;

		popupMesg = '[도면접수]';
		serviceID = 'saveOutactdt';
		requestID = 'outaccept';
		callBackFunc = 'fn_afterOutsrc';

		if (length(trim(ed_zrqseq.Text)) == 0) {
			gfn_showAlert('CW00001', '요청일련번호');
			ed_zrqseq.SetFocus();
			return;
		}

		email_addr = ds_header.GetColumn(0,"KUN_EMAIL");
		email_content = '도면작성접수';

		break;

	case btn_outcanc:
		popupMesg = '[도면처리취소]';
		serviceID = 'saveOutcandt';
		requestID = 'outcancel';
		callBackFunc = 'fn_afterOutsrc';

		if (length(trim(ed_zrqseq.Text)) == 0) {
			gfn_showAlert('CW00001', '요청일련번호');
			ed_zrqseq.SetFocus();
			return;
		}

		email_addr = get_vendor_email_address(cb_outgbn.Value);
		email_content = '도면처리취소';

		break;

	case btn_outfnl:
		if( checkRequiredAboutOutsourcing() == false)
			return;

		popupMesg = '[외주완료]';
		serviceID = 'saveOutfinish';
		requestID = 'outfinish';
		callBackFunc = 'fn_afterOutsrc';

		if (length(trim(ed_zrqseq.Text)) == 0) {
			gfn_showAlert('CW00001', '요청일련번호');
			ed_zrqseq.SetFocus();
			return;
		}

		email_addr = ds_header.GetColumn(0,"KUN_EMAIL");
		email_content = '도면작성완료';

		break;

	case btn_outreturn:
		popupMesg = '[외주반려]';
		serviceID = 'saveOutretdt';
		requestID = 'outreturn';
		callBackFunc = 'fn_afterOutsrc';

		if (length(trim(ed_zrqseq.Text)) == 0) {
			gfn_showAlert('CW00001', '요청일련번호');
			ed_zrqseq.SetFocus();
			return;
		}

		email_addr = ds_header.GetColumn(0,"KUN_EMAIL");
		email_content = '도면작성반려';

		break;

	case btn_confirmDrawing:
		popupMesg = '[도면완료]';
		serviceID = 'confirmDrawing';
		requestID = serviceID;
		callBackFunc = 'fn_afterConfirmDrawing';
		break;

	case btn_rejectDrawing:
		popupMesg = '[도면반려]';
		serviceID = 'rejectDrawing';
		requestID = serviceID;
		callBackFunc = 'fn_afterRejectDrawing';

		// 오류항목
		gds_zsdt1079.ClearData();
		if(ds_zsdt1079.GetColumn(0,"CHGCHRPTA") == 1) {
			gds_zsdt1079.AppendRow();
			gds_zsdt1079.SetColumn(gds_zsdt1079.currow,"CHGCHRPT", "A");
		}
		if(ds_zsdt1079.GetColumn(0,"CHGCHRPTB") == 1) {
			gds_zsdt1079.AppendRow();
			gds_zsdt1079.SetColumn(gds_zsdt1079.currow,"CHGCHRPT", "B");
		}
		if(ds_zsdt1079.GetColumn(0,"CHGCHRPTC") == 1) {
			gds_zsdt1079.AppendRow();
			gds_zsdt1079.SetColumn(gds_zsdt1079.currow,"CHGCHRPT", "C");
		}
		if(ds_zsdt1079.GetColumn(0,"CHGCHRPTD") == 1) {
			gds_zsdt1079.AppendRow();
			gds_zsdt1079.SetColumn(gds_zsdt1079.currow,"CHGCHRPT", "D");
		}
		if(ds_zsdt1079.GetColumn(0,"CHGCHRPTE") == 1) {
			gds_zsdt1079.AppendRow();
			gds_zsdt1079.SetColumn(gds_zsdt1079.currow,"CHGCHRPT", "E");
		}

		if (length(trim(ed_zrqseq.Text)) == 0) {
			gfn_showAlert('CW00001', '요청일련번호');
			ed_zrqseq.SetFocus();
			return;
		}

		if (gds_zsdt1079.GetRowCount() == 0) {
			gfn_showAlert('CW00001', '오류항목');
			return;
		}

		if (length(trim(ed_error_text.Text)) == 0) {
			gfn_showAlert('CW00001', '오류내용');
			ed_error_text.SetFocus();
			return;
		}

		email_addr = get_vendor_email_address(cb_outgbn.Value);
		email_content = '도면검토반려';

		// 첨부파일 정리
		ds_del_file_2.ClearData();
		for ( var i = 0 ; i < ds_file_2.GetRowCount() ; i++ ) {
			// 일련번호를 재부여한다.
			ds_file_2.SetColumn(i, "ZRQSEQ", ds_header.GetColumn(0, "ZRQSEQ"));

			// DB처리 완료후, 실제 삭제할 파일을 별도 데이터셋에 저장해둔다.
			if ( ds_file_2.GetColumn(i, "FLAG") == "D" ) {
				ds_del_file_2.AddRow();
				ds_del_file_2.CopyRow(ds_del_file_2.rowcount-1, ds_file_2, i);
			}

			// 추가된 첨부 중 승인 파일 존재여부 확인
			if (ds_file_2.GetColumn(i, "FLAG") == "I")	{
				if (ds_file_2.GetColumn(i, "ZATGBN") == "F")	{	// 전송 파일 확인
					nCnt++;
				}
			}
		}

		break;

	default:
		break;
	}

	if( !gfn_showConfirm('CI00024', popupMesg) ) // [~~]을 하시겠습니까?
		return;

	if(email_addr != '')
		ufn_send_email(email_addr, email_content, trim(fa_gsnam));

	fn_createWait('',this);     // 처리중입니다. 메시지 처리

	var inDs =  'ds_header=ds_header'
				+ ' ds_zsdt1079=gds_zsdt1079'
				+ " ds_file_2=ds_file_2";
	var outDs = 'ds_header=ds_header'
			 + ' gds_zsdt1079=ds_zsdt1079'
			 + ' ds_error=ds_error';

	nsfRequest(serviceID, 'ses0401/'+requestID, inDs, outDs, strParam, callBackFunc);
}

function get_vendor_email_address(gbn) {
	return ds_extvd_master.getColumn(ds_extvd_master.FindRow('cvabr',gbn),'email');
}
function fn_afterConfirmDrawing() {
	this.Close();
}
function fn_afterRejectDrawing() {
	this.Close();
}
function btn_spec_OnClick(obj) {
	var arg;
	if(ds_1084.GetRowCount() == 0) {
		arg = "fa_charno="+quote(ed_qtso_no.Text)
			+ " fa_charseq="+quote(ed_qtser.Text);
	} else {
		arg = " fa_charno="+quote(ed_vbeln.Text)
			+ " fa_charseq="+quote(ed_seq.Text);
	}

	gfn_openPopUpUrlHdel("Sso::Sso0059.xml", true, arg);
}


