//요청도면종류 선택 이벤트 : 2020.08.27 jss
function cadreqtype_Changed(obj,strPreText,strPostText)
{
	if(substr(qtso_no, 0, 1) == "N"){
		if(strPreText == "D" || strPreText == "E"){
		  if(ds_outgbn_cd.FindRow('CODE', 'K') < 0 ){
			//cbOutgbnA = "A";
			//ds_outgbn_cd.AddRow();  11.09 주석처리 jss
			//ds_outgbn_cd.SetColumn(ds_outgbn_cd.currow,'CODE', "K"); 11.09 주석처리 jss
			//ds_outgbn_cd.SetColumn(ds_outgbn_cd.currow,'CODE_NAME', "광장"); 11.09 주석처리 jss
		  }	
		 //cb_outgbn.Value = 'K';    11.09 주석처리 jss
		 //cb_outgbn.Enable = false; 11.09 주석처리 jss
		  btn_cadjob.Text = "외주처리";
		  btn_outcanc.Text = "외주처리취소";
		  btn_rejectDrawing.Text = "외주반려";
		  btn_confirmDrawing.Text = "외주완료";

		}else{
			//cb_outgbn.Value = cbOutgbnV; 11.09 주석처리 jss
			//cb_outgbn.Enable = cbOutgbnE;
			btn_cadjob.Text = "도면처리";
			btn_outcanc.Text = "도면처리취소";
			btn_rejectDrawing.Text = "도면반려";
			btn_confirmDrawing.Text = "도면완료";

			if(cbOutgbnA == "A"){
			  //ds_outgbn_cd.DeleteRow(ds_outgbn_cd.FindRow('CODE', 'K'));  11.09 주석처리 jss
			  //ds_outgbn_cd.DeleteRow(ds_outgbn_cd.FindRow('CODE_NAME', '광장')); 11.09 주석처리 jss
			}
		}
	}	

}
