	var inDs =  'ds_list=ds_reppm';
	var outDs = 'ds_reppm=ds_list'
			 + ' ds_error=ds_error'
	;
	http.sync = true;
	nsfRequest('searchRepPmByTeam', 'ses0412/searchRepPmByTeam', inDs, outDs, 'VKGRP = ' + quote(vkgrp), 'fn_afterSearchRepPmByTeam');
	http.sync = false;
}
function fn_afterSearchRepPmByTeam() {
}
/********************************************************************************
* 기      능   : 저장 Callback
********************************************************************************/
function fn_afterSave(svcId, errCode, errMsg)
{
	if ( ds_error.GetRowCount() > 0 ) {
		gfn_showAlert("CE00001");
		return;
	} 

	if ( svcId == "saveHeader" ) {
		// 실제 파일 삭제
		for ( var i = 0 ; i < ds_del_file.GetRowCount() ; i++ ) {
			tit_fileDelete(F_SERVER_DIR, ds_del_file.getColumn(i, "ZATTFN"), "");
		}
		ds_del_file.ClearData();

		gfn_showAlert("CI00003"); // 저장을 완료했습니다.

		parent.fn_query();

		ds_sms.ClearData();
		var addRow = ds_sms.AddRow();
		var vSendTel ;
		var vBackTel = parent.ds_list.GetColumn(parent.ds_list.currow, "ZKUNNR_TEL");		// 영업담당자
		var vMsg;

		vkgrp = ds_header.GetColumn(0,"VKGRP");
		zbdtyp =  ds_header.GetColumn(0,"ZBDTYP");

		if (zbdtyp != "01"){  //건물용도가 office 일때를 제외화고 설치pm 에게 메시지 전송 

			fn_searchRepPmByTeam(vkgrp);
			vSendTel = ds_reppm.GetColumn(0,'CELLP');
			/*switch(vkgrp){
				case "B1N": vSendTel = "011-270-6807";   // 송인기 강남
				case "B2N": vSendTel = "010-8760-1642";  // 이재규 강북
				case "B4C": vSendTel = "010-4707-2992"; 	// 김광용 영업3부-광주지사
				case "B4E": vSendTel = "011-9476-1827"; 	// 구범서 영업3부-전주지사
				case "B4F": vSendTel = "010-8793-1795"; 	// 임병선 영업3부-강원지사
				case "B4H": vSendTel = "010-8002-3523"; 	// 유진수 영업3부-중부지사
				case "B4I": vSendTel = "010-2081-2088"; 	// 서윤길 영업3부-인천지사
				case "BCA": vSendTel = "010-6280-5903"; 	// 손성진 영업4부-부산
				case "BCB": vSendTel = "010-2585-6770"; 	// 장영창 영업4부-대구
				case "BCD": vSendTel = "010-5436-2912"; 	// 이태병 영업4부-대전
				case "BCG": vSendTel = "010-3531-5478"; 	// 황대석 영업4부-울산
				case "BCJ": vSendTel = "010-6530-5700"; 	// 이쌍문 영업4부-경남
				case "BCK": vSendTel = "010-7315-5529"; 	// 양승보 영업4부-제주
			}*/
		}else{
			vSendTel = "010-6258-5982"; 	// 유태용 차장 
		}

		vMsg = "리모델링 현장check 요청! ";		
		vMsg += "요청번호 : " + ed_zrqseq.Text;
		vMsg += " - 담당자 : "+parent.ds_list.GetColumn(parent.ds_list.currow, "ZKUNNR_NM") +"/" + gsnam;      //현장명 추가 

	// 전화번호가 부 정확한 경우 SMS 처리 불가

		var telTemp = vSendTel;
		if (length(trim(replace(telTemp,"-",""))) < 11)	{
			gfn_showAlert("CW10025", "SMS 발송 실패. 영업담당자 전화번호 확인 바랍니다.");
			fn_afterSMS(svcId, errCode, errMsg);
			return;
		}

		if (length(trim(vBackTel)) < 12)	{
			vBackTel = "02-3670-0729";      
		}

	// 전송 정보 Setting
		ds_sms.SetColumn(addRow, "TITLE",   F_SMS_TITLE);	// Title
		ds_sms.SetColumn(addRow, "TEL",     vSendTel);		// 영업사원 전화번호
		ds_sms.SetColumn(addRow, "BACKTEL", vBackTel);		// 회송전화번호
		ds_sms.SetColumn(addRow, "MSG",     vMsg);

		tit_clearActionInfo();
		tit_addSingleActionInfo("cs12:CS1221001A_I2");	//CS1221001A_I1
		tit_callService(
			"PDA_ACT"
			, ""
			, "ds_sms=ds_sms"
			, ""
			, ""
			, "fn_afterSMS");       

		// 재조회
		ds_cond.ClearData();
		ds_cond.AddRow();
		ds_cond.SetColumn(0, "MANDT" , G_MANDT  );
		ds_cond.SetColumn(0, "ZRQSEQ", ds_header.GetColumn(0, "ZRQSEQ"));

		fn_query();

	} else if ( svcId == "saveDel" ) {
	    gfn_showAlert("CW10025", "삭제되었습니다.");
	    parent.fn_query();
	    close();


	}  
}

function fn_afterSMS(svcId, errCode, errMsg)
{
	this.close();
	//fn_query();
}
