/********************************************************************************
* 기      능   : 요청번호 검색 
********************************************************************************/
function ufn_openPjt(obj)
{	
	if (length(ed_qtso_no.Text) < 2) 
		ed_qtso_no.Text = '';

	var arg = "rqdat_f = "  + quote(gfn_getCurrDate()) 		// 요청일
	        + " rqdat_t = "  + quote(gfn_getCurrDate())  		// 요청일
			+ " sman = " + quote(G_SAP_USER_ID); 	//현장명
	var arr = gfn_openPopUpUrlHdel("Com::Com0260.xml", true, arg);			

	if (arr != null && arr != 0) {
		ed_zrqseq.Text = arr[0];
	}		

}
