/********************************************************************************
* 기      능   : 첨부파일 추가
********************************************************************************/
function btn_add_OnClick(obj)
{
	var nRow = ds_file.AddRow();
	var nSeq = toNumber(ds_file.Max("ZATTSEQ"));
	if ( nSeq == "" || nSeq == null ) nSeq = 0;

	ds_file.setColumn(nRow, "FLAG"   , "I");
	ds_file.setColumn(nRow, "MANDT"  , ds_header.GetColumn(0, "MANDT" ));
	ds_file.setColumn(nRow, "ZRQSEQ" , ds_header.GetColumn(0, "ZRQSEQ"));
	ds_file.SetColumn(nRow, "ZATTSEQ", nSeq+1);
	ds_file.SetColumn(nRow, "ZATGBN", "F"    ); //첨부파일 구분 요청-R, 완료-F
	ds_file.SetColumn(nRow, "FILEGBN", "S"    ); //첨부파일 구분 영업-S, 외주-O
}

/********************************************************************************
* 기      능   : 첨부파일 삭제
********************************************************************************/
function btn_del_OnClick(obj)
{
	var nRow = ds_file.currow;
	if ( nRow > -1 ) {
		if (ds_file.GetColumn(nRow, "ZATGBN") == "R")	{	//첨부파일 구분 요청-R, 완료-F
			gfn_showAlert("CW10025", "요청자료로 삭제가 불가능한 첨부자료 입니다.");
			return;
		}

		var sRowType = ds_file.GetRowType(nRow);
		if ( sRowType == "insert" ) {
			ds_file.DeleteRow(nRow);
		} else {
			if (ds_file.GetColumn(nRow, "FLAG") == "D")	{
				ds_file.SetColumn(nRow, "FLAG", "");
			}else	{
				ds_file.SetColumn(nRow, "FLAG", "D");
			}
		}
	}
}

/********************************************************************************
* 기      능   : 그리드에서 file upload 입력시 
********************************************************************************/
function grd_file_OnExpandEdit(obj,nRow,nCell,strVal,nPivotIndex)
{
	//파일명 만들기	(DS : 도면 승인)		
	//var saveFileName = "DS_" + ds_header.GetColumn(0, "ZRQSEQ");

	if ( nCell == obj.GetBindCellIndex("body", "ZATTFN" ) ) {
		var arr = gfn_openFileMgr(
		      F_SERVER_DIR							// fa_serverDir
			, ds_file.getColumn(nRow, "ZATTFN")		// fa_serverFileName
			, ds_file.getColumn(nRow, "ZATTPATH")	// fa_localFileDir
			, ""									// fa_fileType
			, imgUploadFlg							// fa_saveType ("":업로드, "VIEW":보기)
			, true									// fa_isServerSave
			//, saveFileName							// fa_saveFileName
			//, false								// fa_isSave
			//, 									// fa_maxFileSize
		);

		if ( arr != null ) {
		   ds_file.setColumn(nRow, "ZATTPATH", arr[0]); // 디렉토리 정보 
		   ds_file.setColumn(nRow, "ZATTFN"  , arr[1]); // 파일명 
		}
	}
}

/********************************************************************************
* 기      능   : 파일 수정
********************************************************************************/
function ds_file_OnColumnChanged(obj,nRow,strColumnID,varOldValue,varNewValue,nPivotIndex)
{
	if ( varOldValue != varNewValue ) {
		if ( ds_file.GetColumn(nRow, "FLAG") != "I" && ds_file.GetColumn(nRow, "FLAG") != "D" ) {
			ds_file.SetColumn(nRow, "FLAG", "U");
		}
	}
}


/********************************************************************************
* 기      능   :  담당자 정보 처리
********************************************************************************/ 
function ed_sman_cd_OnChanged(obj,strText)
{
	// 입력된 조건이 없으면
	if (length(trim(ed_sman_cd.text)) != 0 ) {
		fn_query_sman();  				// 담당자조회 서비스 호출
	}else	{
		ed_sman_nm.Text = "";
	}
}

/********************************************************************************
* 기      능   :  담당자 조회   
********************************************************************************/
function fn_query_sman() { 

	gfn_clearMsgBar();   

//	trace(ds_cond_sman.SaveXML()); 	

	// 출력데이타셋 초기화
	ds_list_sman.ClearData();
	ds_cond_sman.ClearData();
	ds_cond_sman.AddRow();
	ds_cond_sman.SetColumn(ds_cond_sman.row, "SMAN_CD", trim(ed_sman_cd.text));
	ds_cond_sman.SetColumn(ds_cond_sman.row, "SMAN_NM", trim(ed_sman_nm.text));

	// 서비스호출
	nsfRequest("query"									// srvId
				, "com0030/find"						// url
				, "ds_cond_sman=ds_cond_sman ds_list_sman=ds_list_sman"		// inDsList
				, "ds_list_sman=ds_list_sman ds_error_sman=ds_error_sman"	// outDsList
				, ""									// strParam
				, "fn_afterQuery_sman");				// CallBackFunc
}


/********************************************************************************
* 기      능   :  담당자 조회 Callback  
********************************************************************************/ 
function fn_afterQuery_sman(errCode, errMsg) {
		//alert(" .... first search : "+ds_list_sman.SaveXML());
	if(ds_error_sman.rowcount() > 0) { 	// 호출결과처리
		//trace("fn_Query FAILED!!! "
		//       + "[idx : " 			+ ds_error.GetColumn(0,"IDX") + "] "
		//       + "[error message : "+ ds_error.GetColumn(0,"ERRMSG") + "]"
		//       );		       
	} else {
		//alert(" .... first search : "+ds_list_sman.SaveXML());
		if (ds_list_sman.RowCount() = 1) { // 한 건 조회된 경우
			// 조회해 온 값으로 담당자 검색정보 SET
			ed_sman_cd.Text = ds_list_sman.GetColumn(ds_list_sman.row, "SMAN_CD");
			ed_sman_nm.Text = ds_list_sman.GetColumn(ds_list_sman.row, "SMAN_NM");
		} 
		else if (ds_list_sman.RowCount() > 1) { // 다 건 조회된 경우	
			fn_OnClick_popup(btn_popSman); // 담당자조회 팝업화면  호출
		}
	}
}

function fn_OnClick_popup(obj)
{
	var arg = "fa_sman_cd= " 	+ quote(ed_sman_cd.text) // 담당자코드
			+ " fa_sman_nm = " + quote(ed_sman_nm.text); // 담당자명 

	var arr = gfn_openPopUpUrlHdel("Com::Com0030.xml", true, arg);	

	//trace("arr : " + arr);

	if (arr != null && arr != 0) {
		ed_sman_cd.text = arr[0];	// 리턴된 담당자코드로 검색조건 SET
		ed_sman_nm.text = arr[1];	// 리턴된 담당자명으로 검색조건 SET
		ed_sman_nm.SetFocus();
	} 
}

/******************************************************
// 담당자코드값 변경시 명칭 clear처리
******************************************************/
function ed_sman_cd_OnCharChanged(obj,strPreText,strPostText)
{
	if (strPreText != strPostText)
		ed_sman_nm.Text = "";
}

/********************************************************************************
* 기      능   : 그리드 더블클릭으로 파일 다운로드
********************************************************************************/
function grd_file_OnCellDblClick(obj,nRow,nCell,nX,nY,nPivotIndex)
{
	if (length(trim(ds_file.getColumn(nRow, "ZATTFN"))) == 0)	{
		gfn_showAlert("CW10025", "첨부자료가 없습니다.");
		return;		
	}

	var svrFileName = ds_file.GetColumn(nRow, "ZATTFN");
	var orgFileName = ds_file.GetColumn(nRow, "ZATTFN");
	var ext         = gfn_getFileExt(ds_file.getColumn(nRow, "ZATTFN"));

	//fileDialog.FileName = orgFileName;
	saveDialog.FileName = orgFileName;
	//fileDialog.Filter = "Acrobet Reader(*.pdf)|*.pdf|CAD Files(*.dwg)|*.dwg|";
	saveDialog.Filter = "All(*.*)|*.*";
	//fileDialog.FilterIndex = decode(ext, "pdf","0", "1");
    //fileDialog.open();
    saveDialog.Open();

	var idx = indexOf(saveDialog.FileName, ".");
	var fdFileName = saveDialog.FileName;
    var fileName =  decode(idx, -1, fdFileName, substr(fdFileName, 0, indexOf(fdFileName, ".")));
    var localFileName = fileName + "." + ext;

	var rMsg = tit_fileRead(
							httpFile
							, F_SERVER_DIR			// 저장 디렉토리
							, svrFileName			// 서버에서 전송받을 이름
							, saveDialog.FilePath	// 로컬 경로
							, localFileName			// 로컬 파일명
	);

	if ( rMsg[0] == "E" ) {
		if ( length(trim(rMsg[1])) > 0 ) {
			gfn_showAlert("CW10025", rMsg[1]);
		} else {
			gfn_showAlert("CW10025", "파일 다운로드에 실패하였습니다.");
		}
	} else {
		gfn_showAlert("CW10025", "파일을 다운로드 하였습니다.");
	}
}

function btn_outsourcing_OnClick(obj)
{

	if ( !gfn_showConfirm("CI00024", "[외주발송]") ) { // [외주발송]을 하시겠습니까?
		return;
	}

	    //=====================메일====================
	var body;
	var title;

	title = "요청번호 " + ed_zrqseq.text + "에 대한 도면작성의뢰건 ";
	body = "견적/수주 " + ed_qtso_no.text + " 에 대하여 도면작성 요청이 왔습니다. "
	     + " 확인 바랍니다. " ;
	body = body + " 발송일 : " + gfn_getCurrDate();    

	tit_clearActionInfo();	
	tit_callService(
			"COMM_ACT"
			, "sendmail"
			, ""
			, ""
			//, "USER_JUMIN=" + quote(med_jumin1.value+med_jumin2.value)
			//	+ 
			," SEND_USER=ADMIN@HDEL.CO.KR"
				//+ " RECE_USER=SEONHOWORLD@HDEL.CO.KR"
				+ " RECE_USER=KJANG0701@NAVER.COM"
				+ " MESGTITL=" + quote( title ) 
				+ " MESGBODY=" + quote( body ) 
			, ""  //"fn_afterQuery"
			, false
			, true);	
	    //=====================메일끝====================			

	if (length(trim(ed_zrqseq.Text)) == 0)		{
		gfn_showAlert("CW00001", "요청일련번호");
		ed_zrqseq.SetFocus();
		return;
	}

	fn_createWait("",this);     // 처리중입니다. 메시지 처리

	var inDs =  "ds_header=ds_header";

	var outDs = "ds_header=ds_header"
			 + " ds_error=ds_error";

	nsfRequest("saveOutdate", "ses0412/outsourcing", inDs, outDs, "", "fn_afterOutsrc");



}


function fn_afterOutsrc(svcId, errCode, errMsg)
{
	fn_destroyWait();

	if ( ds_error.GetRowCount() > 0 ) {
		gfn_showAlert("CE00001");
		return;
	}
	gfn_showAlert("CI00006", "외주발송"); // ${} 처리를 완료했습니다.
	btn_outsrc.Enable  = false;

	this.close();
	//fn_query();
}

function btn_outfinal_OnClick(obj)
{	

	if ( !gfn_showConfirm("CI00024", "[외주발송]") ) { // [외주발송]을 하시겠습니까?
		return;
	}

	    //=====================메일====================
	var body;
	var title;
	var kun_email;

	title = "요청번호 " + ed_zrqseq.text + "에 대한 도면작성완료건 ";
	body = "견적/수주 " + ed_qtso_no.text + " 에 대하여 도면작성이 완료됐습니다. "
	     + " 확인 바랍니다. " ;
	body = body + " 발송일 : " + gfn_getCurrDate();    

	ds_header.savexml();
	kun_email = ds_header.GetColumn(0,"KUN_EMAIL");
	tit_clearActionInfo();	
	tit_callService(
			"COMM_ACT"
			, "sendmail"
			, ""
			, ""
			//, "USER_JUMIN=" + quote(med_jumin1.value+med_jumin2.value)
			//	+ 
			," SEND_USER=ADMIN@HDEL.CO.KR"
				//+ " RECE_USER=SEONHOWORLD@HDEL.CO.KR"
				+ " RECE_USER=" + quote( kun_email )  
				+ " MESGTITL=" + quote( title ) 
				+ " MESGBODY=" + quote( body ) 
			, ""  //"fn_afterQuery"
			, false
			, true);	
	    //=====================메일끝====================	

	if (length(trim(ed_zrqseq.Text)) == 0)		{
		gfn_showAlert("CW00001", "요청일련번호");
		ed_zrqseq.SetFocus();
		return;
	}

	fn_createWait("",this);     // 처리중입니다. 메시지 처리

	var inDs =  "ds_header=ds_header";

	var outDs = "ds_header=ds_header"
			 + " ds_error=ds_error";

	nsfRequest("saveOutfinish", "ses0412/outfinish", inDs, outDs, "", "fn_afterOutsrc");




}
