	div_lifnr.ed_name1.Enable  = false;

	// 부서코드 정보에 따라 업무구분 결정
	/*if (G_SAP_USER_VGCD == "B81")	{
		cb_zrqflg.Value = "D";		// 도면 (설계부팀)
	}else */
	if (G_SAP_USER_VGCD == "B82")	{
		cb_zrqflg.Value = "R";		// 디자인(디자인팀)
	}
	/*else	{
		cb_zrqflg.Value = "D";
		//gfn_showAlert("CW10025", "권한이 존재하지 않습니다. 정보기술팀에 문의 하십시요.");
		//this.Close();
	}*/

	cb_zrqflg.Enable = false;

	//코드콤보 설정 
	var codeArr    = ["VKBUR",   "VKGRP"]; 	//코드콤보 설정
	var dsObjArr   = [ds_vkbur, ds_vkgrp];
	var addTypeArr = [G_CODE_ALL];
	gfn_searchCommCode(codeArr, dsObjArr, addTypeArr);

}


//=========================================================================================
// [ PART 3 ]
// Main Process Event 정의(조회,입력,삭제,저장,인쇄,form닫기)
//=========================================================================================
/********************************************************************************
* 기      능   : 조회 1
********************************************************************************/
var fn_query_chk = false; //중복조회 방지용 flag

function fn_query() {

	if (!fn_query_validation())	{
		return;
	}

	if(!fn_query_chk) {
		fn_query_chk = true;
	} else {
		gfn_showAlert("CW10025", "조회중입니다..");
		return;
	}

	fn_createWait("",this);     // 처리중입니다. 메시지 처리

	ds_error.ClearData();
	ds_cond.DeleteAll();
	ds_cond.AddRow();

	ds_cond.SetColumn(0, "FR_ZRQDAT", cal_zrqdat_fm.Value);
	ds_cond.SetColumn(0, "TO_ZRQDAT", cal_zrqdat_to.Value);
	ds_cond.SetColumn(0, "FR_VKBUR",  div_vbvg.cb_frvb.Value);	// 시작부서
	ds_cond.SetColumn(0, "TO_VKBUR",  div_vbvg.cb_tovb.Value);	// 종료부서
	ds_cond.SetColumn(0, "FR_VKGRP",  div_vbvg.cb_frvg.Value);	// 시작팀
	ds_cond.SetColumn(0, "TO_VKGRP",  div_vbvg.cb_tovg.Value);	// 종료팀
	//ds_cond.SetColumn(0, "ZAGNT",     div_lifnr.ed_lifnr.Text);	// 대리점
	//ds_cond.SetColumn(0, "ZKUNNR",    ed_sman_cd.Text);			// 영업담당자
	//ds_cond.SetColumn(0, "ZKUNNR_Z3", div_sman.ed_sman_cd.Text);	// 기술영업담당자
	//ds_cond.SetColumn(0, "ZRQFLG",    cb_zrqflg.Value);			// 구분(디자인, 도면)
	//ds_cond.SetColumn(0, "QTSO_NO",   ed_qtso_no.Text);			// 견적/프로젝트

	nsfRequest(  "query"
			   , "ses0430/findList"
			   , "ds_cond=ds_cond ds_list=ds_list"
			   , "ds_list=ds_list ds_error=ds_error"
			   , ""
			   , "fn_afterQuery"
			  );
}

/********************************************************************************
* 기      능   : 조회조건 필수 체크
********************************************************************************/
function fn_query_validation()
{
	var vMSG = "요청기간";

	// 필수조회조건 체크
	if(length(trim(cal_zrqdat_fm.Value)) != 8)	{
		gfn_showAlert("CW00001", vMSG);
		cal_zrqdat_fm.SetFocus();
		return false;
	}

	if(length(trim(cal_zrqdat_to.Value)) != 8)	{
		gfn_showAlert("CW00001", vMSG);
		cal_zrqdat_to.SetFocus();
		return false;
	}

	var arr = ['수주일자', '12'];
	if(gfn_getDayCnt(ds_days, cal_zrqdat_fm.Value, cal_zrqdat_to.Value) > 365) {
		gfn_showAlert("CW10087", arr);
		cal_zrqdat_fm.SetFocus();
		return false;
	}

	return true;
}

/********************************************************************************
* 기      능   : 닫기  
********************************************************************************/
function fn_close() {
	close();
}

//=========================================================================================
// [ PART 4]
// Callback 함수 정의 : callback 함수명은 메인 함수의 fn_xxxx() 에서 xxxx 부분은 fn_afterXXXX()
// 형태가 되도록 작성 
//=========================================================================================
/********************************************************************************
* 기      능   : 조회 Callback  
********************************************************************************/
function fn_afterQuery(svcId, errCode, errMsg) {

	fn_query_chk = false; //중복조회 방지용 flag
	fn_destroyWait();

	if(ds_error.rowcount() > 0) 	{
		gfn_showAlert("CE00001");
		return;
	}
	grd_list_OnCellClick();

	gfn_showMsg("CI00002", ds_list.RowCount() + "");
}

/********************************************************************************
* 기      능   : 공통코드가 조회된 후의 Callback 함수 
********************************************************************************/
function fn_completeInit(sCode, sDsObj, sCodeType) {
	return;
}

