	return gfn_isFormUnload(obj);
}

/********************************************************************************
* 기      능   : 폼 초기화
********************************************************************************/
function fn_init_form() {
	cal_qtdat_fm.Text = addDate(gfn_getCurrDate(), -5);	// 현재일자 5일전의 일자부터
	cal_qtdat_to.Text = gfn_getCurrDate();

	//코드콤보 설정 
	var codeArr    = ["VKBUR",   "VKGRP"]; 	//코드콤보 설정
	var dsObjArr   = [ds_vkbur, ds_vkgrp];
	var addTypeArr = [G_CODE_ALL];
	gfn_searchCommCode(codeArr, dsObjArr, addTypeArr);

	div_vbvg.st_vb_bar.Visible = false;
	div_vbvg.st_vg_bar.Visible = false;
	div_vbvg.cb_tovb.Visible   = false;
	div_vbvg.cb_tovg.Visible   = false;

	//div_vbvg.cb_frvg.Top       = 24;

	//프로젝트 조회 제외 임시 
	/*if ( G_USER_ID != "2020254") {
		ds_gubun.Filter("CODE ==" + quote("Q"));
	}*/
	// 공통코드 콤보목록 조회
 	fn_query_DD07T("ZRQSTATD", ds_zrqstat_cd);		// 진행상태

 	if (div_vbvg.cb_frvb.Value >= "EA00" && div_vbvg.cb_frvb.Value <= "EZ00"){
		btn_draw_request.Visible = "false";
 	} else if( div_vbvg.cb_frvb.Value != null)  {
		btn_draw_request2.Visible = "false";
 	}

 	if ( F_USER_GBN == "50" ) {
		btn_draw_request.Visible  = "true";
		btn_draw_request.Visible2 = "true";
 	}
}

// 공통코드 조회
function fn_query_DD07T(pDomainId, pDs)
{  
	// 데이타셋 초기화
	ds_cond_dd07t.ClearData();	
	pDs.ClearData();

	// 데이타셋 설정
	ds_cond_dd07t.AddRow();
	ds_cond_dd07t.SetColumn(ds_cond_dd07t.row, "DOMNAME"	, pDomainId);  
	ds_cond_dd07t.SetColumn(ds_cond_dd07t.row, "CONCAT_YN"	, "N");  

	// 서비스 호출
	nsfRequest(  "query"
			   , "combo/comboDD07T"
			   , "ds_cond_dd07t=ds_cond_dd07t " + pDs.ID+"="+pDs.ID
			   , pDs.ID+"="+pDs.ID +" ds_error=ds_error"
			   , ""
			   , "fn_afterQuery_DD07T_"+pDomainId); 
}

// 기종 조회 CALL BACK
function fn_afterQuery_DD07T_ZRQSTATD(errCode, errMsg) 
{  
	gfn_InsertRowCombo(ds_zrqstat_cd, 0, "Y", "Y"); // 선택 행 추가
	cb_zrqstat.index = 0;
}

