/********************************************************************************
* 기      능   : 조회 1
********************************************************************************/
var fn_query_chk = false; //중복조회 방지용 flag

function fn_query() {

	if (!fn_query_validation())	{
		return;
	}

	if(!fn_query_chk) {
		fn_query_chk = true;
	} else {
		gfn_showAlert("CW10025", "조회중입니다..");
		return;
	}

	fn_createWait("",this);     // 처리중입니다. 메시지 처리

	ds_detail.ClearData();

	ds_error.ClearData();
	ds_cond.DeleteAll();
	ds_cond.AddRow();

	if (div_vbvg.cb_frvb.Value >= "EA00" && div_vbvg.cb_frvb.Value <= "EZ00"){
	ds_cond.SetColumn(0, "ZRQSTAT",   cb_zrqstat.Value);
	ds_cond.SetColumn(0, "GUBUN",     'Q');//cb_gubun.Value);
	ds_cond.SetColumn(0, "QTSO_NO",   "S200000010");

	} else {
	ds_cond.SetColumn(0, "FR_QTDAT",  cal_qtdat_fm.Value);
	ds_cond.SetColumn(0, "TO_QTDAT",  cal_qtdat_to.Value);
	ds_cond.SetColumn(0, "FR_DLVDAT", cal_dlvdat_fm.Value);
	ds_cond.SetColumn(0, "TO_DLVDAT", cal_dlvdat_to.Value);
	ds_cond.SetColumn(0, "VKBUR",     div_vbvg.cb_frvb.Value);
	ds_cond.SetColumn(0, "VKGRP",     div_vbvg.cb_frvg.Value);
	ds_cond.SetColumn(0, "ZKUNNR",    div_sman.ed_sman_cd.Text);
	ds_cond.SetColumn(0, "ZRQSTAT",   cb_zrqstat.Value);
	ds_cond.SetColumn(0, "GUBUN",     'Q');//cb_gubun.Value);
	ds_cond.SetColumn(0, "QTSO_NO",   ed_qtso_no.Text);
	ds_cond.SetColumn(0, "VBELN",   ed_vbeln_no.Text);
	//ds_cond.SetColumn(0, "ZRQID",	div_cuser.ed_sman_cd.Text);
	}
	nsfRequest(  "header"
			   , "ses0450/findListHeader"
			   , "ds_cond=ds_cond ds_header=ds_header"
			   , "ds_header=ds_header ds_error=ds_error"
			   , ""
			   , "fn_afterQuery"
			  );
}

/********************************************************************************
* 기      능   : 조회조건 필수 체크
********************************************************************************/
function fn_query_validation()
{
	var vMSG = "생성기간";

	// 필수조회조건 체크
	if(length(trim(cal_qtdat_fm.Value)) != 8)	{
		gfn_showAlert("CW00001", vMSG);
		cal_qtdat_fm.SetFocus();
		return false;
	}

	if(length(trim(cal_qtdat_to.Value)) != 8)	{
		gfn_showAlert("CW00001", vMSG);
		cal_qtdat_to.SetFocus();
		return false;
	}

	if(gfn_getDayCnt(ds_days, cal_qtdat_fm.Value, cal_qtdat_to.Value) > 365) {
		var msg = "생성기간은 최대 12개월 까지만 가능합니다. \n확인하여 주십시오.";
		gfn_openMsgWin(msg, "W", "A");
		cal_qtdat_fm.SetFocus();
		return false;
	}

	vMSG = "발송기간";
	var vMSGs = ['발송기간 From','발송기간 To'];
	if(length(trim(cal_dlvdat_fm.Value)) > 0 && length(trim(cal_dlvdat_fm.Value)) < 8)	{
		gfn_showAlert("CW00008", vMSG);
		cal_dlvdat_fm.SetFocus();
		return false;
	}else if (length(trim(cal_dlvdat_fm.Value)) == 8)	{
		if (length(trim(cal_dlvdat_to.Value)) != 8)	{
			gfn_showAlert("CW00004", vMSGs);
			cal_dlvdat_to.SetFocus();
			return false;
		}
	}

	vMSGs = ['발송기간 To','발송기간 From'];
	if(length(trim(cal_dlvdat_to.Value)) > 0 && length(trim(cal_dlvdat_to.Value)) < 8)	{
		gfn_showAlert("CW00008", vMSG);
		cal_dlvdat_to.SetFocus();
		return false;
	}else if (length(trim(cal_dlvdat_to.Value)) == 8)	{
		if (length(trim(cal_dlvdat_fm.Value)) != 8)	{
			gfn_showAlert("CW00004", vMSGs);
			cal_dlvdat_fm.SetFocus();
			return false;
		}
	}

	return true;
}

/********************************************************************************
* 기      능   : 조회 2
********************************************************************************/
function fn_detail() {

	fn_createWait("",this);     // 처리중입니다. 메시지 처리

	ds_error.ClearData();
	ds_cond.ClearData();
	ds_cond.AddRow();

	ds_cond.SetColumn(0, "QTSO_NO", grd_header.GetCellText("Body", grd_header.CurrentRow, 2));
	ds_cond.SetColumn(0, "QTSER",   grd_header.GetCellText("Body", grd_header.CurrentRow, 3));
	ds_cond.SetColumn(0, "QTSEQ",   grd_header.GetCellText("Body", grd_header.CurrentRow, 4));
	ds_cond.SetColumn(0, "HOGI",    grd_header.GetCellText("Body", grd_header.CurrentRow, 5));

	nsfRequest( "detail"
			  , "ses0450/findListDetail"
			  , "ds_cond=ds_cond ds_detail=ds_detail"
			  , "ds_detail=ds_detail ds_error=ds_error"
			  , ""
			  , "fn_afterQuery"
			  );
}


