//=========================================================================================
// [ PART 4]
// Callback 함수 정의 : callback 함수명은 메인 함수의 fn_xxxx() 에서 xxxx 부분은 fn_afterXXXX()
// 형태가 되도록 작성 
//=========================================================================================
/********************************************************************************
* 기      능   : 조회 Callback  
********************************************************************************/
function fn_afterQuery(svcId, errCode, errMsg) {

	fn_query_chk = false; //중복조회 방지용 flag
	fn_destroyWait();

	if(ds_error.rowcount() > 0) 	{
		gfn_showAlert("CE00001");
		return;
	}

	if (svcId == "header")	{
		if ( ds_header.GetColumn(0,"QTSO_NO") == "S200000009"){
			ds_header.SetColumn(0,"VKBUR", "");
			ds_header.SetColumn(0,"VKGRP", "");
			ds_header.SetColumn(0,"ZKUNNR_NM", "");
			ds_header.SetColumn(0,"KUNNR_NM", "");
		}
		fn_header_grid_change('Q');//cb_gubun.Value);
		grd_header_OnCellClick();
	}

	gfn_showMsg("CI00002", ds_header.RowCount() + "");
}

