/********************************************************************************
* 기      능   :  SPEC 정보 추출
********************************************************************************/
function fn_specInfo(strGubun, nRow, strMatnr, strRtype, strType1, strType2, strType3, strZacapa, strZuse, strZopn, strZlen)
{
	if (strGubun == "Q")	{	// 견적의 경우만 처리
		var strVkbur = ds_header.GetColumn(nRow, "VKBUR");
		var strSpec  = "";
		strSpec = gfn_specInfo(strVkbur, strMatnr, strRtype, strType1, strType2, strType3, strZacapa, strZuse, strZopn, strZlen);
		ds_header.SetColumn(nRow, "SPEC", strSpec);
		return strSpec;
	}
}

