/********************************************************************************
* 기      능   :  더블클릭으로 상세화면 호출
********************************************************************************/
function grd_detail_OnCellDblClick(obj,nRow,nCell,nX,nY,nPivotIndex)
{
	if (ds_detail.GetRowCount() == 0)	return;

	var arg =  " fa_zrqseq  = " + quote(ds_detail.GetColumn(nRow, "ZRQSEQ"));
	    //arg += " fa_qtso_no = " + quote("");
	    arg += " fa_qtso_no = " + quote(ds_detail.GetColumn(nRow, "QTSO_NO"));
	    arg += " fa_qtser   = " + quote("");
	    arg += " fa_qtseq   = " + quote("");
	    arg += " fa_hogi    = " + quote("");
	    arg += " fa_matnr   = " + quote(ds_header.GetColumn(ds_header.currow, "MATNR"));	// 자재는 header정보에서 추출
	    arg += " fa_posnr   = " + quote(ds_header.GetColumn(ds_header.currow, "POSNR"));
		arg += " fa_vbeln   = " + quote("");
		arg += " fa_znumber = " + quote("");
		arg += " fa_gsnam   = " + quote("");
		if(ds_detail.GetColumn(nRow, "ZRQSEQ_DEL")=="A"){
			arg += " fa_zrqseq_del = " + quote("A");
			arg += " fa_f_frm_title = " + quote("디자인의뢰요청등록");
		}else if(ds_detail.GetColumn(nRow, "ZRQSEQ_DEL")=="B"){
			arg += " fa_zrqseq_del = " + quote("B");
			arg += " fa_f_frm_title = " + quote("의장승인용샘플신청등록");
		}else{
			arg += " fa_zrqseq_del = " + quote("C");
			arg += " fa_f_frm_title = " + quote("승인완료샘플배포등록");
		}


	fn_callPopup(arg);
}

/********************************************************************************
* 기      능   :  출력 버튼 클릭
********************************************************************************/
function grd_detail_OnExpandEdit(obj,nRow,nCell,strVal,nPivotIndex)
{
	if (nCell == 12)	{

		fn_createWait("",this);     // 처리중입니다. 메시지 처리

		ds_cond_print.ClearData();
		ds_cond_print.AddRow();
		ds_cond_print.SetColumn(0, "MANDT"  , G_MANDT   );
		ds_cond_print.SetColumn(0, "ZRQSEQ" , ds_detail.GetColumn(nRow, "ZRQSEQ") );
		ds_cond_print.SetColumn(0, "QTSO_NO", ds_header.GetColumn(ds_header.currow, "QTSO_NO"));
		ds_cond_print.SetColumn(0, "QTSER"  , ds_header.GetColumn(ds_header.currow, "QTSER"));
		ds_cond_print.SetColumn(0, "QTSEQ"  , ds_header.GetColumn(ds_header.currow, "QTSEQ"));
		ds_cond_print.SetColumn(0, "HOGI"   , ds_header.GetColumn(ds_header.currow, "HOGI"));
		ds_cond_print.SetColumn(0, "MATNR"  , ds_header.GetColumn(ds_header.currow, "MATNR"));	// 자재는 header정보에서 추출
		ds_cond_print.SetColumn(0, "POSNR"  , ds_header.GetColumn(ds_header.currow, "POSNR"));

		ds_error.ClearData();

		var inDs =  "ds_cond=ds_cond_print"
				 + " ds_header=ds_print"
				 + " ds_template=ds_template"
				 + " ds_templateSd120=ds_templateSd120"
				 + " ds_file=ds_file"
				 + " ds_email=ds_email";

		var outDs = "ds_print=ds_header"
				 + " ds_template=ds_template"
				 + " ds_templateSd120=ds_templateSd120"
				 + " ds_file=ds_file"
				 + " ds_email=ds_email"
				 + " ds_error=ds_error";

		nsfRequest(  "print"
				   , "ses0451/find"
				   , inDs
				   , outDs
				   , ""
				   , "fn_afterPrint"
				  );
	}
}

/********************************************************************************
* 기      능   : 출력 Callback  
********************************************************************************/
function fn_afterPrint(svcId, errCode, errMsg)
{

	if(ds_error.rowcount() > 0) 	{
		fn_destroyWait();

		gfn_showAlert("CE00001");
		return;
	}

	if (svcId == "print")	{

		ds_print.SetColumn(0, "MATNR", ds_header.GetColumn(ds_header.currow, "MATNR"));

		var dsTemp = gfn_drawTemplateSet(ds_template, ds_templateSd120);

		ds_template.ClearData();
		ds_template.Copy(dsTemp);

		var varAtkla = "";
		for (var i = 0; i < ds_template.GetRowCount(); i++)	{
			varAtkla = substr(ds_template.GetColumn(i, "ATKLA"), 3);

			switch (varAtkla)	{
				case "BASIC"   : varAtkla = "1";
							     break;
				case "INSIDE"  : varAtkla = "2";
							     break;
				case "OUTSIDE" : varAtkla = "3";
							     break;
				case "OPTION"  : varAtkla = "4";
							     break;
				default : break;
			}
			ds_template.SetColumn(i, "ATKLA", varAtkla);
		}
		fn_destroyWait();
		Dialog("SES::Ses0452.xml", "", -1, -1, "Resize=true");	// UbiReport Call
	}
}

// 견적번호 또는 프로젝트 번호 더블 클릭 시  상세 사양 조회 popup처리
function grd_header_OnCellDblClick(obj,nRow,nCell,nX,nY,nPivotIndex)
{
	if (ds_header.RowCount() == 0)
		return;

	var matnr = trim( ds_header.getColumn(nRow, "MATNR") );
	if (matnr == null || matnr == "")
	{
		gfn_showAlert("CW10046");
		return false;
	}else if (matnr == "F-1000")	{	// 물류는 사양이 존재하지 않음
		return false;
	}

	// 견적번호 또는 프로젝트 번호 
	if (nCell == 2)	{
		ds_arg.ClearData(); 
		var addRow = ds_arg.addRow();

		if (ds_header.GetColumn(nRow, "GUBUN") == "P")		//  프로젝트 경우
		{
			ds_arg.setColumn(addRow, "GUBUN", "SO");		// 수주
			ds_arg.setColumn(addRow, "MANDT", G_MANDT);
			ds_arg.setColumn(addRow, "VBELN", ds_header.getColumn(nRow, "QTSO_NO"));	// 프로젝트
			ds_arg.setColumn(addRow, "MATNR", matnr);  // 자재번호

			// 판매문서품목 체크
			posnr = trim(ds_header.getColumn(nRow, "POSNR"));
			if (posnr == null || posnr == "")
			{
				gfn_showAlert("CW10068");
				return false;
			}

			ds_arg.setColumn(addRow, "POSNR",   posnr);   // 판매문서품목
			ds_arg.setColumn(addRow, "HOGI",    trim(ds_header.getColumn(nRow, "HOGI")));      // 호기
		}
		else	// 견적정보 경우
		{
			ds_arg.setColumn(addRow, "GUBUN", "QT");		// 견적
			ds_arg.setColumn(addRow, "MANDT", G_MANDT); 
			ds_arg.setColumn(addRow, "QTNUM", ds_header.getColumn(nRow, "QTSO_NO")); 	// 견적번호
			ds_arg.setColumn(addRow, "QTSER", ds_header.getColumn(nRow, "QTSER"));	// 견적차수
			ds_arg.setColumn(addRow, "QTSEQ", ds_header.getColumn(nRow, "QTSEQ"));	// 견적일련번호
			ds_arg.setColumn(addRow, "MATNR", matnr);  // 자재번호
		}

		var vArg = "fa_gubun = " + quote("T");
		var arr = gfn_openPopUpUrlHdel("SSO::Sso0062.xml", true, vArg);

		if (arr != null && arr != 0) {
			return;
		}	
	}
}

// 이동시 처리
function grd_header_OnCellPosChanged(obj,nRow,nCell,nOldRow,nOldCell,nPivotIndex,nOldPivotIndex)
{
	if (nOldRow != nRow)	{
		grd_header_OnCellClick(obj, nRow, nCell, "", "", nPivotIndex);
	}
}



