		arg += " fa_vbeln   = " + quote(ds_header.GetColumn(ds_header.currow, "VBELN"));
		//arg += " fa_znumber = " + quote(ds_header.GetColumn(ds_header.currow, "ZNUMBER"));
		//arg += " fa_gsnam   = " + quote(ds_header.GetColumn(ds_header.currow, "GSNAM"));
		arg += " fa_zrqseq_del = " + quote("A");
		arg += " fa_f_frm_title = " + quote("디자인의뢰요청등록");
