function fn_OnLoadCompleted(obj) {
	// Form 로드 이벤트 맨 첫줄에 기술 
	gfn_initForm(true);

	fn_init_form(); 	// 폼 초기화
}

/********************************************************************************
* 기      능   : form UnloadCompleted 
********************************************************************************/
function fn_OnUnloadCompleted(obj) {
	return gfn_isFormUnload(obj);
}

/********************************************************************************
* 기      능   : 폼 초기화
********************************************************************************/
function fn_init_form() {

	cal_dlvdat_fm.Text = addDate(gfn_getCurrDate(), -10);	// 현재일자 10일전의 일자부터
	cal_dlvdat_to.Text = gfn_getCurrDate();

	//div_lifnr.ed_name1.Visible = true;
	//div_lifnr.ed_name1.Enable  = false;

	// 접수/반려 & 전송 버튼 disable
	btn_accept.Enable   = false;
	btn_approval.Enable = false;
	btn_detail.Enable   = false;

	//코드콤보 설정 
	var codeArr    = ["VKBUR",   "VKGRP"]; 	//코드콤보 설정
	var dsObjArr   = [ds_vkbur, ds_vkgrp];
	var addTypeArr = [G_CODE_ALL];
	gfn_searchCommCode(codeArr, dsObjArr, addTypeArr);

	// 공통코드 콤보목록 조회
 	fn_query_DD07T("ZRQSTATD", ds_zrqstat_cd);		// 진행상태
 	fn_query_DD07T("ZSDEMAILGBN", ds_code_email_dept); //이메일 전송할 수신부서
 	fn_query_DD07T("ZRQSEQ_DEL", ds_zrqseq_del); //구분

 	cb_dsngbn.index = 0;
 	cb_proctp.index = 0;
}

// 공통코드 조회
function fn_query_DD07T(pDomainId, pDs)
{  
	// 데이타셋 초기화
	ds_cond_dd07t.ClearData();	
	pDs.ClearData();

	// 데이타셋 설정
	ds_cond_dd07t.AddRow();
	ds_cond_dd07t.SetColumn(ds_cond_dd07t.row, "DOMNAME"	, pDomainId);  
	ds_cond_dd07t.SetColumn(ds_cond_dd07t.row, "CONCAT_YN"	, "N");  

	// 서비스 호출
	nsfRequest(  "query"
			   , "combo/comboDD07T"
			   , "ds_cond_dd07t=ds_cond_dd07t " + pDs.ID+"="+pDs.ID
			   , pDs.ID+"="+pDs.ID +" ds_error=ds_error"
			   , ""
			   , "fn_afterQuery_DD07T_"+pDomainId); 
}

// 기종 조회 CALL BACK
function fn_afterQuery_DD07T_ZRQSTATD(errCode, errMsg) 
{
	gfn_InsertRowCombo(ds_zrqstat_cd, 0, "Y", "Y"); // 선택 행 추가

	// 작성은 제외하여 표시
	var searchRow = ds_zrqstat_cd.SearchRow("CODE == 'W'");
	if (searchRow != -1)	{
		ds_zrqstat_cd.DeleteRow(searchRow);
	}

	cb_zrqstat.index = 0;
}

function fn_afterQuery_DD07T_ZSDEMAILGBN(errCode, errMsg) 
{
	gfn_InsertRowCombo(ds_code_email_dept, 0, "Y", "Y"); // 선택 행 추가

	cb_email_dept.index = 0;
}

function fn_afterQuery_DD07T_ZRQSEQ_DEL(errCode, errMsg) 
{
	gfn_InsertRowCombo(ds_zrqseq_del, 0, "Y", "Y"); // 선택 행 추가

	cb_zrqseq_del.index = 0;
}
