/********************************************************************************
* 기      능   : 조회 1
********************************************************************************/
var fn_query_chk = false; //중복조회 방지용 flag

function fn_query() {

	if (!fn_query_validation())	{
		return;
	}

	if(!fn_query_chk) {
		fn_query_chk = true;
	} else {
		gfn_showAlert("CW10025", "조회중입니다..");
		return;
	}

	fn_createWait("",this);     // 처리중입니다. 메시지 처리

	ds_error.ClearData();
	ds_cond.DeleteAll();
	ds_cond.AddRow();

	ds_cond.SetColumn(0, "FR_DLVDAT", cal_dlvdat_fm.Value);
	ds_cond.SetColumn(0, "TO_DLVDAT", cal_dlvdat_to.Value);
	ds_cond.SetColumn(0, "FR_VKBUR",  div_vbvg.cb_frvb.Value);	// 시작부서
	ds_cond.SetColumn(0, "TO_VKBUR",  div_vbvg.cb_tovb.Value);	// 종료부서
	ds_cond.SetColumn(0, "FR_VKGRP",  div_vbvg.cb_frvg.Value);	// 시작팀
	ds_cond.SetColumn(0, "TO_VKGRP",  div_vbvg.cb_tovg.Value);	// 종료팀
	//ds_cond.SetColumn(0, "ZAGNT",     div_lifnr.ed_lifnr.Text);	// 대리점
	//ds_cond.SetColumn(0, "ZKUNNR",    ed_sman_cd.Text);			// 영업담당자
	//ds_cond.SetColumn(0, "KUNNR_DS",  div_sman.ed_sman_cd.Text);	// 디자인담당자
	ds_cond.SetColumn(0, "ZKUNNR",    div_sman.ed_sman_cd.Text);			// 영업담당자
	ds_cond.SetColumn(0, "KUNNR_DS",  ed_sman_cd.Text);	// 디자인담당자
	ds_cond.SetColumn(0, "ZRQSTAT",   cb_zrqstat.Value);		// 진행상태
	ds_cond.SetColumn(0, "QTSO_NO",   ed_qtso_no.Text);			// 견적/프로젝트
	ds_cond.SetColumn(0, "EMAIL_DEPT",   cb_email_dept.value);	//이메일 수신부서
	ds_cond.SetColumn(0, "ZRQSEQ_DEL",   cb_zrqseq_del.value);	//구분

	ds_cond.SetColumn(0, "DSNGBN",   cb_dsngbn.value);	//디자인구분(요청구분)
	ds_cond.SetColumn(0, "PROCTP",   cb_proctp.value);	//구분2 (완료 구분)

	nsfRequest(  "query"
			   , "ses0450/findRequestList"
			   , "ds_cond=ds_cond ds_list=ds_list"
			   , "ds_list=ds_list ds_error=ds_error"
			   , ""
			   , "fn_afterQuery"
			  );
}

/********************************************************************************
* 기      능   : 조회조건 필수 체크
********************************************************************************/
function fn_query_validation()
{
	var vMSG = "발송기간";

	// 필수조회조건 체크
	if(length(trim(cal_dlvdat_fm.Value)) != 8)	{
		gfn_showAlert("CW00001", vMSG);
		cal_dlvdat_fm.SetFocus();
		return false;
	}

	if(length(trim(cal_dlvdat_to.Value)) != 8)	{
		gfn_showAlert("CW00001", vMSG);
		cal_dlvdat_to.SetFocus();
		return false;
	}

	var arr = ['발송기간', '12'];
	if(gfn_getDayCnt(ds_days, cal_dlvdat_fm.Value, cal_dlvdat_to.Value) > 365) {
		gfn_showAlert("CW10087", arr);
		cal_dlvdat_fm.SetFocus();
		return false;
	}

	return true;
}

/********************************************************************************
* 기      능   : 닫기  
********************************************************************************/
function fn_close() {
	close();
}

