/********************************************************************************
* 기      능   :  SPEC 정보 추출
********************************************************************************/
function fn_specInfo(strGubun, nRow, strMatnr, strRtype, strType1, strType2, strType3, strZacapa, strZuse, strZopn, strZlen)
{
	if (strGubun == "Q")	{	// 견적의 경우만 처리
		var strVkbur = ds_list.GetColumn(nRow, "VKBUR");
		var strSpec  = "";
		strSpec = gfn_specInfo(strVkbur, strMatnr, strRtype, strType1, strType2, strType3, strZacapa, strZuse, strZopn, strZlen);
		ds_list.SetColumn(nRow, "SPEC", strSpec);
		return strSpec;
	}
}

/********************************************************************************
* 기      능   :  담당자 정보 처리
********************************************************************************/ 
function ed_sman_cd_OnChanged(obj,strText)
{
	// 입력된 조건이 없으면
	if (length(trim(ed_sman_cd.text)) == 0 && length(trim(ed_sman_nm.text)) == 0) {
		//fn_OnClick_popup(btn_popSman);  // 팝업화면 호출
	} else {
		fn_query_sman();  				// 담당자조회 서비스 호출
	}
}


/********************************************************************************
* 기      능   :  담당자 조회   
********************************************************************************/
function fn_query_sman() { 

	gfn_clearMsgBar();   

//	trace(ds_cond_sman.SaveXML()); 	

	// 출력데이타셋 초기화
	ds_list_sman.ClearData();
	ds_cond_sman.ClearData();
	ds_cond_sman.AddRow();
	ds_cond_sman.SetColumn(ds_cond_sman.row, "SMAN_CD", trim(ed_sman_cd.text));
	ds_cond_sman.SetColumn(ds_cond_sman.row, "SMAN_NM", trim(ed_sman_nm.text));

	// 서비스호출
	nsfRequest("query"									// srvId
				, "com0030/find"						// url
				, "ds_cond_sman=ds_cond_sman ds_list_sman=ds_list_sman"		// inDsList
				, "ds_list_sman=ds_list_sman ds_error_sman=ds_error_sman"	// outDsList
				, ""									// strParam
				, "fn_afterQuery_sman");				// CallBackFunc
}


/********************************************************************************
* 기      능   :  담당자 조회 Callback  
********************************************************************************/ 
function fn_afterQuery_sman(errCode, errMsg) {
		//alert(" .... first search : "+ds_list_sman.SaveXML());
	if(ds_error_sman.rowcount() > 0) { 	// 호출결과처리
		return;
	} else {
		if (ds_list_sman.RowCount() = 1) { // 한 건 조회된 경우
			// 조회해 온 값으로 담당자 검색정보 SET
			ed_sman_cd.Text = ds_list_sman.GetColumn(ds_list_sman.row, "SMAN_CD");
			ed_sman_nm.Text = ds_list_sman.GetColumn(ds_list_sman.row, "SMAN_NM");
		} 
		else if (ds_list_sman.RowCount() > 1) { // 다 건 조회된 경우	
			fn_OnClick_popup(btn_popSman); // 담당자조회 팝업화면  호출
		}
	}
}

function fn_OnClick_popup(obj)
{
	var arg = "fa_sman_cd= " 	+ quote(ed_sman_cd.text) // 담당자코드
			 + " fa_sman_nm = " + quote(ed_sman_nm.text); // 담당자명 

	var arr = gfn_openPopUpUrlHdel("Com::Com0030.xml", true, arg);

	if (arr != null && arr != 0) {
		ed_sman_cd.text = arr[0];	// 리턴된 담당자코드로 검색조건 SET
		ed_sman_nm.text = arr[1];	// 리턴된 담당자명으로 검색조건 SET
		ed_sman_nm.SetFocus();
	} 
}

/******************************************************
// 담당자코드값 변경시 명칭 clear처리
******************************************************/
function ed_sman_cd_OnCharChanged(obj,strPreText,strPostText)
{
	if (strPreText != strPostText)
		ed_sman_nm.Text = "";
}

/******************************************************
// 견적번호 또는 프로젝트 번호 더블 클릭 시  상세 사양 조회 popup처리
******************************************************/
function grd_list_OnCellDblClick(obj,nRow,nCell,nX,nY,nPivotIndex)
{
	if (ds_list.RowCount() == 0)
		return;

	var matnr = trim( ds_list.getColumn(nRow, "MATNR") );
	if (matnr == null || matnr == "")
	{
		gfn_showAlert("CW10046");
		return false;
	}else if (matnr == "F-1000")	{	// 물류는 사양이 존재하지 않음
		return false;
	}

	// 견적번호 또는 프로젝트 번호 
	if (nCell == 5)	{
		ds_arg.ClearData(); 
		var addRow = ds_arg.addRow();

		if (ds_list.GetColumn(nRow, "GUBUN") == "P")		//  프로젝트 경우
		{
			ds_arg.setColumn(addRow, "GUBUN", "SO");		// 수주
			ds_arg.setColumn(addRow, "MANDT", G_MANDT);
			ds_arg.setColumn(addRow, "VBELN", ds_list.getColumn(nRow, "QTSO_NO"));	// 프로젝트
			ds_arg.setColumn(addRow, "MATNR", matnr);  // 자재번호

			// 판매문서품목 체크
			posnr = trim(ds_list.getColumn(nRow, "POSNR"));
			if (posnr == null || posnr == "")
			{
				gfn_showAlert("CW10068");
				return false;
			}

			ds_arg.setColumn(addRow, "POSNR",   posnr);   // 판매문서품목
			ds_arg.setColumn(addRow, "HOGI",    trim(ds_list.getColumn(nRow, "HOGI")));      // 호기
		}
		else	// 견적정보 경우
		{
			ds_arg.setColumn(addRow, "GUBUN", "QT");		// 견적
			ds_arg.setColumn(addRow, "MANDT", G_MANDT); 
			ds_arg.setColumn(addRow, "QTNUM", ds_list.getColumn(nRow, "QTSO_NO")); 	// 견적번호
			ds_arg.setColumn(addRow, "QTSER", ds_list.getColumn(nRow, "QTSER"));	// 견적차수
			ds_arg.setColumn(addRow, "QTSEQ", ds_list.getColumn(nRow, "QTSEQ"));	// 견적일련번호
			ds_arg.setColumn(addRow, "MATNR", matnr);  // 자재번호
		}

		var vArg = "fa_gubun = " + quote("T");
		var arr = gfn_openPopUpUrlHdel("SSO::Sso0062.xml", true, vArg);

		if (arr != null && arr != 0) {
			return;
		}	
	}
}

/********************************************************************************
* 기      능   :  요청화면 call , 접수 및 반려
********************************************************************************/
function fn_callPopup(arg)
{
	var url = "Ses::Ses0461.xml";

    gfn_openPopUpUrlHdel(url, true, arg);
}

/********************************************************************************
* 기      능   :  접수 및 반려 버튼 클릭
********************************************************************************/
function btn_accept_OnClick(obj)
{
	var arg =  " fa_zrqseq = "  + quote(ds_list.GetColumn(ds_list.currow, "ZRQSEQ"));
		arg +=  " fa_matnr  = "  + quote(ds_list.GetColumn(ds_list.currow, "MATNR"));
		arg +=  " fa_posnr  = "  + quote(ds_list.GetColumn(ds_list.currow, "POSNR"));
	    arg += " fa_flag = "    + quote("R");	// 요청 접수 (담당자 지정) : 팀장권한만 처리
	    arg += " fa_qtso_no = "    + quote(ds_list.GetColumn(ds_list.currow, "QTSO_NO"));

	fn_callPopup(arg);
}

/********************************************************************************
* 기      능   :  접수 후 완료 처리를 위한 호출
********************************************************************************/
function fn_approval_OnClick(obj)
{
	var arg =  " fa_zrqseq = "  + quote(ds_list.GetColumn(ds_list.currow, "ZRQSEQ"));
		arg +=  " fa_matnr  = "  + quote(ds_list.GetColumn(ds_list.currow, "MATNR"));
		arg +=  " fa_posnr  = "  + quote(ds_list.GetColumn(ds_list.currow, "POSNR"));
	    arg += " fa_flag = "    + quote("A");	// 완료
	    arg += " fa_qtso_no = "    + quote(ds_list.GetColumn(ds_list.currow, "QTSO_NO"));

	fn_callPopup(arg);
}

/******************************************************
// 그리드 클릭 시 접수/반납 & 전송 버튼 상태 처리
******************************************************/
function grd_list_OnCellClick(obj,nRow,nCell,nX,nY,nPivotIndex)
{
	var varUserGbn = right(G_USER_GROUP_B, 2 );		// 30 : 담당, 40 : 팀장, 50 : 관리자
	var varStat = ds_list.GetColumn(nRow, "ZRQSTAT");

	btn_detail.Enable   = true;

	if (varStat == "R")		{	// 요청이 경우 팀장, 관리자만 접수/반려 가능
		if (varUserGbn == "30" || varUserGbn == "40" || varUserGbn == "50")	{
			btn_accept.Enable = true;
		}else	{
			btn_accept.Enable = false;
		}
		btn_approval.Enable  = false;
	}else if (varStat == "A")	{	// 접수상태에서만 전송 가능 //접수 이후 담당자 변경가능토록 버튼 활성화 2013.12.16
		btn_approval.Enable  = true;
		if (varUserGbn == "30" || varUserGbn == "40" || varUserGbn == "50")	{
			btn_accept.Enable = true;
		}else	{
			btn_accept.Enable = false;
		}
	}else	{		// 상세 조회만 가능 >>변경 요청이 잦아 버튼 활성화 20160627
		btn_accept.Enable   = true; //false;
		btn_approval.Enable = true; //false;
	}	
}

/******************************************************
// 그리드 row 변경 시
******************************************************/
function grd_list_OnCellPosChanged(obj,nRow,nCell,nOldRow,nOldCell,nPivotIndex,nOldPivotIndex)
{
	if (nOldRow != nRow)	{
		grd_list_OnCellClick(obj, nRow, nCell, "", "", nPivotIndex);
	}
}

/******************************************************
// 그리드 출력버튼 클릭
******************************************************/
function grd_list_OnExpandEdit(obj,nRow,nCell,strVal,nPivotIndex)
{
	if (nCell == 21)	{

		fn_createWait("",this);     // 처리중입니다. 메시지 처리

		ds_cond_print.ClearData();
		ds_cond_print.AddRow();
		ds_cond_print.SetColumn(0, "MANDT"  , G_MANDT   );
		ds_cond_print.SetColumn(0, "ZRQSEQ" , ds_list.GetColumn(nRow, "ZRQSEQ") );
		ds_cond_print.SetColumn(0, "QTSO_NO", ds_list.GetColumn(nRow, "QTSO_NO"));
		ds_cond_print.SetColumn(0, "QTSER"  , ds_list.GetColumn(nRow, "QTSER"));
		ds_cond_print.SetColumn(0, "QTSEQ"  , ds_list.GetColumn(nRow, "QTSEQ"));
		ds_cond_print.SetColumn(0, "HOGI"   , ds_list.GetColumn(nRow, "HOGI"));
		ds_cond_print.SetColumn(0, "MATNR"  , ds_list.GetColumn(nRow, "MATNR"));	// 자재는 header정보에서 추출
		ds_cond_print.SetColumn(0, "POSNR"  , ds_list.GetColumn(nRow, "POSNR"));

		ds_error.ClearData();

		var inDs =  "ds_cond=ds_cond_print"
				 + " ds_header=ds_print"
				 + " ds_template=ds_template"
				 + " ds_templateSd120=ds_templateSd120"
				 + " ds_file=ds_file"
				 + " ds_email=ds_email";

		var outDs = "ds_print=ds_header"
				 + " ds_template=ds_template"
				 + " ds_templateSd120=ds_templateSd120"
				 + " ds_file=ds_file"
				 + " ds_email=ds_email"
				 + " ds_error=ds_error";

		nsfRequest(  "print"
				   , "ses0451/find"
				   , inDs
				   , outDs
				   , ""
				   , "fn_afterPrint"
				  );
	}
}

/********************************************************************************
* 기      능   : 출력 Callback  
********************************************************************************/
function fn_afterPrint(svcId, errCode, errMsg)
{

	if(ds_error.rowcount() > 0) 	{
		fn_destroyWait();

		gfn_showAlert("CE00001");
		return;
	}

	if (svcId == "print")	{

		ds_print.SetColumn(0, "MATNR", ds_list.GetColumn(ds_list.currow, "MATNR"));

		var dsTemp = gfn_drawTemplateSet(ds_template, ds_templateSd120);
		ds_template.ClearData();
		ds_template.Copy(dsTemp);

		var varAtkla = "";
		for (var i = 0; i < ds_template.GetRowCount(); i++)	{
			varAtkla = substr(ds_template.GetColumn(i, "ATKLA"), 3);

			switch (varAtkla)	{
				case "BASIC"   : varAtkla = "1";
							     break;
				case "INSIDE"  : varAtkla = "2";
							     break;
				case "OUTSIDE" : varAtkla = "3";
							     break;
				case "OPTION"  : varAtkla = "4";
							     break;
				default : break;
			}
			ds_template.SetColumn(i, "ATKLA", varAtkla);
		}

		fn_destroyWait();
		Dialog("SES::Ses0452.xml", "", -1, -1, "Resize=true");	// UbiReport Call
	}
}

// 상세조회 버튼 클릭
function btn_detail_OnClick(obj)
{
	if (ds_list.GetRowCount() == 0)		return;

	var arg =  " fa_zrqseq = "  + quote(ds_list.GetColumn(ds_list.currow, "ZRQSEQ"));
		arg +=  " fa_matnr  = "  + quote(ds_list.GetColumn(ds_list.currow, "MATNR"));
		arg +=  " fa_posnr  = "  + quote(ds_list.GetColumn(ds_list.currow, "POSNR"));
	    arg += " fa_flag = "    + quote("Q");	// 상세 조회
	    arg += " fa_qtso_no = "    + quote(ds_list.GetColumn(ds_list.currow, "QTSO_NO"));

	fn_callPopup(arg);
}
