// Form Loading / Unloading 시 작업 정의
//=========================================================================================
function fn_OnLoadCompleted(obj)
{
	F_USER_GBN = right(G_USER_GROUP_B, 2); // 20 : 담당, 30 : 팀장, 40 : 부서장, 50 : 관리자

	gfn_initForm(false);	// Form 로드 이벤트 맨 첫줄에 기술
	fn_init_form();			// 폼 초기화

	div_btn.fn_loadUseBtn("5,65,62", F_FRM_TITLE, "", ""); // 5-접수,65-반송,62-완료

	div_btn.btn_sendback.DomainId = "";
	div_btn.btn_sendback.Text     = "반려";
}

/********************************************************************************
* 기      능   : form UnloadCompleted 
********************************************************************************/
function fn_OnUnloadCompleted(obj)
{
	return gfn_isFormUnload(obj);
}

/********************************************************************************
* 기      능   : 폼 초기화
********************************************************************************/
function fn_init_form()
{
	var zrqseq  = ""; if ( IsExistVar("fa_zrqseq" ) ) zrqseq  = trim(fa_zrqseq );
	var matnr   = ""; if ( IsExistVar("fa_matnr"  ) ) matnr   = trim(fa_matnr  );
	var posnr   = ""; if ( IsExistVar("fa_posnr"  ) ) posnr   = trim(fa_posnr  );
	var flag    = ""; if ( IsExistVar("fa_flag"   ) ) flag    = trim(fa_flag   );

	// 디자인 담당자 변경 못하게
	ed_sman_cd.Enable  = false;
	btn_popSman.Enable = false;
	ed_zrsrlt.Enable   = false;

	div_lifnr.ed_lifnr.enable = false;
	div_lifnr.ed_name1.enable = false;
	div_lifnr.btn_popLifnr.enable = false;

	// 의뢰자는 변경 못하게 고정
	div_zrqid.ed_sman_cd.enable = false;
	div_zrqid.btn_popSman.enable = false;

	// 기디자인 담당자 변경 못하게 고정
	div_ex_kunnr.ed_sman_cd.enable = false;
	div_ex_kunnr.btn_popSman.enable = false;

	div_btn.btn_receipt.enable = false; // 접수 버튼
	div_btn.btn_sendback.enable = false; // 반려 버튼
	div_btn.btn_finish.enable = false; // 완료 버튼

	btn_add.Visible   = false;
	btn_del.Visible   = false;				

	if (flag == "R") {
		F_FRM_TITLE  = "디자인의뢰요청 접수/반려";
		if (F_USER_GBN == "30" || F_USER_GBN == "40" || F_USER_GBN == "50")	{	// 팀장 및 관리자의 경우 접수/ 반려 가능
			div_btn.btn_receipt.enable = true; // 접수
			div_btn.btn_sendback.enable = true; // 반려
			ed_zrsrlt.Enable   = true;			
			ed_sman_cd.Enable  = true;
			btn_popSman.Enable = true;
			div_lifnr.ed_lifnr.enable = true;
			div_lifnr.ed_name1.enable = true;
			div_lifnr.btn_popLifnr.enable = true;
		}
	} else if (flag == "A") {
		F_FRM_TITLE  = "디자인의뢰요청 완료";
		div_btn.btn_finish.Enable = true; // 완료
		ed_zrsrlt.Enable  = true;
		ed_sman_cd.Enable  = true;		  //디자인 담당자 변경가능 2013.07.22 신미정		
		btn_popSman.Enable = true;		
		btn_add.Visible   = true;
		btn_del.Visible   = true;			
	} else {
		F_FRM_TITLE  = "디자인의뢰요청 상세조회";
	}

	if ( substr(fa_qtso_no,0,1) = "S" ) { //서비스 견적일때
		tab.TabIndex = 1;				  //탭 기본설정
	}

	ds_cond.ClearData();
	ds_cond.AddRow();
	ds_cond.SetColumn(0, "MANDT"  , G_MANDT);
	ds_cond.SetColumn(0, "ZRQSEQ" , zrqseq );
	ds_cond.SetColumn(0, "MATNR"  , matnr  );
	ds_cond.SetColumn(0, "POSNR"  , posnr  );

	fn_query();
}

