/********************************************************************************
* 기      능   : 조회
********************************************************************************/
function fn_query()
{
	var inDs =  "ds_cond=ds_cond"
			 + " ds_header=ds_header"
			 + " ds_template=ds_template"
			 + " ds_templateSd120=ds_templateSd120"
			 + " ds_file=ds_file"
			 + " ds_email=ds_email"			 
	;
	var outDs = "ds_header=ds_header"
			 + " ds_template=ds_template"
			 + " ds_templateSd120=ds_templateSd120"
			 + " ds_file=ds_file"
			 + " ds_email=ds_email"
			 + " ds_error=ds_error"
	;

	fn_createWait("",this);     // 처리중입니다. 메시지 처리

	nsfRequest("query", "ses0451/find", inDs, outDs, "", "fn_afterQuery");
}

/********************************************************************************
* 기      능   : 접수 처리
********************************************************************************/
function fn_receipt(obj)
{
	if (length(trim(ed_zrqseq.Text)) == 0)		{
		gfn_showAlert("CW00001", "요청일련번호");
		ed_zrqseq.SetFocus();
		return;
	}
	/*  2013 12 27  디자인팀 육천익 과장 요청 
	if (length(trim(ed_zrsrlt.Text)) == 0)		{
		gfn_showAlert("CW00001", "처리결과");
		ed_zrsrlt.SetFocus();
		return;
	}*/
	//trace(ds_header.savexml());
	if ( !gfn_showConfirm("CI00024", "[접수]") ) { // ${} 작업을 진행 하시겠습니까?
		return;
	}

	ds_header.SetColumn(0, "ZRQSTAT", "A");	// 접수
	ds_header.SetColumn(0, "LIFNR",  div_lifnr.ed_lifnr.value);
	TRACE("eunha debugging : " + ds_header.GetColumn(0, "LIFNR"));

	var inDs = "ds_header=ds_header"
	;
	var outDs = "ds_header=ds_header"
			 + " ds_error=ds_error"
	;

	fn_createWait("",this);     // 처리중입니다. 메시지 처리

	nsfRequest("saveReceipt", "ses0451/saveZrqstat", inDs, outDs, "", "fn_afterSave");
}

/********************************************************************************
* 기      능   :  반려 처리
********************************************************************************/
function fn_sendback()
{
	if (length(trim(ed_zrqseq.Text)) == 0)		{
		gfn_showAlert("CW00001", "요청일련번호");
		ed_zrqseq.SetFocus();
		return;
	}

	/*if (length(trim(ed_zrsrlt.Text)) == 0)		{
		gfn_showAlert("CW00001", "처리결과");
		ed_zrsrlt.SetFocus();
		return;
	}*/

	if ( !gfn_showConfirm("CI00024", "[반려]") ) { // ${} 작업을 진행 하시겠습니까?
		return;
	}

	ds_header.SetColumn(0, "ZRQSTAT", "D");	// 반려

	var inDs = "ds_header=ds_header"
	;
	var outDs = "ds_header=ds_header"
			 + " ds_error=ds_error"
	;

	fn_createWait("",this);     // 처리중입니다. 메시지 처리

	nsfRequest("saveSendback", "ses0451/saveZrqstat", inDs, outDs, "", "fn_afterSave");
}

/********************************************************************************
* 기      능   :  완료 처리
********************************************************************************/
function fn_finish()
{
	if (length(trim(ed_zrqseq.Text)) == 0)		{
		gfn_showAlert("CW00001", "요청일련번호");
		ed_zrqseq.SetFocus();
		return;
	}

	/*if (length(trim(ed_zrsrlt.Text)) == 0)		{
		gfn_showAlert("CW00001", "처리결과");
		ed_zrsrlt.SetFocus();
		return;
	}*/

	// 첨부파일 정리
	ds_del_file.ClearData();
	for ( var i = 0 ; i < ds_file.GetRowCount() ; i++ ) {
		// 일련번호를 재부여한다.
		ds_file.SetColumn(i, "ZRQSEQ", ds_header.GetColumn(0, "ZRQSEQ"));

		// DB처리 완료후, 실제 삭제할 파일을 별도 데이터셋에 저장해둔다.
		if ( ds_file.GetColumn(i, "FLAG") == "D" ) {
			ds_del_file.AddRow();
			ds_del_file.CopyRow(ds_del_file.rowcount-1, ds_file, i);
		}

		// 추가된 첨부 중 승인 파일 존재여부 확인
		if (ds_file.GetColumn(i, "FLAG") == "I")	{
			if (ds_file.GetColumn(i, "ZATGBN") == "F")	{	// 전송 파일 확인
				nCnt++;
			}
		}
	}

	//if (nCnt == 0)	{
	//	gfn_showAlert("CW10216", "완료");
	//	return;
	//}


	if ( !gfn_showConfirm("CI00024", "[완료]") ) { // ${} 작업을 진행 하시겠습니까?
		return;
	}

	ds_header.SetColumn(0, "ZRQSTAT", "S");	// 완료

	var inDs = "ds_header=ds_header"
			 + " ds_file=ds_file";

	var outDs = "ds_header=ds_header"
			 + " ds_file=ds_file"	
			 + " ds_error=ds_error"
	;

	fn_createWait("",this);     // 처리중입니다. 메시지 처리

	nsfRequest("saveFinish", "ses0451/saveApproval", inDs, outDs, "", "fn_afterSave");
}

/********************************************************************************
* 기      능   : 닫기  
********************************************************************************/
function fn_close()
{
	this.close();
}

