/********************************************************************************
* 기      능   : 저장 Callback
********************************************************************************/
function fn_afterQuery(svcId, errCode, errMsg)
{
	fn_destroyWait();

	if ( ds_error.GetRowCount() > 0 ) {
		gfn_showAlert("CE00001");
	}

	if ( svcId == "query" ) {

		if (length(trim(ds_header.GetColumn(0,"HOGI"))) == 0) { // 견적의 경우
			st_qtso_no.Text = "견적번호";
			st_qtser.Text   = "차수";
			ed_qtser.Column = "QTSER";
		} else {
			st_qtso_no.Text = "프로젝트";
			st_qtser.Text   = "호기";
			ed_qtser.Column = "HOGI";
		}

		var flag = "";
		if ( IsExistVar("fa_flag") )
			flag = trim(fa_flag);

		if (flag == "Q")	{	// 조회
			grd_file.SetCellProp("body", 5, "Expandshow", "false");
		}		

		div_zrqid.ed_sman_cd.Text = ds_header.GetColumn(0, "ZRQID"); // 요청자ID
		div_zrqid.ed_sman_nm.Text = ds_header.GetColumn(0, "ZRQNM"); // 요청자명

		div_ex_kunnr.ed_sman_cd.Text = ds_header.GetColumn(0, "EX_KUNNR"); 	 // 기디자인담당ID
		div_ex_kunnr.ed_sman_nm.Text = ds_header.GetColumn(0, "EX_KUNNRNM"); // 기디자인담당명

		TRACE("eunha debugging : "+ds_header.GetColumn(0, "LIFNR"));
		if (ds_header.GetColumn(0, "LIFNR") != "" && ds_header.GetColumn(0, "LIFNR") != null) {
			div_lifnr.ed_lifnr.Text = ds_header.GetColumn(0, "LIFNR"); // 외주협력업체
			div_lifnr.ed_name1.Text = ds_header.GetColumn(0, "LIFNRNM"); // 외주협력업체
		}

		var dsTemp = gfn_drawTemplateSet(ds_template, ds_templateSd120);

		// 사양 쪼개기
		var fa  = "ATKLA==";
		var fa1 = fa+quote("EL_BASIC"  )+" || "+fa+quote("ES_BASIC" )+" || "+fa+quote("MW_BASIC" )+" || "+fa+quote("PSD_BASIC")+" || "+fa+quote("AP_BASIC")+" || Substr(PRH,0,3)=="+quote("SV_")+" || Substr(PRH,0,3)=="+quote("SP_");
		var fa2 = fa+quote("EL_INSIDE" )+" || "+fa+quote("ES_OPTION")+" || "+fa+quote("MW_OPTION")+" || "+fa+quote("PSD_SASH" );
		var fa3 = fa+quote("EL_OUTSIDE")+" || "+fa+quote("ES_SAFETY")+" || "+fa+quote("MW_SAFETY")+" || "+fa+quote("PSD_STEEL");
		var fa4 = fa+quote("EL_OPTION" )+" || "+fa+quote("ES_EXTER" )+" || "+fa+quote("MW_EXTER" )+" || "+fa+quote("PSD_ELEC" );

		dsTemp.Filter(fa1);
		ds_template1.CopyF(dsTemp);

		dsTemp.Filter(fa2);
		ds_template2.CopyF(dsTemp);

		dsTemp.Filter(fa3);
		ds_template3.CopyF(dsTemp);

		dsTemp.Filter(fa4);
		ds_template4.CopyF(dsTemp);
	}
}

/********************************************************************************
* 기      능   : 저장 Callback
********************************************************************************/
function fn_afterSave(svcId, errCode, errMsg)
{
	fn_destroyWait();

	if ( ds_error.GetRowCount() > 0 ) {
		gfn_showAlert("CE00001");
		return;
	}

	parent.fn_query();

	ds_sms.ClearData();
	var addRow = ds_sms.AddRow();

	var vMsg = "요청일련번호 : " + ed_zrqseq.Text;
	vMsg += "의 디자인의뢰요청이 ";

	var vSendTel = ds_header.GetColumn(0, "ZRQCEL");		// 영업담당자
	var vBackTel = "";	// 처리자 전화번호

	if ( svcId == "saveReceipt" ) {
		//vBackTel = ds_header.GetColumn(0, "ZRQCEL");
		vMsg += "접수 ";
	}else if ( svcId == "saveSendback" ) {
		//vBackTel = ds_header.GetColumn(0, "ZRQCEL");
		vMsg += "반려 ";
	}else if ( svcId == "saveFinish" ) {
		//vBackTel = parent.ds_list.GetColumn(parent.ds_list.currow, "KUNNR_DS_TEL");		// 디자인담당
		vMsg += "완료 ";
	}
	vMsg += "되었습니다." ;
	vMsg += "디자인담당자(" + ds_header.GetColumn(0, "KUNNR_DS") +")"	;

	//vSendTel = "010-9188-7098";	// Test완료 후 제거

	// 전화번호가 부 정확한 경우 SMS 처리 불가
	var telTemp = vSendTel;
	trace(vMsg + "!!"+telTemp);
	if (length(trim(replace(telTemp,"-",""))) < 11)	{
		gfn_showAlert("CW10025", "SMS 발송 실패. 영업담당자 전화번호 확인 바랍니다.");
		fn_afterSMS(svcId, errCode, errMsg);
		return;
	}

	if (length(trim(vBackTel)) < 12)	{
	//	vBackTel = "02-3670-0960";
	}

	// 전송 정보 Setting
	ds_sms.SetColumn(addRow, "TITLE",   F_SMS_TITLE);	// Title
	ds_sms.SetColumn(addRow, "TEL",     vSendTel);		// 영업사업 전화번호
	ds_sms.SetColumn(addRow, "BACKTEL", vBackTel);		// 회송전화번호
	ds_sms.SetColumn(addRow, "MSG",     vMsg);

	fn_createWait("",this);     // 처리중입니다. 메시지 처리

	tit_clearActionInfo();
	tit_addSingleActionInfo("cs12:CS1221001A_I2");	//CS1221001A_I1
	tit_callService(
        "PDA_ACT"
        , ""
        , "ds_sms=ds_sms"
        , ""
        , ""
        , "fn_afterSMS");
}

function fn_afterSMS(errCode, errMsg)
{
	fn_destroyWait();

	var zrqstat = ds_header.getColumn(0, "ZRQSTAT"); // A-접수,D-반려,S-완료

	if ( zrqstat == "A" ) {
		gfn_showAlert("CI00006", "접수"); // ${} 처리를 완료했습니다.
		div_btn.btn_receipt.Visible  = false;
		div_btn.btn_sendback.Visible = false;
	} else if ( zrqstat == "D" ) {
		gfn_showAlert("CI00006", "반려"); // ${} 처리를 완료했습니다.
		div_btn.btn_receipt.Visible  = false;
		div_btn.btn_sendback.Visible = false;
	} else if ( zrqstat == "S" ) {
		// 실제 파일 삭제
		for ( var i = 0 ; i < ds_del_file.GetRowCount() ; i++ ) {
			tit_fileDelete(F_SERVER_DIR, ds_del_file.getColumn(i, "ZATTFN"), "");
		}
		ds_del_file.ClearData();

		gfn_showAlert("CI00006", "완료"); // ${} 처리를 완료했습니다.
		div_btn.btn_approval.Visible  = false;
	}

	this.close();
}


