function fn_OnLoadCompleted(obj) {
	// Form 로드 이벤트 맨 첫줄에 기술 
	gfn_initForm(true);

	fn_init_form(); 	// 폼 초기화
}

/********************************************************************************
* 기      능   : form UnloadCompleted 
********************************************************************************/
function fn_OnUnloadCompleted(obj) {
	return gfn_isFormUnload(obj);
}

/********************************************************************************
* 기      능   : 폼 초기화
********************************************************************************/
function fn_init_form() {

	cal_zrqdat_fm.Text = addDate(gfn_getCurrDate(), -365);	// 현재일자 10일전의 일자부터

	cal_zrqdat_to.Text = gfn_getCurrDate();

	div_lifnr.ed_name1.Visible = true;
	div_lifnr.ed_name1.Enable  = false;

	// 접수/반려 & 전송 버튼 disable
	btn_accept.Enable   = false;
	btn_approval.Enable = false;
	btn_detail.Enable   = false;

	// 부서코드 정보에 따라 업무구분 결정
	if (G_SAP_USER_VGCD == "B81")	{
		cb_zrqflg.Value = "D";		// 도면 (설계부팀)
	}else if (G_SAP_USER_VGCD == "B82")	{
		cb_zrqflg.Value = "R";		// 디자인(디자인팀)
	}else	{
		cb_zrqflg.Value = "D";
		//gfn_showAlert("CW10025", "권한이 존재하지 않습니다. 정보기술팀에 문의 하십시요.");
		//this.Close();
	}

	cb_zrqflg.Enable = false;

	//코드콤보 설정 
	var codeArr    = ["VKBUR",   "VKGRP"]; 	//코드콤보 설정
	var dsObjArr   = [ds_vkbur, ds_vkgrp];
	var addTypeArr = [G_CODE_ALL];
	gfn_searchCommCode(codeArr, dsObjArr, addTypeArr);

	// 공통코드 콤보목록 조회
 	fn_query_DD07T("ZRQSTAT", ds_zrqstat_cd);		// 진행상태
}

// 공통코드 조회
function fn_query_DD07T(pDomainId, pDs)
{  
	// 데이타셋 초기화
	ds_cond_dd07t.ClearData();	
	pDs.ClearData();

	// 데이타셋 설정
	ds_cond_dd07t.AddRow();
	ds_cond_dd07t.SetColumn(ds_cond_dd07t.row, "DOMNAME"	, pDomainId);  
	ds_cond_dd07t.SetColumn(ds_cond_dd07t.row, "CONCAT_YN"	, "N");  

	// 서비스 호출
	nsfRequest(  "query"
			   , "combo/comboDD07T"
			   , "ds_cond_dd07t=ds_cond_dd07t " + pDs.ID+"="+pDs.ID
			   , pDs.ID+"="+pDs.ID +" ds_error=ds_error"
			   , ""
			   , "fn_afterQuery_DD07T_"+pDomainId); 
}

// 기종 조회 CALL BACK
function fn_afterQuery_DD07T_ZRQSTAT(errCode, errMsg) 
{  
	gfn_InsertRowCombo(ds_zrqstat_cd, 0, "Y", "Y"); // 선택 행 추가
	cb_zrqstat.index = 0;
}

//=========================================================================================
// [ PART 3 ]
// Main Process Event 정의(조회,입력,삭제,저장,인쇄,form닫기)
//=========================================================================================
/********************************************************************************
* 기      능   : 조회 1
********************************************************************************/
function fn_query() {

	if (!fn_query_validation())	{
		return;
	}

	fn_createWait("",this);     // 처리중입니다. 메시지 처리

	ds_error.ClearData();
	ds_cond.DeleteAll();
	ds_cond.AddRow();

	ds_cond.SetColumn(0, "FR_ZRQDAT", cal_zrqdat_fm.Value);
	ds_cond.SetColumn(0, "TO_ZRQDAT", cal_zrqdat_to.Value);
	ds_cond.SetColumn(0, "FR_VKBUR",  div_vbvg.cb_frvb.Value);	// 시작부서
	ds_cond.SetColumn(0, "TO_VKBUR",  div_vbvg.cb_tovb.Value);	// 종료부서
	ds_cond.SetColumn(0, "FR_VKGRP",  div_vbvg.cb_frvg.Value);	// 시작팀
	ds_cond.SetColumn(0, "TO_VKGRP",  div_vbvg.cb_tovg.Value);	// 종료팀
	ds_cond.SetColumn(0, "ZAGNT",     "");	// 대리점
	ds_cond.SetColumn(0, "ZKUNNR",    ed_sman_cd.Text);			// 영업담당자
	ds_cond.SetColumn(0, "ZKUNNR_Z3", div_sman.ed_sman_cd.Text);	// 기술영업담당자
	ds_cond.SetColumn(0, "ZRQSTAT",   cb_zrqstat.Value);		// 진행상태
	ds_cond.SetColumn(0, "ZRQFLG",    cb_zrqflg.Value);			// 구분(디자인, 도면)
	ds_cond.SetColumn(0, "QTSO_NO",   ed_qtso_no.Text);			// 견적
	ds_cond.SetColumn(0, "PJT",       ed_pjt.Text);			    // 프로젝트
	ds_cond.SetColumn(0, "LOPL",       ed_lopl.Text);			  //도면제출처

	trace("ds_cond SAVE XML ---------> "+ds_cond.SaveXML());

	nsfRequest(  "query"
			   , "ses0470/findRequestList"
			   , "ds_cond=ds_cond ds_list=ds_list"
			   , "ds_list=ds_list ds_error=ds_error"
			   , ""
			   , "fn_afterQuery"
			  );
}

/********************************************************************************
* 기      능   : 조회조건 필수 체크
********************************************************************************/
function fn_query_validation()
{
	var vMSG = "요청기간";

	// 필수조회조건 체크
	if(length(trim(cal_zrqdat_fm.Value)) != 8)	{
		gfn_showAlert("CW00001", vMSG);
		cal_zrqdat_fm.SetFocus();
		return false;
	}

	if(length(trim(cal_zrqdat_to.Value)) != 8)	{
		gfn_showAlert("CW00001", vMSG);
		cal_zrqdat_to.SetFocus();
		return false;
	}

	return true;
}

/********************************************************************************
* 기      능   : 닫기  
********************************************************************************/
function fn_close() {
	close();
}

