/******************************************************
// 그리드 클릭 시 접수/반납 & 전송 버튼 상태 처리
******************************************************/
function grd_list_OnCellClick(obj,nRow,nCell,nX,nY,nPivotIndex)
{
	var varUserGbn = right(G_USER_GROUP_B, 2 );		// 30 : 담당, 40 : 팀장, 50 : 관리자
	var varStat = ds_list.GetColumn(nRow, "ZRQSTAT");

	btn_detail.Enable   = true;

	if (varStat == "R")		{	// 요청이 경우 팀장, 관리자만 접수/반려 가능
		if (varUserGbn == "30" || varUserGbn == "40" || varUserGbn == "50")	{
			btn_accept.Enable = true;
		}else	{
			btn_accept.Enable = false;
		}
		btn_approval.Enable  = false;
	}else if (varStat == "A")	{	// 접수상태에서만 전송 가능
		btn_approval.Enable  = true;
		btn_accept.Enable = false;
	}else	{		// 상세 조회만 가능
		btn_accept.Enable   = false;
		btn_approval.Enable = false;
	}	
}

/******************************************************
// 그리드 row 변경 시
******************************************************/
function grd_list_OnCellPosChanged(obj,nRow,nCell,nOldRow,nOldCell,nPivotIndex,nOldPivotIndex)
{
	if (nOldRow != nRow)	{
		grd_list_OnCellClick(obj, nRow, nCell, "", "", nPivotIndex);
	}
}

