//=========================================================================================
// [ PART 2 ]
// Form Loading / Unloading 시 작업 정의
//=========================================================================================
function fn_OnLoadCompleted(obj) {

	gfn_initForm(true);	// Form 로드 이벤트 맨 첫줄에 기술 

	this.onsize = "fn_resize";
	parent.onkeydown = "";

	fn_init_form(); 	// 폼 초기화
}

/********************************************************************************
* 기      능   : form UnloadCompleted 
********************************************************************************/
function fn_OnUnloadCompleted(obj) {
	parent.onkeydown = "mainForm_OnKeyDown";
	return gfn_isFormUnload(obj);
}

//=========================================================================================
// [ PART 3 ]
// Main Process Event 정의(조회,입력,삭제,저장,인쇄,form닫기)
//=========================================================================================
/********************************************************************************
* 기      능   : 조회 
********************************************************************************/
var fn_query_chk = false; //중복조회 방지용 flag

function fn_query() {

	if(!fn_query_chk) {
		fn_query_chk = true;
	} else {
		gfn_showAlert("CW10025", "조회중입니다..");
		return;
	}
	fn_createWait("",this);     // 처리중입니다. 메시지 처리
	nsfRequest("searchTemplate", "ses0490/searchTemplate", "ds_list=ds_list", "ds_list=ds_list ds_error=ds_error", "", "fn_afterQuery");
}

/********************************************************************************
* 기      능   : 저장 
********************************************************************************/
function fn_save()
{
    gfn_clearMsgBar();   

    if(ds_list.rowcount < 1) {
		gfn_showAlert("CW00010");	// 데이터가 없습니다.
		return false;
    }

    if(ds_list.CaseCount("FLAG=='U'") < 1) {
		gfn_showAlert("CW00005");	// 변경된 정보가 없습니다.
		return false;
    }

	if (gfn_showConfirm("CI10003") == true) { // CI10003 저장하시겠습니까?
		fn_createWait("",this);     	// 처리중입니다. 메시지 처리 

		ds_error.ClearData();   		// 결과값 

		nsfRequest("save"				// srvId
					, "ses0490/save"	// url
					, "ds_list=ds_list"	// inDsList
					, "ds_list=ds_list"	// outDsList
					, ""				// strParam
					, "fn_afterQuery"	// CallBackFunc
					);	
	}
}
/********************************************************************************
* 기      능   : 폼 초기화
********************************************************************************/
function fn_init_form() {
	fn_resize();
	fn_query();
}

/********************************************************************************
* 기      능   : 닫기  
********************************************************************************/
function fn_close() {
	close();
}

//=========================================================================================
// [ PART 4]
// Callback 함수 정의 : callback 함수명은 메인 함수의 fn_xxxx() 에서 xxxx 부분은 fn_afterXXXX()
// 형태가 되도록 작성 
//=========================================================================================
/********************************************************************************
* 기      능   : Callback  
********************************************************************************/
function fn_afterQuery(svcId, errCode, errMsg) {

	fn_query_chk = false; //중복조회 방지용 flag
	fn_destroyWait();

	if(ds_error.rowcount() > 0) {
		gfn_showAlert(ds_error.GetColumn(0,"ERRMSG"));
		return false;
	}

	if(svcId == "searchTemplate") {
		tab.TabIndex = 0;

		var chkCnt;
		for(var i = 0; i < ds_cond.rowcount; i++) {
			chkCnt = ds_list.CaseCount("MATNR=='" + ds_cond.GetColumn(i, "MATNR") + "'&&AGENT=='1'");
			ds_cond.SetColumn(i, "CNT", chkCnt);
		}	

		fn_template(ds_cond.currow);
	} else if(svcId == "save") {
		gfn_showAlert("CI00003");
		fn_query();
	}
} 

