/********************************************************************************
* 기      능   : 그리드 클릭 이벤트
********************************************************************************/
function grd_header_OnCellClick(obj,nRow,nCell,nX,nY,nPivotIndex)
{
	fn_template(nRow);
}

/********************************************************************************
* 기      능   : 사양 옵션별 정리
********************************************************************************/
function fn_template(nRow) {
	fn_createWait("",this); // 처리중입니다. 메시지 처리

	edt_matNo.Text = ds_cond.GetColumn(nRow, "MATNR");

	// 그리드 변경사항 반영 여부
	fn_grdRedraw(false);

	ds_temp1.OnColumnChanged = "";
	ds_temp2.OnColumnChanged = "";
	ds_temp3.OnColumnChanged = "";
	ds_temp4.OnColumnChanged = "";
	ds_temp5.OnColumnChanged = "";
	ds_temp6.OnColumnChanged = "";

	ds_list.UnFilter();
	ds_list.Filter("MATNR==" + quote(ds_cond.GetColumn(nRow, "MATNR")));

	var fa  = "ATKLA==";
	var fa1 = fa+quote("EL_BASIC"  )+" || "+fa+quote("ES_BASIC" )+" || "+fa+quote("MW_BASIC" )+" || "+fa+quote("PSD_BASIC")+" || "+
	          fa+quote("AP_BASIC")+" || Substr(PRH,0,3)=="+quote("WCL")+" || Substr(PRH,0,3)=="+quote("DW_")+" || Substr(PRH,0,3)=="+
	          quote("SV_")+" || ( Substr(PRH,0,3)=="+quote("SP_")+" && Substr(PRHNAME, 0, 1) !=" + quote("Z")+")"  ;
	var fa2 = fa+quote("EL_INSIDE" )+" || "+fa+quote("ES_OPTION")+" || "+fa+quote("MW_OPTION")+" || "+fa+quote("PSD_SASH" );
	var fa3 = fa+quote("EL_OUTSIDE")+" || "+fa+quote("ES_SAFETY")+" || "+fa+quote("MW_SAFETY")+" || "+fa+quote("PSD_STEEL");
	var fa4 = fa+quote("EL_OPTION" )+" || "+fa+quote("ES_EXTER" )+" || "+fa+quote("MW_EXTER" )+" || "+fa+quote("PSD_ELEC" );
	var fa5 = fa+quote("EL_LAYOUT" );
	var fa6 = fa+quote(" ") + " && Substr(PRHNAME, 0, 6) !=" + quote("REMARK") + " && Substr(PRH, 0, 3) !=" + quote("WCL") + 
	          " && Substr(PRH, 0, 3) !=" + quote("DW_") + " || Substr(PRHNAME, 0, 1) ==" + quote("Z");

	ds_temp1.copyF(ds_list);
	ds_temp2.copyF(ds_list);
	ds_temp3.copyF(ds_list);
	ds_temp4.copyF(ds_list);
	ds_temp5.copyF(ds_list);
	ds_temp6.copyF(ds_list);

	ds_temp1.Filter(fa1);
	ds_temp2.Filter(fa2);
	ds_temp3.Filter(fa3);
	ds_temp4.Filter(fa4);
	ds_temp5.Filter(fa5);
	ds_temp6.Filter(fa6);

	ds_temp1.OnColumnChanged = "ds_tempOnColumnChanged";
	ds_temp2.OnColumnChanged = "ds_tempOnColumnChanged";
	ds_temp3.OnColumnChanged = "ds_tempOnColumnChanged";
	ds_temp4.OnColumnChanged = "ds_tempOnColumnChanged";
	ds_temp5.OnColumnChanged = "ds_tempOnColumnChanged";
	ds_temp6.OnColumnChanged = "ds_tempOnColumnChanged";

	ds_temp1.Sort("PRHNAME",true); 
	ds_temp2.Sort("PRHNAME",true); 
	ds_temp3.Sort("PRHNAME",true); 
	ds_temp4.Sort("PRHNAME",true);
	ds_temp5.Sort("PRHNAME",true);
	ds_temp6.Sort("PRHNAME",true);

	fn_grdRedraw(true);
	fn_destroyWait();
}

//=========================================================================================
//  함수기능 : 그리드 변경 반영여부
//  파라미터 : 
//  리턴값   : 
//  기능ID   : 
//  개선내역 : 
//  HISTORY  : 2016.02.15 최초 코딩 hsi
//=========================================================================================
function fn_grdRedraw(val) {
	var grdArry = ["tab.basic.grd_list", "tab.inside.grd_list","tab.outside.grd_list", "tab.option.grd_list","tab.layout.grd_list", "tab.common.grd_list"];

	for(var i = 0; i < grdArry.length; i++) {
		eval(grdArry[i]).Redraw = val;
	}
}

/********************************************************************************
* 기      능   : 사양찾기
********************************************************************************/
function edt_keyDown(obj,nChar,bShift,bCtrl, bAlt,LLParam,HLParam)
{
	if(nChar == "13") {


		var grdArry = ["tab.basic.grd_list", "tab.inside.grd_list","tab.outside.grd_list", "tab.option.grd_list","tab.layout.grd_list", "tab.common.grd_list"];

		if(length(obj.text) < 1) {
			for(var i = 0; i < grdArry.length; i++) {
				eval(grdArry[i]).Redraw = false;
				eval(grdArry[i]).SetCellProp("body", 1, "bkColor", "");
				eval(grdArry[i]).SetCellProp("body", 1, "bkColor2", "User Color 22");
				eval(grdArry[i]).SetCellProp("body", 2, "Color", "");
				eval(grdArry[i]).SetCellProp("body", 3, "Color", "");
				eval(grdArry[i]).Redraw = true;
			}
		} else {
			for(var i = 0; i < grdArry.length; i++) {
				eval(grdArry[i]).Redraw = false;
				eval(grdArry[i]).SetCellProp("body", 1, "bkColor", "expr:iif(IndexOf(PRHNAME, edt_temp.Text) < 0, '', 'red')");
				eval(grdArry[i]).SetCellProp("body", 1, "bkColor2", "expr:iif(IndexOf(PRHNAME, edt_temp.Text) < 0, 'User Color 22', 'red')");
				eval(grdArry[i]).SetCellProp("body", 2, "color", "expr:iif(IndexOf(PRHNAME, edt_temp.Text) < 0, '', 'red')");
				eval(grdArry[i]).SetCellProp("body", 3, "color", "expr:iif(IndexOf(PRHNAME, edt_temp.Text) < 0, '', 'red')");
				eval(grdArry[i]).Redraw = true;
			}
		}
	}
	//
}

/********************************************************************************
* 기      능   : 그리드값 변경시 원본 ds에 값 세팅
********************************************************************************/
function ds_tempOnColumnChanged(obj,nRow,strColumnID,varOldValue,varNewValue,nPivotIndex) {
	if(strColumnID == "AGENT") {
		var chkSt = "MATNR ==" + quote(obj.getColumn(nRow, "MATNR")) + 
		            " && ATKLA ==" + quote(obj.getColumn(nRow, "ATKLA")) + 
		            " && PRH ==" + quote(obj.getColumn(nRow, "PRH"));

		var chkRow = ds_list.SearchRow(chkSt);

		ds_list.SetColumn(chkRow, "AGENT", varNewValue);
		ds_list.SetColumn(chkRow, "FLAG", "U");
		obj.setColumn(nRow, "FLAG", "U");

		ds_cond.SetColumn(ds_cond.currow, "CNT", ds_list.CaseCount("MATNR=='" + ds_cond.getColumn(ds_cond.currow, "MATNR") + "'&&AGENT=='1'"));
	}
}

/********************************************************************************
* 기      능   :  ui resize
********************************************************************************/
function fn_resize() {
	Shape1.Width = 333;
	grd_header.Width = 333;

	tab.width  = window.width-385;
	tab.height = window.height-130;
	Shape0.Width = window.width-385;

	tab.basic.Width   = tab.width-4;
	tab.inside.Width  = tab.width-4;
	tab.outside.Width = tab.width-4;
	tab.option.Width  = tab.width-4;
	tab.layout.Width  = tab.width-4;
	tab.common.Width  = tab.width-4;

	tab.basic.height = tab.height-25;
	tab.inside.height = tab.height-25;
	tab.outside.height = tab.height-25;
	tab.option.height = tab.height-25;
	tab.layout.height = tab.height-25;
	tab.common.height = tab.height-25;


	grd_header.height = window.height-125;
	tab.basic.grd_list.height = tab.height-30;
	tab.inside.grd_list.height = tab.height-30;
	tab.outside.grd_list.height = tab.height-30;
	tab.option.grd_list.height = tab.height-30;
	tab.layout.grd_list.height = tab.height-30;
	tab.common.grd_list.height = tab.height-30;

	tab.basic.grd_list.width = tab.width-10;
	tab.inside.grd_list.width = tab.width-10;
	tab.outside.grd_list.width = tab.width-10;
	tab.option.grd_list.width = tab.width-10;
	tab.layout.grd_list.width = tab.width-10;
	tab.common.grd_list.width = tab.width-10;
}

