/********************************************************************************
* 기      능   : 조회 1
********************************************************************************/
function fn_query() {

    fn_createWait("",this);     // 처리중입니다. 메시지 처리

    ds_cond.setColumn(0, "MANDT"  , G_MANDT  );
    if (G_VEND_WGBN = "E")	{	// 해외대리점의 경우 대리점코드로 조회
		ds_cond.setColumn(0, "ZWRITER", G_VEND_CODE);
    }else	{
		ds_cond.setColumn(0, "ZWRITER", G_SAP_USER_ID); // G_USER_ID
	}
    ds_cond.setColumn(0, "ZPRDGBN", mclass   );      // ds_cond.setColumn(0, "ZTPLGBN", mclass);
    ds_cond.setColumn(0, "BRNDCD",  cb_abrand.value); // 2020 브랜드 입력
    ds_cond.setColumn(0, "BRNDSEQ", brndseq);
    ds_cond.setColumn(0, "QTNUM", qtnum);

	nsfRequest("query", "ses0010/find", "ds_cond=ds_cond ds_list=ds_list", "ds_list=ds_list ds_error=ds_error", "", "fn_afterQuery");
}

/********************************************************************************
* 기      능   : 폼 초기화
********************************************************************************/
function fn_init_form() {

    ds_cond.SetColumn(0, "ZPRDGBN", mclass);
	nsfRequest("queryClass", "combo/comboClass", "ds_cond=ds_cond ds_zprdgbn=ds_zprdgbn", "ds_zprdgbn=ds_zprdgbn", "", "fn_afterQuery");
}

/********************************************************************************
* 기      능   : 닫기  
********************************************************************************/
function fn_close() {
	close();
}

//=========================================================================================
// [ PART 4]
// Callback 함수 정의 : callback 함수명은 메인 함수의 fn_xxxx() 에서 xxxx 부분은 fn_afterXXXX()
// 형태가 되도록 작성 
//=========================================================================================
/********************************************************************************
* 기      능   : 조회 Callback  
********************************************************************************/
function fn_afterQuery(svcId, errCode, errMsg) {
	var vDutyCall = "N";	// Duty call 여부

	fn_destroyWait();

	if(ds_error.rowcount() > 0) {
		gfn_showMsg("CE00001");
		return;
	} else {
		gfn_showMsg("CI00002", ds_list.RowCount() + "");
	}

	if (svcId == "queryClass") {
		if (mclass == null) {
		    cb_zprdgbn.Index = 0;
		    grd_list.SetColProp(4, "Width", 0);
   		    fn_query();
		} else {
		    cb_zprdgbn.Index = ds_zprdgbn.FindRow("CODE", mclass);
		    if(mclass == 'ELV_01') {
		        // 브랜드 쿼리를 수행
		        txt_abrand.Visible = true;
		        cb_abrand.Visible = true;
		        ds_cond.setColumn(0, "MANDT"  , G_MANDT);
		        ds_cond.setColumn(0, "BRNDCD",  brndcd);
				grd_list.SetColProp(1, "Width", 68);
	            nsfRequest("inintQueryEnd", "ses0010/abrand", "ds_cond=ds_cond ds_abrand=ds_abrand", "ds_abrand=ds_abrand ds_error=ds_error", "", "fn_afterQuery");
		    } else {
		        txt_abrand.Visible = false;
		        cb_abrand.Visible = false;
				grd_list.SetColProp(1, "Width", 0);
    		    fn_query();
		    }
		}
	} else if(svcId == "inintQueryEnd") {
		ds_abrand.InsertRow(0);
		ds_abrand.SetColumn(0, "CODE", "");
		ds_abrand.SetColumn(0, "CODE_NAME", "- 전체 -");
		cb_abrand.Index = ds_abrand.FindRow("CODE", brndcd);
	    fn_query();
	} else if(svcId == "queryTemp") {
	    parent.ds_template_add.copy(ds_temp);
		parent.fn_template("Y", nRow);

		// 2013.01.08 설계부의 표준템플릿의 경우 사전 정의 Duty 호출
		// 제품구분의 정보가 없는 경우 원 표준 Template만 제공
		var vZTPLGBN = "";
		vZTPLGBN = ds_list.GetColumn(ds_list.currow, "ZTPLGBN");
		if ( vZTPLGBN == "02")	{
			var vGtype = "";

			switch(ds_list.GetColumn(ds_list.currow, "ZPRDGBN"))	{
				case "ELV_01" : vGtype = "SEL1";
								break;
				case "ESC_01" : vGtype = "SES1";
								break;
				case "MW_01"  : vGtype = "SMW1";
								break;
				default       : break;
			}
			if (length(trim(vGtype)) > 0)	{
				// 종속성과 관련된 Duty call
				parent.fn_stdGenDuty(vZTPLGBN, vGtype);
				vDutyCall = "Y";
			}
		} else if ( vZTPLGBN == "03" ) {
			var vGtype = "";

			switch(ds_list.GetColumn(ds_list.currow, "ZPRDGBN"))	{
				case "ELV_01" : 
					switch(ds_list.GetColumn(ds_list.currow,"ZTPLNO")){
						case "4925" :
							vGtype = "YEL1";	break;
						case "4928" :
							vGtype = "YEL2";	break;
						case "4929" :
							vGtype = "YEL3";	break;
						case "4930" :
							vGtype = "YEL4";	break;
						case "4931" :
							vGtype = "YEL5";	break;
						case "4932" :
							vGtype = "YEL6";	break;
						case "4933" :
							vGtype = "YEL7";	break;
						default      : break;
					}					
					break;
				case "ESC_01" : break;
				case "MW_01"  : break;
				default       : break;
			}
			if (length(trim(vGtype)) > 0)	{
				// 종속성과 관련된 Duty call
				parent.fn_stdGenDuty(vZTPLGBN, vGtype);
				vDutyCall = "Y";
			}
		}

		if (vDutyCall == "N")	{
			// 2013.01.15 공통사양 반영 처리
			// Ses0260(예상원가산출(설계부)에서 호출되어 처리되는 경우 화면 script오류 발생은 무시.
			var ds_h = object(parent.ds_header.ID);

			parent.fn_aspc("", "", "", 0, 0, 0, 0, ds_h);					// 시방서 처리
			parent.fn_bcpi("", "", "", "", "", "", "", 0, 0, 0, 0, ds_h);  // LED 총 수량
			parent.fn_bclcd("", "", "", "", 0, 0, 0, 0, ds_h);
			parent.fn_bclcd("", "", "", "", 0, 0, 0, 0, ds_h); 			// LCD 총 수량
			parent.fn_bcpi("", "", "", "", "", "", "", 0, 0, 0, 0, ds_h); 	// LED 총 수량
			parent.fn_deph("", "", "", 0, 0, 0, 0, ds_h);					// 비상통화장치 DI-PBX 적용대수
			parent.fn_ddvr("", "", "", 0, 0, 0, 0, ds_h);					// DVR 적용대수
			// parent.fn_drvhq("", "", "", 0, 0, 0, 0, ds_h);					// 행선층 예약시스템 적용대수
			parent.fn_dsv1("", "", "", 0, 0, 0, 0, ds_h);					// 감시반1
			parent.fn_dsv2("", "", "", 0, 0, 0, 0, ds_h);					// 감시반2
		}

		// 2013.02.18 견적에서 템플릿불러오기 후 원가산출 버튼 비활성화
		if ( isValid(parent.btn_count) ) {
			parent.btn_count.visible = false;
		}
		parent.destroyWait();

		//항목별 복사창 닫기
		for(var i=0; i	if(AllWindows[i].id == "SES::Ses0583.xml") {  
			    AllWindows[i].close();
			} 
		}
		//fn_close();
	}
}

